// src/components/GuestRoom.js
import React, { useEffect, useRef, useState } from 'react';
import io from 'socket.io-client';
import './Room.css';

const socket = io.connect('https://localhost:5000'); // your backend address

function GuestRoom() {
  const [roomId, setRoomId] = useState('');
  const [name, setName] = useState('');
  const [joined, setJoined] = useState(false);
  const [participants, setParticipants] = useState([]);
  const [muted, setMuted] = useState(false);
  const [videoOff, setVideoOff] = useState(false);
  const [streams, setStreams] = useState({});
  const localVideoRef = useRef(null);
  const localStream = useRef(null);
  const peersRef = useRef({});

  useEffect(() => {
    socket.on('all-users', (users) => {
      users.forEach(({ socketId }) => {
        callUser(socketId);
      });
    });

    socket.on('user-joined', ({ socketId, userName }) => {
      setParticipants(prev => [...prev, { id: socketId, name: userName }]);
    });

    socket.on('user-left', (id) => {
      setParticipants(prev => prev.filter(p => p.id !== id));
      if (peersRef.current[id]) {
        peersRef.current[id].close();
        delete peersRef.current[id];
        setStreams(prev => {
          const updated = { ...prev };
          delete updated[id];
          return updated;
        });
      }
    });

    socket.on('receive-signal', async ({ from, signal, userName }) => {
      const peer = createPeer(false);
      peer.signal(signal);
      peer.on('signal', signal => {
        socket.emit('return-signal', { to: from, signal });
      });
      peer.on('stream', stream => {
        setStreams(prev => ({ ...prev, [from]: { stream, name: userName } }));
      });
      peersRef.current[from] = peer;
    });

    socket.on('returned-signal', ({ from, signal }) => {
      if (peersRef.current[from]) {
        peersRef.current[from].signal(signal);
      }
    });

    return () => {
      socket.disconnect();
    };
  }, []);

  const callUser = (socketId) => {
    const peer = createPeer(true);
    peer.on('signal', signal => {
      socket.emit('send-signal', {
        signal,
        to: socketId,
        userName: name,
      });
    });
    peer.on('stream', stream => {
      setStreams(prev => ({ ...prev, [socketId]: { stream } }));
    });
    peer.addStream(localStream.current);
    peersRef.current[socketId] = peer;
  };

  const createPeer = (initiator) => {
    const Peer = require('simple-peer');
    return new Peer({
      initiator,
      trickle: false,
      stream: localStream.current,
    });
  };

  const handleJoin = async () => {
    if (!roomId || !name) return alert("Enter room ID and your name");

    try {
      const stream = await navigator.mediaDevices.getUserMedia({ video: true, audio: true });
      localStream.current = stream;
      if (localVideoRef.current) {
        localVideoRef.current.srcObject = stream;
      }

      socket.emit('join-room', { roomId, userName: name });
      setParticipants([{ id: socket.id, name }]);
      setJoined(true);
    } catch (err) {
      console.error("Media error:", err);
      alert("Unable to access camera or microphone");
    }
  };

  const toggleMute = () => {
    const enabled = !muted;
    localStream.current.getAudioTracks()[0].enabled = !enabled;
    setMuted(enabled);
  };

  const toggleVideo = () => {
    const off = !videoOff;
    localStream.current.getVideoTracks()[0].enabled = !off;
    setVideoOff(off);
  };

  const handleLeave = () => {
    socket.disconnect();
    window.location.reload();
  };

  return (
    <div className="room-container">
      {!joined ? (
        <div className="side-panel">
          <h3 style={{ color: 'gold' }}>Join Ve Meet</h3>
          <input
            type="text"
            placeholder="Room Code"
            value={roomId}
            onChange={(e) => setRoomId(e.target.value)}
          />
          <input
            type="text"
            placeholder="Your Name"
            value={name}
            onChange={(e) => setName(e.target.value)}
          />
          <button className="gold-button" onClick={handleJoin}>Join</button>
        </div>
      ) : (
        <>
          <div className="video-grid">
            <div className="video-block">
              <video className="video" ref={localVideoRef} autoPlay playsInline muted />
              <div className="video-name">{name} (You)</div>
            </div>
            {Object.keys(streams).map((id) => (
              <div className="video-block" key={id}>
                <video
                  className="video"
                  autoPlay
                  playsInline
                  ref={(video) => {
                    if (video) video.srcObject = streams[id].stream;
                  }}
                />
                <div className="video-name">{streams[id].name || 'Guest'}</div>
              </div>
            ))}
          </div>
          <div className="side-panel">
            <h3>Participants</h3>
            <ul>
              {participants.map(p => (
                <li key={p.id}>{p.name}</li>
              ))}
            </ul>
            <div className="controls">
              <button onClick={toggleMute}>{muted ? 'Unmute' : 'Mute'}</button>
              <button onClick={toggleVideo}>{videoOff ? 'Video On' : 'Video Off'}</button>
              <button className="end" onClick={handleLeave}>Leave Meeting</button>
            </div>
          </div>
        </>
      )}
    </div>
  );
}

export default GuestRoom;
