import React, { useEffect, useRef, useState } from "react";
import io from "socket.io-client";
import Peer from "simple-peer";
import "./Room.css";

const socket = io("https://192.168.29.23:5000", {
  transports: ["websocket"],
});

const GuestRoom = () => {
  const [peers, setPeers] = useState([]);
  const [stream, setStream] = useState(null);
  const [name, setName] = useState("");
  const [roomId, setRoomId] = useState("");
  const [joined, setJoined] = useState(false);
  const [peerConnections, setPeerConnections] = useState({});
  const [participants, setParticipants] = useState([]);

  const userVideo = useRef();
  const peersRef = useRef([]);

  useEffect(() => {
    socket.on("user-joined", handleUserJoined);
    socket.on("offer", handleReceiveOffer);
    socket.on("answer", handleAnswer);
    socket.on("ice-candidate", handleNewICECandidateMsg);
    socket.on("user-left", handleUserLeft);
    socket.on("user-list", (list) => setParticipants(list));

    return () => {
      socket.disconnect();
    };
  }, []);

  const handleJoin = async () => {
    try {
      const mediaStream = await navigator.mediaDevices.getUserMedia({
        video: true,
        audio: true,
      });
      setStream(mediaStream);
      if (userVideo.current) {
        userVideo.current.srcObject = mediaStream;
      }

      socket.emit("join-room", { roomId, userName: name });
      setJoined(true);
    } catch (err) {
      console.error("Error accessing media devices.", err);
    }
  };

  const handleUserJoined = ({ id, name: userName }) => {
    const peer = createPeer(id, socket.id, stream);
    peersRef.current.push({ peerID: id, peer });
    setPeers((prev) => [...prev, { peerID: id, peer, name: userName }]);
  };

  const createPeer = (userToSignal, callerID, stream) => {
    const peer = new Peer({
      initiator: true,
      trickle: false,
      stream,
    });

    peer.on("signal", (signal) => {
      socket.emit("offer", {
        to: userToSignal,
        offer: signal,
      });
    });

    return peer;
  };

  const handleReceiveOffer = async ({ from, offer, name: userName }) => {
    const peer = new Peer({
      initiator: false,
      trickle: false,
      stream,
    });

    peer.on("signal", (signal) => {
      socket.emit("answer", {
        to: from,
        answer: signal,
      });
    });

    peer.signal(offer);
    peersRef.current.push({ peerID: from, peer });
    setPeers((prev) => [...prev, { peerID: from, peer, name: userName }]);
  };

  const handleAnswer = ({ from, answer }) => {
    const item = peersRef.current.find((p) => p.peerID === from);
    item?.peer.signal(answer);
  };

  const handleNewICECandidateMsg = ({ from, candidate }) => {
    const item = peersRef.current.find((p) => p.peerID === from);
    if (item) {
      item.peer.signal(candidate);
    }
  };

  const handleUserLeft = (id) => {
    const peerObj = peersRef.current.find((p) => p.peerID === id);
    if (peerObj) {
      peerObj.peer.destroy();
    }

    peersRef.current = peersRef.current.filter((p) => p.peerID !== id);
    setPeers((prev) => prev.filter((p) => p.peerID !== id));
  };

  const renderVideos = () => {
    return (
      <div className="video-grid">
        {stream && (
          <div className="video-container">
            <video
              playsInline
              muted
              ref={userVideo}
              autoPlay
              className="video"
            />
            <p className="name-tag">{name} (You)</p>
          </div>
        )}
        {peers.map((peerObj) => (
          <Video key={peerObj.peerID} peer={peerObj.peer} name={peerObj.name} />
        ))}
      </div>
    );
  };

  return (
    <div className="room-container">
      <h1 className="logo">Ve Meet</h1>
      {!joined ? (
        <div className="join-container">
          <input
            type="text"
            placeholder="Enter your name"
            value={name}
            onChange={(e) => setName(e.target.value)}
          />
          <input
            type="text"
            placeholder="Enter Room Code"
            value={roomId}
            onChange={(e) => setRoomId(e.target.value)}
          />
          <button onClick={handleJoin}>Join</button>
        </div>
      ) : (
        <>
          {renderVideos()}
          <div className="controls">
            <button onClick={() => window.location.reload()}>Leave</button>
          </div>
        </>
      )}
    </div>
  );
};

const Video = ({ peer, name }) => {
  const ref = useRef();

  useEffect(() => {
    peer.on("stream", (stream) => {
      if (ref.current) {
        ref.current.srcObject = stream;
      }
    });
  }, [peer]);

  return (
    <div className="video-container">
      <video playsInline autoPlay ref={ref} className="video" />
      <p className="name-tag">{name}</p>
    </div>
  );
};

export default GuestRoom;
