import React, { useEffect, useRef } from 'react';
import { useParams } from 'react-router-dom';
import io from 'socket.io-client';

// Connect to signaling server
const socket = io('http://localhost:5000');

export default function Room() {
  const { roomId } = useParams();
  const localVideoRef = useRef();

  // Safe getUserMedia wrapper
  const getUserMedia = (constraints) => {
    if (navigator.mediaDevices && navigator.mediaDevices.getUserMedia) {
      return navigator.mediaDevices.getUserMedia(constraints);
    } else {
      return Promise.reject(new Error('getUserMedia not supported'));
    }
  };

  useEffect(() => {
    // Step 1: Get user's video/audio
    getUserMedia({ video: true, audio: true })
      .then(stream => {
        if (localVideoRef.current) {
          localVideoRef.current.srcObject = stream;
        }

        // Step 2: Join the room using socket.io
        socket.emit('join-room', roomId);
        console.log('Joined room:', roomId);
      })
      .catch(error => {
        console.error('getUserMedia error:', error);
        alert('Camera or microphone not supported or permission denied.');
      });

    // Cleanup: Disconnect on unmount
    return () => {
      socket.disconnect();
    };
  }, [roomId]);

  return (
    <div style={{ background: '#000', height: '100vh' }}>
      <h2 style={{ color: 'white' }}>Room ID: {roomId}</h2>
      <video
        ref={localVideoRef}
        autoPlay
        playsInline
        muted
        style={{ width: 300, borderRadius: 12 }}
      />
    </div>
  );
}
