import React, { useEffect, useRef, useState } from 'react';
import io from 'socket.io-client';
import './Room.css'; // We'll apply black-gold theme via this

const socket = io('http://localhost:5000'); // Replace with your deployed server if needed

const Room = () => {
  const [roomId, setRoomId] = useState('');
  const [name, setName] = useState('');
  const [joined, setJoined] = useState(false);
  const [users, setUsers] = useState([]);
  const [localStream, setLocalStream] = useState(null);
  const [remoteStreams, setRemoteStreams] = useState([]);
  const [selectedCam, setSelectedCam] = useState('');
  const [cameras, setCameras] = useState([]);
  const localVideoRef = useRef();
  const peersRef = useRef({});
  const screenSharing = useRef(false);

  useEffect(() => {
    if (!joined) return;

    navigator.mediaDevices.enumerateDevices().then(devices => {
      const cams = devices.filter(device => device.kind === 'videoinput');
      setCameras(cams);
      if (!selectedCam && cams.length) setSelectedCam(cams[0].deviceId);
    });

    const constraints = {
      audio: true,
      video: { deviceId: selectedCam ? { exact: selectedCam } : undefined },
    };

    navigator.mediaDevices.getUserMedia(constraints).then(stream => {
      setLocalStream(stream);
      localVideoRef.current.srcObject = stream;

      socket.emit('join-room', { roomId, name });

      socket.on('user-list', setUsers);

      socket.on('user-joined', ({ id }) => {
        const peer = new RTCPeerConnection();
        peersRef.current[id] = peer;

        stream.getTracks().forEach(track => peer.addTrack(track, stream));

        peer.onicecandidate = e => {
          if (e.candidate) {
            socket.emit('candidate', { to: id, candidate: e.candidate });
          }
        };

        peer.ontrack = e => {
          setRemoteStreams(prev => [...prev, { id, stream: e.streams[0] }]);
        };

        peer.createOffer().then(offer => {
          peer.setLocalDescription(offer);
          socket.emit('offer', { to: id, offer });
        });
      });

      socket.on('offer', async ({ from, offer }) => {
        const peer = new RTCPeerConnection();
        peersRef.current[from] = peer;

        stream.getTracks().forEach(track => peer.addTrack(track, stream));

        peer.onicecandidate = e => {
          if (e.candidate) {
            socket.emit('candidate', { to: from, candidate: e.candidate });
          }
        };

        peer.ontrack = e => {
          setRemoteStreams(prev => [...prev, { id: from, stream: e.streams[0] }]);
        };

        await peer.setRemoteDescription(offer);
        const answer = await peer.createAnswer();
        await peer.setLocalDescription(answer);
        socket.emit('answer', { to: from, answer });
      });

      socket.on('answer', async ({ from, answer }) => {
        await peersRef.current[from].setRemoteDescription(answer);
      });

      socket.on('candidate', async ({ from, candidate }) => {
        await peersRef.current[from]?.addIceCandidate(candidate);
      });

      socket.on('user-left', ({ id }) => {
        if (peersRef.current[id]) {
          peersRef.current[id].close();
          delete peersRef.current[id];
          setRemoteStreams(prev => prev.filter(p => p.id !== id));
        }
      });
    });
  }, [joined, selectedCam]);

  const handleJoin = () => {
    if (roomId && name) {
      setJoined(true);
    }
  };

  const handleEnd = () => {
    Object.values(peersRef.current).forEach(peer => peer.close());
    localStream?.getTracks().forEach(track => track.stop());
    socket.emit('leave-room', { roomId });
    setJoined(false);
    setUsers([]);
    setRemoteStreams([]);
  };

  const toggleAudio = () => {
    if (localStream) {
      localStream.getAudioTracks()[0].enabled = !localStream.getAudioTracks()[0].enabled;
    }
  };

  const toggleVideo = () => {
    if (localStream) {
      localStream.getVideoTracks()[0].enabled = !localStream.getVideoTracks()[0].enabled;
    }
  };

  const handleScreenShare = async () => {
    if (!screenSharing.current) {
      const screenStream = await navigator.mediaDevices.getDisplayMedia({ video: true });
      const screenTrack = screenStream.getVideoTracks()[0];
      const sender = localStream.getVideoTracks()[0];
      Object.values(peersRef.current).forEach(peer => {
        const senders = peer.getSenders().filter(s => s.track.kind === 'video');
        senders.forEach(s => s.replaceTrack(screenTrack));
      });
      screenTrack.onended = () => {
        Object.values(peersRef.current).forEach(peer => {
          const senders = peer.getSenders().filter(s => s.track.kind === 'video');
          senders.forEach(s => s.replaceTrack(sender));
        });
      };
      screenSharing.current = true;
    }
  };

  if (!joined) {
    return (
      <div className="join-container">
        <h2>Join a Meeting</h2>
        <input placeholder="Enter Room Code" value={roomId} onChange={e => setRoomId(e.target.value)} />
        <input placeholder="Enter Your Name" value={name} onChange={e => setName(e.target.value)} />
        <select value={selectedCam} onChange={e => setSelectedCam(e.target.value)}>
          {cameras.map(cam => (
            <option key={cam.deviceId} value={cam.deviceId}>{cam.label || 'Camera'}</option>
          ))}
        </select>
        <button onClick={handleJoin}>Join</button>
      </div>
    );
  }

  return (
    <div className="room-container">
      <div className="header">
        <h2>Room: {roomId}</h2>
        <h3>User: {name}</h3>
      </div>
      <div className="video-grid">
        <div className="video-block">
          <video ref={localVideoRef} autoPlay muted playsInline className="video" />
          <p>You ({name})</p>
        </div>
        {remoteStreams.map(({ id, stream }) => (
          <div key={id} className="video-block">
            <video autoPlay playsInline srcObject={stream} className="video" />
            <p>{id}</p>
          </div>
        ))}
      </div>
      <div className="controls">
        <button onClick={toggleAudio}>Mute/Unmute</button>
        <button onClick={toggleVideo}>Video On/Off</button>
        <button onClick={handleScreenShare}>Share Screen</button>
        <button onClick={handleEnd}>End Meeting</button>
      </div>
      <div className="sidebar">
        <h4>Participants</h4>
        <ul>
          {users.map((u, i) => (
            <li key={i}>{u}</li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default Room;
