// Room.js

import React, { useEffect, useRef, useState } from 'react';
import io from 'socket.io-client';
import Peer from 'simple-peer';

const socket = io.connect('https://localhost:5000'); // Use your local or deployed server

function Room() {
  const [peers, setPeers] = useState([]);
  const [participants, setParticipants] = useState([]);
  const [userName, setUserName] = useState('');
  const [roomID, setRoomID] = useState('');
  const [micOn, setMicOn] = useState(true);
  const [camOn, setCamOn] = useState(true);
  const [screenSharing, setScreenSharing] = useState(false);
  const userVideo = useRef();
  const peersRef = useRef([]);
  const streamRef = useRef();

  useEffect(() => {
    const name = prompt('Enter your name');
    const room = prompt('Enter room code');
    setUserName(name);
    setRoomID(room);

    navigator.mediaDevices.getUserMedia({ video: true, audio: true }).then(stream => {
      streamRef.current = stream;
      if (userVideo.current) {
        userVideo.current.srcObject = stream;
      }

      socket.emit('join-room', { roomID: room, userName: name });

      socket.on('all-users', users => {
        const newPeers = [];
        users.forEach(user => {
          const peer = createPeer(user.socketID, socket.id, stream, name);
          peersRef.current.push({
            peerID: user.socketID,
            peer,
            userName: user.userName,
          });
          newPeers.push({ peerID: user.socketID, peer, userName: user.userName });
        });
        setPeers(newPeers);
      });

      socket.on('user-joined', payload => {
        const peer = addPeer(payload.signal, payload.callerID, stream, payload.userName);
        peersRef.current.push({
          peerID: payload.callerID,
          peer,
          userName: payload.userName,
        });
        setPeers(users => [...users, { peerID: payload.callerID, peer, userName: payload.userName }]);
      });

      socket.on('receiving-returned-signal', payload => {
        const item = peersRef.current.find(p => p.peerID === payload.id);
        item.peer.signal(payload.signal);
      });

      socket.on('update-user-list', users => {
        setParticipants(users);
      });

      socket.on('user-disconnected', id => {
        setPeers(peers => peers.filter(p => p.peerID !== id));
        peersRef.current = peersRef.current.filter(p => p.peerID !== id);
      });
    });
  }, []);

  function createPeer(userToSignal, callerID, stream, userName) {
    const peer = new Peer({
      initiator: true,
      trickle: false,
      stream,
    });

    peer.on('signal', signal => {
      socket.emit('sending-signal', { userToSignal, callerID, signal, userName });
    });

    return peer;
  }

  function addPeer(incomingSignal, callerID, stream, userName) {
    const peer = new Peer({
      initiator: false,
      trickle: false,
      stream,
    });

    peer.on('signal', signal => {
      socket.emit('returning-signal', { signal, callerID });
    });

    peer.signal(incomingSignal);

    return peer;
  }

  const toggleMic = () => {
    streamRef.current.getAudioTracks()[0].enabled = !micOn;
    setMicOn(!micOn);
  };

  const toggleCam = () => {
    streamRef.current.getVideoTracks()[0].enabled = !camOn;
    setCamOn(!camOn);
  };

  const handleScreenShare = async () => {
    if (!screenSharing) {
      const screenStream = await navigator.mediaDevices.getDisplayMedia({ video: true });
      const screenTrack = screenStream.getVideoTracks()[0];

      peersRef.current.forEach(({ peer }) => {
        peer.replaceTrack(streamRef.current.getVideoTracks()[0], screenTrack, streamRef.current);
      });

      screenTrack.onended = () => {
        peersRef.current.forEach(({ peer }) => {
          peer.replaceTrack(screenTrack, streamRef.current.getVideoTracks()[0], streamRef.current);
        });
        setScreenSharing(false);
      };

      setScreenSharing(true);
    }
  };

  const endMeeting = () => {
    window.location.reload();
  };

  return (
    <div style={{ backgroundColor: '#000', minHeight: '100vh', color: '#fff', padding: '10px' }}>
      <h1 style={{ color: 'gold', fontSize: '32px', textShadow: '2px 2px 4px #000' }}>Ve Meet</h1>
      <div style={{ marginBottom: '10px' }}>
        <strong>Room:</strong> {roomID} &nbsp; | &nbsp; <strong>You:</strong> {userName}
      </div>

      {/* Video Grid */}
      <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))', gap: '10px' }}>
        <div>
          <video muted ref={userVideo} autoPlay playsInline style={{ width: '100%', borderRadius: '10px' }} />
          <div style={{ textAlign: 'center' }}>{userName} (You)</div>
        </div>
        {peers.map((peerObj, index) => (
          <Video key={index} peer={peerObj.peer} name={peerObj.userName} />
        ))}
      </div>

      {/* Controls */}
      <div style={{ marginTop: '20px' }}>
        <button onClick={toggleMic} style={btnStyle}>{micOn ? 'Mute' : 'Unmute'}</button>
        <button onClick={toggleCam} style={btnStyle}>{camOn ? 'Video Off' : 'Video On'}</button>
        <button onClick={handleScreenShare} style={btnStyle}>Share Screen</button>
        <button onClick={endMeeting} style={btnStyle}>End Meeting</button>
      </div>

      {/* Participants */}
      <div style={{ marginTop: '20px' }}>
        <h3 style={{ color: 'gold' }}>Participants:</h3>
        <ul>
          <li>{userName} (You)</li>
          {participants.map((p, i) => (
            <li key={i}>{p}</li>
          ))}
        </ul>
      </div>
    </div>
  );
}

const Video = ({ peer, name }) => {
  const ref = useRef();

  useEffect(() => {
    peer.on('stream', stream => {
      if (ref.current) {
        ref.current.srcObject = stream;
      }
    });
  }, [peer]);

  return (
    <div>
      <video playsInline autoPlay ref={ref} style={{ width: '100%', borderRadius: '10px' }} />
      <div style={{ textAlign: 'center' }}>{name}</div>
    </div>
  );
};

const btnStyle = {
  margin: '5px',
  padding: '10px 15px',
  backgroundColor: 'gold',
  border: 'none',
  borderRadius: '8px',
  fontWeight: 'bold',
  cursor: 'pointer',
};

export default Room;
