import React, { useEffect, useRef, useState } from "react";
import io from "socket.io-client";
import Peer from "simple-peer";
import "./Room.css"; // Black & gold UI styles

const socket = io.connect("https://192.168.29.23:5000");

const GuestRoom = () => {
  const [peers, setPeers] = useState([]);
  const [name, setName] = useState("");
  const [roomID, setRoomID] = useState("");
  const [joined, setJoined] = useState(false);
  const [stream, setStream] = useState(null);
  const peersRef = useRef([]);
  const userVideo = useRef();

  const videoGridRef = useRef();

  useEffect(() => {
    socket.on("user-connected", (payload) => {
      const peer = addPeer(payload.signal, payload.callerID, stream);
      peersRef.current.push({
        peerID: payload.callerID,
        peer,
        name: payload.name,
      });
      setPeers((users) => [...users, { peerID: payload.callerID, peer, name: payload.name }]);
    });

    socket.on("receiving-returned-signal", (payload) => {
      const item = peersRef.current.find((p) => p.peerID === payload.id);
      if (item) {
        item.peer.signal(payload.signal);
      }
    });

    socket.on("user-disconnected", (id) => {
      const peerObj = peersRef.current.find((p) => p.peerID === id);
      if (peerObj) {
        peerObj.peer.destroy();
      }
      peersRef.current = peersRef.current.filter((p) => p.peerID !== id);
      setPeers((users) => users.filter((p) => p.peerID !== id));
    });
  }, [stream]);

  const handleJoin = async () => {
    try {
      const mediaStream = await navigator.mediaDevices.getUserMedia({
        video: true,
        audio: true,
      });
      setStream(mediaStream);
      if (userVideo.current) {
        userVideo.current.srcObject = mediaStream;
      }
      setJoined(true);

      socket.emit("join-room", { roomID, name });

      socket.on("all-users", (users) => {
        const peersToAdd = [];
        users.forEach((user) => {
          const peer = createPeer(user.socketID, socket.id, mediaStream);
          peersRef.current.push({
            peerID: user.socketID,
            peer,
            name: user.name,
          });
          peersToAdd.push({ peerID: user.socketID, peer, name: user.name });
        });
        setPeers(peersToAdd);
      });
    } catch (err) {
      alert("Error accessing media devices.");
      console.error(err);
    }
  };

  const createPeer = (userToSignal, callerID, stream) => {
    const peer = new Peer({
      initiator: true,
      trickle: false,
      stream,
    });

    peer.on("signal", (signal) => {
      socket.emit("sending-signal", {
        userToSignal,
        callerID,
        signal,
        name,
      });
    });

    return peer;
  };

  const addPeer = (incomingSignal, callerID, stream) => {
    const peer = new Peer({
      initiator: false,
      trickle: false,
      stream,
    });

    peer.on("signal", (signal) => {
      socket.emit("returning-signal", {
        signal,
        callerID,
      });
    });

    peer.signal(incomingSignal);

    return peer;
  };

  const handleLeave = () => {
    socket.disconnect();
    window.location.reload();
  };

  const toggleAudio = () => {
    if (stream) {
      stream.getAudioTracks()[0].enabled = !stream.getAudioTracks()[0].enabled;
    }
  };

  const toggleVideo = () => {
    if (stream) {
      stream.getVideoTracks()[0].enabled = !stream.getVideoTracks()[0].enabled;
    }
  };

  return (
    <div className="room-container">
      <div className="header">
        <h2 className="logo">Ve Meet</h2>
        <div className="controls">
          {joined && (
            <>
              <button onClick={toggleAudio}>Mute/Unmute</button>
              <button onClick={toggleVideo}>Video On/Off</button>
              <button onClick={handleLeave}>Leave</button>
            </>
          )}
        </div>
      </div>

      {!joined ? (
        <div className="join-box">
          <input
            type="text"
            placeholder="Enter Your Name"
            value={name}
            onChange={(e) => setName(e.target.value)}
          />
          <input
            type="text"
            placeholder="Enter Room Code"
            value={roomID}
            onChange={(e) => setRoomID(e.target.value)}
          />
          <button onClick={handleJoin}>Join</button>
        </div>
      ) : (
        <div className="video-grid" ref={videoGridRef}>
          <div className="video-block">
            <video ref={userVideo} autoPlay playsInline muted className="video" />
            <div className="name-label">{name} (You)</div>
          </div>
          {peers.map((peerObj, index) => (
            <Video key={peerObj.peerID} peer={peerObj.peer} name={peerObj.name} />
          ))}
        </div>
      )}
    </div>
  );
};

const Video = ({ peer, name }) => {
  const ref = useRef();

  useEffect(() => {
    peer.on("stream", (stream) => {
      if (ref.current) {
        ref.current.srcObject = stream;
      }
    });
  }, [peer]);

  return (
    <div className="video-block">
      <video playsInline autoPlay ref={ref} className="video" />
      <div className="name-label">{name}</div>
    </div>
  );
};

export default GuestRoom;
