// Room.js
import React, { useEffect, useRef, useState } from "react";
import io from "socket.io-client";
import Peer from "simple-peer";
import "./Room.css";

const socket = io.connect("https://192.168.29.23:5000");

const Room = () => {
  const [peers, setPeers] = useState([]);
  const [participants, setParticipants] = useState([]);
  const [stream, setStream] = useState();
  const [videoEnabled, setVideoEnabled] = useState(true);
  const [audioEnabled, setAudioEnabled] = useState(true);

  const userVideo = useRef();
  const peersRef = useRef([]);
  const roomID = localStorage.getItem("roomID");
  const userName = localStorage.getItem("userName");

  useEffect(() => {
    navigator.mediaDevices.getUserMedia({ video: true, audio: true }).then((stream) => {
      setStream(stream);
      if (userVideo.current) {
        userVideo.current.srcObject = stream;
      }

      socket.emit("join-room", { roomID, userName });

      socket.on("all-users", (users) => {
        const peers = [];
        users.forEach((user) => {
          const peer = createPeer(user.socketID, socket.id, stream);
          peersRef.current.push({
            peerID: user.socketID,
            peer,
            userName: user.userName,
          });
          peers.push({ peerID: user.socketID, peer, userName: user.userName });
        });
        setPeers(peers);
        setParticipants((prev) => [...prev, userName, ...users.map(u => u.userName)]);
      });

      socket.on("user-joined", (payload) => {
        const peer = addPeer(payload.signal, payload.callerID, stream);
        peersRef.current.push({
          peerID: payload.callerID,
          peer,
          userName: payload.userName,
        });
        setPeers((users) => [...users, { peerID: payload.callerID, peer, userName: payload.userName }]);
        setParticipants((prev) => [...prev, payload.userName]);
      });

      socket.on("receiving-returned-signal", (payload) => {
        const item = peersRef.current.find(p => p.peerID === payload.id);
        if (item) item.peer.signal(payload.signal);
      });
    });

    socket.on("user-left", (id) => {
      const peerObj = peersRef.current.find(p => p.peerID === id);
      if (peerObj) peerObj.peer.destroy();
      peersRef.current = peersRef.current.filter(p => p.peerID !== id);
      setPeers((users) => users.filter(p => p.peerID !== id));
    });
  }, []);

  function createPeer(userToSignal, callerID, stream) {
    const peer = new Peer({
      initiator: true,
      trickle: false,
      stream,
    });

    peer.on("signal", signal => {
      socket.emit("sending-signal", { userToSignal, callerID, signal, userName });
    });

    return peer;
  }

  function addPeer(incomingSignal, callerID, stream) {
    const peer = new Peer({
      initiator: false,
      trickle: false,
      stream,
    });

    peer.on("signal", signal => {
      socket.emit("returning-signal", { signal, callerID });
    });

    peer.signal(incomingSignal);
    return peer;
  }

  const toggleAudio = () => {
    stream.getAudioTracks()[0].enabled = !audioEnabled;
    setAudioEnabled(!audioEnabled);
  };

  const toggleVideo = () => {
    stream.getVideoTracks()[0].enabled = !videoEnabled;
    setVideoEnabled(!videoEnabled);
  };

  const endCall = () => {
    socket.disconnect();
    window.location.href = "/";
  };

  return (
    <div className="room-container">
      <div className="top-bar">ðŸŽ¥ <span className="logo-text">Ve Meet</span> - Host Panel</div>

      <div className="video-grid">
        <div className="video-block">
          <video muted ref={userVideo} autoPlay playsInline />
          <p>{userName} (You)</p>
        </div>
        {peers.map((peerObj, index) => (
          <Video key={index} peer={peerObj.peer} name={peerObj.userName} />
        ))}
      </div>

      <div className="controls">
        <button onClick={toggleAudio}>{audioEnabled ? "Mute" : "Unmute"}</button>
        <button onClick={toggleVideo}>{videoEnabled ? "Video Off" : "Video On"}</button>
        <button onClick={endCall}>End Meeting</button>
      </div>

      <div className="participant-list">
        <h4>ðŸ‘¥ Participants ({participants.length})</h4>
        <ul>{participants.map((p, i) => <li key={i}>{p}</li>)}</ul>
      </div>
    </div>
  );
};

const Video = ({ peer, name }) => {
  const ref = useRef();

  useEffect(() => {
    peer.on("stream", stream => {
      if (ref.current) ref.current.srcObject = stream;
    });
  }, [peer]);

  return (
    <div className="video-block">
      <video playsInline autoPlay ref={ref} />
      <p>{name}</p>
    </div>
  );
};

export default Room;
