// ‚úÖ Room.js (Final Working Version from 26 July 2025 Evening)

import React, { useEffect, useRef, useState } from "react";
import io from "socket.io-client";
import Peer from "simple-peer";
import "./Room.css";

const socket = io("https://192.168.29.23:5000", {
  transports: ["websocket"],
});

const Room = () => {
  const [peers, setPeers] = useState([]);
  const [localStream, setLocalStream] = useState();
  const [userName, setUserName] = useState("");
  const [roomID, setRoomID] = useState("");
  const [participants, setParticipants] = useState([]);
  const userVideo = useRef();
  const peersRef = useRef([]);

  useEffect(() => {
    const urlParams = new URLSearchParams(window.location.search);
    const roomIdFromURL = urlParams.get("room");
    const nameFromURL = urlParams.get("name");

    setRoomID(roomIdFromURL);
    setUserName(nameFromURL);

    navigator.mediaDevices.getUserMedia({ video: true, audio: true }).then((stream) => {
      setLocalStream(stream);
      if (userVideo.current) {
        userVideo.current.srcObject = stream;
      }

      socket.emit("join-room", { roomID: roomIdFromURL, name: nameFromURL });

      socket.on("all-users", (users) => {
        const newPeers = [];
        users.forEach(({ socketID, name }) => {
          const peer = createPeer(socketID, socket.id, stream);
          peersRef.current.push({ peerID: socketID, peer, name });
          newPeers.push({ peerID: socketID, peer, name });
        });
        setPeers(newPeers);
      });

      socket.on("user-joined", ({ signal, callerID, name }) => {
        const peer = addPeer(signal, callerID, stream);
        peersRef.current.push({ peerID: callerID, peer, name });
        setPeers((prev) => [...prev, { peerID: callerID, peer, name }]);
      });

      socket.on("receiving-returned-signal", ({ id, signal }) => {
        const item = peersRef.current.find((p) => p.peerID === id);
        if (item) {
          item.peer.signal(signal);
        }
      });

      socket.on("user-disconnected", (id) => {
        setPeers((prevPeers) => prevPeers.filter((p) => p.peerID !== id));
        peersRef.current = peersRef.current.filter((p) => p.peerID !== id);
        setParticipants((prev) => prev.filter((p) => p.socketID !== id));
      });
    });

    socket.on("update-user-list", (userList) => {
      setParticipants(userList);
    });
  }, []);

  function createPeer(userToSignal, callerID, stream) {
    const peer = new Peer({ initiator: true, trickle: false, stream });

    peer.on("signal", (signal) => {
      socket.emit("sending-signal", { userToSignal, callerID, signal, name: userName });
    });

    return peer;
  }

  function addPeer(incomingSignal, callerID, stream) {
    const peer = new Peer({ initiator: false, trickle: false, stream });

    peer.on("signal", (signal) => {
      socket.emit("returning-signal", { signal, callerID });
    });

    peer.signal(incomingSignal);
    return peer;
  }

  const handleEndMeeting = () => {
    window.location.href = "/";
  };

  return (
    <div className="room-container">
      <div className="header">
        <h1 className="logo-text">Ve Meet</h1>
        <div className="room-details">Room Code: {roomID}</div>
      </div>

      <div className="main-grid">
        <div className="video-grid">
          <div className="video-box">
            <video muted ref={userVideo} autoPlay playsInline className="video" />
            <div className="name-tag">{userName} (You)</div>
          </div>
          {peers.map(({ peerID, peer, name }, index) => (
            <Video key={peerID} peer={peer} name={name} />
          ))}
        </div>

        <div className="sidebar">
          <h3>Participants ({participants.length})</h3>
          <ul>
            {participants.map((p) => (
              <li key={p.socketID}>{p.name}</li>
            ))}
          </ul>
        </div>
      </div>

      <div className="controls-vertical">
        <button className="end-btn" onClick={handleEndMeeting}>End</button>
        <button className="control-btn">üé§</button>
        <button className="control-btn">üé•</button>
        <button className="control-btn">üñ•Ô∏è</button>
      </div>
    </div>
  );
};

const Video = ({ peer, name }) => {
  const ref = useRef();

  useEffect(() => {
    peer.on("stream", (stream) => {
      if (ref.current) {
        ref.current.srcObject = stream;
      }
    });
  }, [peer]);

  return (
    <div className="video-box">
      <video playsInline autoPlay ref={ref} className="video" />
      <div className="name-tag">{name}</div>
    </div>
  );
};

export default Room;
