// GuestRoom.js
import React, { useEffect, useRef, useState } from "react";
import io from "socket.io-client";
import Peer from "simple-peer";
import "./Room.css";

const socket = io("https://192.168.29.23:5000");

const GuestRoom = () => {
  const [peers, setPeers] = useState([]);
  const [stream, setStream] = useState();
  const [muted, setMuted] = useState(false);
  const [videoOff, setVideoOff] = useState(false);
  const [joined, setJoined] = useState(false);
  const [roomID, setRoomID] = useState("");
  const [name, setName] = useState("");
  const videoRef = useRef();
  const peersRef = useRef([]);

  useEffect(() => {
    socket.on("user-disconnected", (socketID) => {
      const peerObj = peersRef.current.find((p) => p.peerID === socketID);
      if (peerObj) {
        peerObj.peer.destroy();
      }
      peersRef.current = peersRef.current.filter((p) => p.peerID !== socketID);
      setPeers((users) => users.filter((p) => p.peerID !== socketID));
    });
  }, []);

  function handleJoin() {
    navigator.mediaDevices
      .getUserMedia({ video: true, audio: true })
      .then((stream) => {
        setStream(stream);
        videoRef.current.srcObject = stream;
        socket.emit("join-room", { roomID, name });

        socket.on("all-users", (users) => {
          const peers = [];
          users.forEach((user) => {
            const peer = createPeer(user.socketID, socket.id, stream);
            peersRef.current.push({
              peerID: user.socketID,
              peer,
              name: user.name,
            });
            peers.push({ peerID: user.socketID, peer, name: user.name });
          });
          setPeers(peers);
        });

        socket.on("user-joined", (payload) => {
          const peer = addPeer(payload.signal, payload.callerID, stream);
          peersRef.current.push({
            peerID: payload.callerID,
            peer,
            name: payload.name,
          });

          setPeers((users) => [
            ...users,
            { peerID: payload.callerID, peer, name: payload.name },
          ]);
        });

        socket.on("receiving-returned-signal", (payload) => {
          const item = peersRef.current.find((p) => p.peerID === payload.id);
          if (item) {
            item.peer.signal(payload.signal);
          }
        });

        setJoined(true);
      });
  }

  function createPeer(userToSignal, callerID, stream) {
    const peer = new Peer({
      initiator: true,
      trickle: false,
      stream,
    });

    peer.on("signal", (signal) => {
      socket.emit("sending-signal", {
        userToSignal,
        callerID,
        signal,
        name,
      });
    });

    return peer;
  }

  function addPeer(incomingSignal, callerID, stream) {
    const peer = new Peer({
      initiator: false,
      trickle: false,
      stream,
    });

    peer.on("signal", (signal) => {
      socket.emit("returning-signal", { signal, callerID });
    });

    peer.signal(incomingSignal);
    return peer;
  }

  const toggleMute = () => {
    stream.getAudioTracks()[0].enabled = !stream.getAudioTracks()[0].enabled;
    setMuted(!muted);
  };

  const toggleVideo = () => {
    stream.getVideoTracks()[0].enabled = !stream.getVideoTracks()[0].enabled;
    setVideoOff(!videoOff);
  };

  const handleLeave = () => {
    peersRef.current.forEach((p) => p.peer.destroy());
    socket.disconnect();
    window.location.reload();
  };

  return (
    <div className="roomContainer">
      <div className="header">
        <h1 className="logo">Ve Meet</h1>
        <p className="room-code">Room: {roomID}</p>
        <p className="user-name">You: {name}</p>
      </div>

      {!joined ? (
        <div className="joinScreen">
          <input
            placeholder="Enter Room Code"
            value={roomID}
            onChange={(e) => setRoomID(e.target.value)}
          />
          <input
            placeholder="Your Name"
            value={name}
            onChange={(e) => setName(e.target.value)}
          />
          <button onClick={handleJoin}>Join</button>
        </div>
      ) : (
        <>
          <div className="video-grid">
            <div className="video-block">
              <video ref={videoRef} autoPlay muted playsInline />
              <p>{name} (You)</p>
            </div>

            {peers.map((peerData, index) => (
              <Video key={index} peer={peerData.peer} name={peerData.name} />
            ))}
          </div>

          <div className="controls">
            <button onClick={toggleMute}>{muted ? "Unmute" : "Mute"}</button>
            <button onClick={toggleVideo}>
              {videoOff ? "Start Video" : "Stop Video"}
            </button>
            <button onClick={handleLeave}>Leave</button>
          </div>
        </>
      )}
    </div>
  );
};

const Video = ({ peer, name }) => {
  const ref = useRef();

  useEffect(() => {
    peer.on("stream", (stream) => {
      ref.current.srcObject = stream;
    });
  }, [peer]);

  return (
    <div className="video-block">
      <video playsInline autoPlay ref={ref} />
      <p>{name}</p>
    </div>
  );
};

export default GuestRoom;
