import React, { useEffect, useRef, useState } from "react";
import { useParams } from "react-router-dom";
import io from "socket.io-client";

const SERVER_URL = "http://localhost:5000"; // change if needed

const ICE_SERVERS = {
  iceServers: [
    { urls: "stun:stun.l.google.com:19302" }, // Google's public STUN server
  ],
};

const GuestRoom = () => {
  const { roomId } = useParams();

  const [name, setName] = useState("");
  const [joined, setJoined] = useState(false);
  const [error, setError] = useState("");
  const [muted, setMuted] = useState(false);

  const localVideoRef = useRef(null);
  const remoteVideoRef = useRef(null);

  const localStreamRef = useRef(null);
  const peerConnectionRef = useRef(null);
  const socketRef = useRef(null);

  // Set local video stream to video element
  useEffect(() => {
    if (localVideoRef.current && localStreamRef.current) {
      localVideoRef.current.srcObject = localStreamRef.current;
    }
  }, [joined]);

  // Set remote video stream to video element
  const [remoteStream, setRemoteStream] = useState(null);
  useEffect(() => {
    if (remoteVideoRef.current && remoteStream) {
      remoteVideoRef.current.srcObject = remoteStream;
    }
  }, [remoteStream]);

  // Initialize peer connection and socket listeners
  const initConnection = () => {
    peerConnectionRef.current = new RTCPeerConnection(ICE_SERVERS);

    // Add local stream tracks to peer connection
    localStreamRef.current.getTracks().forEach((track) => {
      peerConnectionRef.current.addTrack(track, localStreamRef.current);
    });

    // Listen for remote track from host
    peerConnectionRef.current.ontrack = (event) => {
      // event.streams[0] is the remote stream
      setRemoteStream(event.streams[0]);
    };

    // ICE candidates sent to host via socket
    peerConnectionRef.current.onicecandidate = (event) => {
      if (event.candidate) {
        socketRef.current.emit("guest-ice-candidate", {
          roomCode: roomId,
          candidate: event.candidate,
        });
      }
    };

    // Handle connection state changes for debug/logging
    peerConnectionRef.current.onconnectionstatechange = () => {
      if (peerConnectionRef.current.connectionState === "disconnected") {
        console.log("Disconnected from host");
      }
    };
  };

  const handleJoin = async () => {
    if (!name.trim()) {
      setError("Please enter your name");
      return;
    }

    try {
      // Get local media stream
      const stream = await navigator.mediaDevices.getUserMedia({
        video: true,
        audio: true,
      });
      localStreamRef.current = stream;

      socketRef.current = io(SERVER_URL);

      socketRef.current.on("connect", () => {
        // Tell server guest wants to join
        socketRef.current.emit("guest-join-room", { roomCode: roomId, name });

        // Init peer connection and add tracks
        initConnection();

        setJoined(true);
        setError("");
      });

      // When host sends WebRTC offer
      socketRef.current.on("host-offer", async ({ sdp }) => {
        console.log("Received host offer");
        if (!peerConnectionRef.current) return;

        await peerConnectionRef.current.setRemoteDescription(
          new RTCSessionDescription(sdp)
        );

        // Create answer for host
        const answer = await peerConnectionRef.current.createAnswer();
        await peerConnectionRef.current.setLocalDescription(answer);

        // Send answer to host
        socketRef.current.emit("guest-answer", {
          roomCode: roomId,
          sdp: peerConnectionRef.current.localDescription,
        });
      });

      // When host sends ICE candidate
      socketRef.current.on("host-ice-candidate", async ({ candidate }) => {
        if (peerConnectionRef.current && candidate) {
          try {
            await peerConnectionRef.current.addIceCandidate(candidate);
          } catch (e) {
            console.error("Error adding received ICE candidate", e);
          }
        }
      });

      socketRef.current.on("connect_error", (err) => {
        setError("Socket connection error: " + err.message);
      });
    } catch (err) {
      setError("Error accessing camera/microphone: " + err.message);
    }
  };

  const toggleMute = () => {
    if (localStreamRef.current) {
      localStreamRef.current.getAudioTracks().forEach((track) => {
        track.enabled = muted;
      });
      setMuted(!muted);
    }
  };

  const handleLeave = () => {
    if (socketRef.current) {
      socketRef.current.disconnect();
      socketRef.current = null;
    }
    if (peerConnectionRef.current) {
      peerConnectionRef.current.close();
      peerConnectionRef.current = null;
    }
    if (localStreamRef.current) {
      localStreamRef.current.getTracks().forEach((track) => track.stop());
      localStreamRef.current = null;
    }
    setRemoteStream(null);
    setJoined(false);
    setMuted(false);
    setError("");
    setName("");
  };

  return (
    <div
      style={{
        backgroundColor: "#121212",
        color: "#D4AF37",
        minHeight: "100vh",
        padding: "20px",
        fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
      }}
    >
      <h2>Join Room: {roomId}</h2>

      {!joined ? (
        <div style={{ marginTop: "20px", textAlign: "center" }}>
          <input
            type="text"
            placeholder="Enter your name"
            value={name}
            onChange={(e) => setName(e.target.value)}
            style={{
              padding: "10px",
              fontSize: "16px",
              borderRadius: "5px",
              border: "2px solid #D4AF37",
              backgroundColor: "#222",
              color: "#fff",
              marginBottom: "15px",
              width: "250px",
              outline: "none",
            }}
          />
          <br />
          <button
            onClick={handleJoin}
            style={{
              padding: "10px 30px",
              backgroundColor: "#D4AF37",
              color: "#121212",
              border: "none",
              borderRadius: "5px",
              fontWeight: "bold",
              cursor: "pointer",
              fontSize: "16px",
            }}
          >
            Join
          </button>
          {error && (
            <p style={{ color: "red", marginTop: "15px" }}>{error}</p>
          )}
        </div>
      ) : (
        <div
          style={{
            marginTop: "20px",
            display: "flex",
            gap: "20px",
            flexWrap: "wrap",
            justifyContent: "center",
          }}
        >
          {/* Local Video */}
          <div>
            <h3>Your Video</h3>
            <video
              ref={localVideoRef}
              autoPlay
              playsInline
              muted
              style={{
                width: "320px",
                height: "240px",
                borderRadius: "8px",
                backgroundColor: "#000",
                border: "2px solid #D4AF37",
              }}
            />
          </div>

          {/* Remote Video */}
          {remoteStream && (
            <div>
              <h3>Host Video</h3>
              <video
                ref={remoteVideoRef}
                autoPlay
                playsInline
                style={{
                  width: "320px",
                  height: "240px",
                  borderRadius: "8px",
                  backgroundColor: "#000",
                  border: "2px solid #D4AF37",
                }}
              />
            </div>
          )}

          {/* Controls */}
          <div style={{ alignSelf: "flex-start", marginTop: "40px" }}>
            <button
              onClick={toggleMute}
              style={{
                padding: "10px 30px",
                backgroundColor: muted ? "#555" : "#D4AF37",
                color: muted ? "#ccc" : "#121212",
                border: "none",
                borderRadius: "5px",
                marginRight: "15px",
                cursor: "pointer",
                fontWeight: "bold",
              }}
            >
              {muted ? "Unmute" : "Mute"}
            </button>
            <button
              onClick={handleLeave}
              style={{
                padding: "10px 30px",
                backgroundColor: "#a00",
                color: "#fff",
                border: "none",
                borderRadius: "5px",
                cursor: "pointer",
                fontWeight: "bold",
              }}
            >
              Leave
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default GuestRoom;
