import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { v4 as uuidv4 } from 'uuid';

const Home = () => {
  const [roomId, setRoomId] = useState('');
  const [userName, setUserName] = useState('');
  const [generatedRoom, setGeneratedRoom] = useState('');
  const navigate = useNavigate();

  const handleCreateRoom = () => {
    const newRoomId = uuidv4();
    setGeneratedRoom(newRoomId);
  };

  const handleJoinRoom = () => {
    if (roomId.trim() && userName.trim()) {
      navigate(`/room/${roomId}?name=${userName}`);
    }
  };

  const handleStartMeeting = () => {
    if (generatedRoom && userName.trim()) {
      navigate(`/room/${generatedRoom}?name=${userName}`);
    }
  };

  return (
    <div style={styles.container}>
      <h1 style={styles.logo}>âœ¨ Ve <span style={styles.gold}>Meet</span></h1>

      <input
        type="text"
        placeholder="Enter your name"
        value={userName}
        onChange={(e) => setUserName(e.target.value)}
        style={styles.input}
      />

      <div style={styles.buttons}>
        <button onClick={handleCreateRoom} style={styles.button}>ðŸŽ¬ Create Room</button>
        <button onClick={handleJoinRoom} style={styles.button}>ðŸ”— Join Room</button>
      </div>

      <input
        type="text"
        placeholder="Enter Room Code"
        value={roomId}
        onChange={(e) => setRoomId(e.target.value)}
        style={styles.input}
      />

      {generatedRoom && (
        <div style={styles.roomBox}>
          <p>âœ… Room Code: <strong>{generatedRoom}</strong></p>
          <p>ðŸ”— Share Link: <br />
            <code style={styles.link}>
              {`${window.location.origin}/room/${generatedRoom}?name=${encodeURIComponent(userName)}`}
            </code>
          </p>
          <button onClick={handleStartMeeting} style={styles.button}>ðŸšª Start Meeting</button>
        </div>
      )}
    </div>
  );
};

const styles = {
  container: {
    backgroundColor: '#000',
    color: 'white',
    height: '100vh',
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    justifyContent: 'center',
    fontFamily: 'Arial, sans-serif',
    padding: '20px',
  },
  logo: {
    fontSize: '3rem',
    marginBottom: '30px',
    fontWeight: 'bold',
    letterSpacing: '2px',
  },
  gold: {
    color: 'gold',
  },
  input: {
    padding: '12px 16px',
    margin: '10px',
    fontSize: '16px',
    borderRadius: '8px',
    border: '1px solid #ccc',
    width: '300px',
    backgroundColor: '#111',
    color: 'white',
  },
  button: {
    backgroundColor: 'gold',
    color: 'black',
    padding: '12px 20px',
    margin: '10px',
    border: 'none',
    borderRadius: '10px',
    fontWeight: 'bold',
    cursor: 'pointer',
  },
  buttons: {
    display: 'flex',
    flexDirection: 'row',
  },
  roomBox: {
    backgroundColor: '#111',
    color: 'white',
    padding: '20px',
    borderRadius: '10px',
    marginTop: '20px',
    width: '80%',
    maxWidth: '600px',
    textAlign: 'center',
  },
  link: {
    color: 'lightgreen',
    wordWrap: 'break-word',
    display: 'inline-block',
    marginTop: '10px',
  },
};

export default Home;
