// Room.js (HOST SIDE FINAL FIXED VERSION)

import React, { useEffect, useRef, useState } from 'react';
import io from 'socket.io-client';
import Peer from 'simple-peer';
import './Room.css';

const socket = io.connect('https://192.168.29.23:5000');

const Room = () => {
  const [peers, setPeers] = useState([]);
  const [stream, setStream] = useState();
  const [userName, setUserName] = useState('');
  const [roomID, setRoomID] = useState('');
  const [joined, setJoined] = useState(false);
  const [participants, setParticipants] = useState([]);
  const userVideo = useRef();
  const peersRef = useRef([]);

  useEffect(() => {
    socket.on('all-users', (users) => {
      const peers = [];
      users.forEach(({ socketID, name }) => {
        const peer = createPeer(socketID, socket.id, stream);
        peersRef.current.push({ peerID: socketID, peer, name });
        peers.push({ peerID: socketID, peer, name });
      });
      setPeers(peers);
    });

    socket.on('user-joined', ({ signal, callerID, name }) => {
      const peer = addPeer(signal, callerID, stream);
      peersRef.current.push({ peerID: callerID, peer, name });
      setPeers((prevPeers) => [...prevPeers, { peerID: callerID, peer, name }]);
    });

    socket.on('receiving-returned-signal', ({ id, signal }) => {
      const item = peersRef.current.find(p => p.peerID === id);
      if (item) {
        item.peer.signal(signal);
      }
    });

    socket.on('update-user-list', (userList) => {
      setParticipants(userList);
    });
  }, [stream]);

  const createPeer = (userToSignal, callerID, stream) => {
    const peer = new Peer({ initiator: true, trickle: false, stream });
    peer.on('signal', (signal) => {
      socket.emit('sending-signal', { userToSignal, callerID, signal, name: userName });
    });
    return peer;
  };

  const addPeer = (incomingSignal, callerID, stream) => {
    const peer = new Peer({ initiator: false, trickle: false, stream });
    peer.on('signal', (signal) => {
      socket.emit('returning-signal', { signal, callerID });
    });
    peer.signal(incomingSignal);
    return peer;
  };

  const handleJoin = async () => {
    try {
      const userMedia = await navigator.mediaDevices.getUserMedia({ video: true, audio: true });
      setStream(userMedia);
      if (userVideo.current) {
        userVideo.current.srcObject = userMedia;
      }
      socket.emit('join-room', { roomID, name: userName });
      setJoined(true);
    } catch (error) {
      console.error('Media error:', error);
    }
  };

  const handleEndMeeting = () => {
    window.location.reload();
  };

  return (
    <div className="room-container">
      <h1 className="logo">Ve Meet</h1>
      {!joined ? (
        <div className="join-panel">
          <input placeholder="Your Name" onChange={(e) => setUserName(e.target.value)} />
          <input placeholder="Room ID" onChange={(e) => setRoomID(e.target.value)} />
          <button onClick={handleJoin}>Join as Host</button>
        </div>
      ) : (
        <div className="video-panel">
          <div className="video-grid">
            <div className="video-box">
              <video ref={userVideo} autoPlay playsInline muted />
              <p>{userName} (You)</p>
            </div>
            {peers.map((peerObj, index) => (
              <Video key={peerObj.peerID} peer={peerObj.peer} name={peerObj.name} />
            ))}
          </div>
          <div className="side-panel">
            <h3>Participants ({participants.length})</h3>
            <ul>
              {participants.map((p, i) => (
                <li key={i}>{p}</li>
              ))}
            </ul>
            <button onClick={handleEndMeeting}>End Meeting</button>
          </div>
        </div>
      )}
    </div>
  );
};

const Video = ({ peer, name }) => {
  const ref = useRef();

  useEffect(() => {
    peer.on('stream', (stream) => {
      if (ref.current) ref.current.srcObject = stream;
    });
  }, [peer]);

  return (
    <div className="video-box">
      <video ref={ref} autoPlay playsInline />
      <p>{name}</p>
    </div>
  );
};

export default Room;

