import React, { useEffect, useRef, useState } from "react";
import { useParams } from "react-router-dom";
import io from "socket.io-client";

// Change this to your actual backend URL if different
const SERVER_URL = "http://localhost:5000";

const GuestRoom = () => {
  const { roomId } = useParams(); // get room code from URL
  const [name, setName] = useState("");
  const [joined, setJoined] = useState(false);
  const [error, setError] = useState("");
  const [muted, setMuted] = useState(false);

  const localVideoRef = useRef(null);
  const socketRef = useRef(null);
  const localStreamRef = useRef(null);

  const handleJoin = async () => {
    if (!name.trim()) {
      setError("Please enter your name");
      return;
    }

    try {
      // Access camera and mic
      const stream = await navigator.mediaDevices.getUserMedia({
        video: true,
        audio: true,
      });

      localStreamRef.current = stream;

      if (localVideoRef.current) {
        localVideoRef.current.srcObject = stream;
      }

      // Connect to socket.io server
      socketRef.current = io(SERVER_URL);

      socketRef.current.on("connect", () => {
        // Join the room with roomId and name
        socketRef.current.emit("join-room", { roomCode: roomId, name });
        setJoined(true);
        setError("");
      });

      // Handle socket errors
      socketRef.current.on("connect_error", (err) => {
        setError("Socket connection error: " + err.message);
      });

      // You can add more socket event handlers here (like receiving host streams)

    } catch (err) {
      setError("Error accessing camera/microphone: " + err.message);
    }
  };

  const toggleMute = () => {
    if (localStreamRef.current) {
      localStreamRef.current.getAudioTracks().forEach((track) => {
        track.enabled = muted;
      });
      setMuted(!muted);
    }
  };

  const handleLeave = () => {
    if (socketRef.current) {
      socketRef.current.disconnect();
      socketRef.current = null;
    }
    if (localStreamRef.current) {
      localStreamRef.current.getTracks().forEach((track) => track.stop());
      localStreamRef.current = null;
    }
    setJoined(false);
    setMuted(false);
    setError("");
    setName("");
  };

  return (
    <div
      style={{
        backgroundColor: "#121212",
        color: "#D4AF37",
        height: "100vh",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        paddingTop: "30px",
        fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
      }}
    >
      <h2>Join Room: {roomId}</h2>

      {!joined ? (
        <div style={{ marginTop: "20px", textAlign: "center" }}>
          <input
            type="text"
            placeholder="Enter your name"
            value={name}
            onChange={(e) => setName(e.target.value)}
            style={{
              padding: "10px",
              fontSize: "16px",
              borderRadius: "5px",
              border: "2px solid #D4AF37",
              backgroundColor: "#222",
              color: "#fff",
              marginBottom: "15px",
              width: "250px",
              outline: "none",
            }}
          />
          <br />
          <button
            onClick={handleJoin}
            style={{
              padding: "10px 30px",
              backgroundColor: "#D4AF37",
              color: "#121212",
              border: "none",
              borderRadius: "5px",
              fontWeight: "bold",
              cursor: "pointer",
              fontSize: "16px",
            }}
          >
            Join
          </button>
          {error && <p style={{ color: "red", marginTop: "15px" }}>{error}</p>}
        </div>
      ) : (
        <div style={{ marginTop: "20px", textAlign: "center" }}>
          <video
            ref={localVideoRef}
            autoPlay
            playsInline
            muted
            style={{
              width: "320px",
              height: "240px",
              borderRadius: "8px",
              backgroundColor: "#000",
              border: "2px solid #D4AF37",
            }}
          />
          <div style={{ marginTop: "15px" }}>
            <button
              onClick={toggleMute}
              style={{
                padding: "8px 20px",
                backgroundColor: muted ? "#555" : "#D4AF37",
                color: muted ? "#ccc" : "#121212",
                border: "none",
                borderRadius: "5px",
                marginRight: "15px",
                cursor: "pointer",
                fontWeight: "bold",
              }}
            >
              {muted ? "Unmute" : "Mute"}
            </button>
            <button
              onClick={handleLeave}
              style={{
                padding: "8px 20px",
                backgroundColor: "#a00",
                color: "#fff",
                border: "none",
                borderRadius: "5px",
                cursor: "pointer",
                fontWeight: "bold",
              }}
            >
              Leave
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default GuestRoom;
