import React, { useEffect, useRef, useState } from "react";
import io from "socket.io-client";
import Peer from "simple-peer";
import "./Room.css";

const socket = io("https://192.168.29.23:5000", { secure: true });

const Room = () => {
  const [peers, setPeers] = useState([]);
  const [userStream, setUserStream] = useState();
  const [userName, setUserName] = useState("");
  const [roomId, setRoomId] = useState("");
  const [participants, setParticipants] = useState([]);
  const [joined, setJoined] = useState(false);
  const [videoEnabled, setVideoEnabled] = useState(true);
  const [audioEnabled, setAudioEnabled] = useState(true);

  const userVideo = useRef();
  const peersRef = useRef([]);

  useEffect(() => {
    const storedRoom = localStorage.getItem("roomId");
    const storedName = localStorage.getItem("userName");
    if (storedRoom && storedName) {
      setRoomId(storedRoom);
      setUserName(storedName);
      handleJoin(storedRoom, storedName);
    }
  }, []);

  const handleJoin = async (room, name) => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ video: true, audio: true });
      setUserStream(stream);
      if (userVideo.current) userVideo.current.srcObject = stream;

      socket.emit("join-room", { roomId: room, userName: name });
      setJoined(true);
    } catch (err) {
      alert("Camera or mic access error");
    }
  };

  useEffect(() => {
    socket.on("all-users", (users) => {
      const peers = [];
      users.forEach((user) => {
        if (user.id === socket.id) return;
        const peer = createPeer(user.id, socket.id, userStream);
        peersRef.current.push({
          peerID: user.id,
          peer,
          userName: user.name,
        });
        peers.push({ peerID: user.id, peer, userName: user.name });
      });
      setPeers(peers);
    });

    socket.on("user-joined", ({ id, name }) => {
      const peer = addPeer(id, socket.id, userStream);
      peersRef.current.push({ peerID: id, peer, userName: name });
      setPeers((prev) => [...prev, { peerID: id, peer, userName: name }]);
    });

    socket.on("receiving-returned-signal", ({ id, signal }) => {
      const item = peersRef.current.find((p) => p.peerID === id);
      item?.peer.signal(signal);
    });

    socket.on("receiving-signal", ({ from, signal, name }) => {
      const peer = addPeer(from, socket.id, userStream, false);
      peer.signal(signal);
      peersRef.current.push({ peerID: from, peer, userName: name });
      setPeers((prev) => [...prev, { peerID: from, peer, userName: name }]);
    });

    socket.on("user-disconnected", (id) => {
      const peerObj = peersRef.current.find((p) => p.peerID === id);
      if (peerObj) peerObj.peer.destroy();
      peersRef.current = peersRef.current.filter((p) => p.peerID !== id);
      setPeers((prev) => prev.filter((p) => p.peerID !== id));
    });

    socket.on("all-users", (list) => setParticipants(list));

    return () => {
      socket.disconnect();
    };
  }, [userStream]);

  const createPeer = (userToSignal, callerID, stream) => {
    const peer = new Peer({
      initiator: true,
      trickle: false,
      stream,
    });

    peer.on("signal", (signal) => {
      socket.emit("sending-signal", {
        userToSignal,
        callerID,
        signal,
      });
    });

    return peer;
  };

  const addPeer = (incomingID, callerID, stream) => {
    const peer = new Peer({
      initiator: false,
      trickle: false,
      stream,
    });

    peer.on("signal", (signal) => {
      socket.emit("returning-signal", { signal, to: incomingID });
    });

    return peer;
  };

  const toggleAudio = () => {
    userStream.getAudioTracks()[0].enabled = !audioEnabled;
    setAudioEnabled(!audioEnabled);
  };

  const toggleVideo = () => {
    userStream.getVideoTracks()[0].enabled = !videoEnabled;
    setVideoEnabled(!videoEnabled);
  };

  const handleEndMeeting = () => {
    peersRef.current.forEach((p) => p.peer.destroy());
    socket.disconnect();
    window.location.href = "/";
  };

  return (
    <div className="room-container">
      <header className="room-header">
        <h1 className="logo">Ve Meet</h1>
        <div className="controls">
          <button onClick={toggleAudio}>{audioEnabled ? "Mute" : "Unmute"}</button>
          <button onClick={toggleVideo}>{videoEnabled ? "Video Off" : "Video On"}</button>
          <button className="end" onClick={handleEndMeeting}>End Meeting</button>
        </div>
      </header>

      <div className="video-grid">
        <div className="video-block">
          <video ref={userVideo} autoPlay muted playsInline className="video" />
          <p className="name">{userName} (You)</p>
        </div>
        {peers.map((peer) => (
          <Video key={peer.peerID} peer={peer.peer} name={peer.userName} />
        ))}
      </div>

      <div className="participant-list">
        <h3>Participants ({participants.length})</h3>
        <ul>
          {participants.map((p) => (
            <li key={p.id}>{p.name}</li>
          ))}
        </ul>
      </div>
    </div>
  );
};

const Video = ({ peer, name }) => {
  const ref = useRef();

  useEffect(() => {
    peer.on("stream", (stream) => {
      if (ref.current) ref.current.srcObject = stream;
    });
  }, [peer]);

  return (
    <div className="video-block">
      <video playsInline autoPlay ref={ref} className="video" />
      <p className="name">{name}</p>
    </div>
  );
};

export default Room;
