import React, { useEffect, useRef, useState } from 'react';
import io from 'socket.io-client';
import Peer from 'simple-peer';
import './GuestRoom.css';

const socket = io('https://localhost:5000', { secure: true });

const GuestRoom = () => {
  const [roomId, setRoomId] = useState('');
  const [name, setName] = useState('');
  const [joined, setJoined] = useState(false);
  const [participants, setParticipants] = useState([]);
  const [peers, setPeers] = useState({});
  const [devices, setDevices] = useState([]);
  const [selectedDeviceId, setSelectedDeviceId] = useState('');
  const localVideoRef = useRef(null);
  const streamRef = useRef(null);
  const peersRef = useRef({});

  // Get video input devices
  useEffect(() => {
    navigator.mediaDevices.enumerateDevices().then(devices => {
      const videoDevices = devices.filter(device => device.kind === 'videoinput');
      setDevices(videoDevices);
      if (videoDevices[0]) setSelectedDeviceId(videoDevices[0].deviceId);
    });
  }, []);

  // Handle join
  const handleJoin = async () => {
    try {
      const mediaStream = await navigator.mediaDevices.getUserMedia({
        video: { deviceId: selectedDeviceId ? { exact: selectedDeviceId } : undefined },
        audio: true,
      });

      streamRef.current = mediaStream;
      if (localVideoRef.current) {
        localVideoRef.current.srcObject = mediaStream;
      }

      socket.emit('join-room', { roomId, name });
      setJoined(true);
    } catch (err) {
      console.error('Error accessing media devices.', err);
    }
  };

  // Socket listeners
  useEffect(() => {
    if (!joined) return;

    socket.on('user-list', userList => {
      setParticipants(userList);
    });

    socket.on('user-joined', ({ id, name: remoteName }) => {
      const peer = createPeer(id, socket.id, streamRef.current);
      peersRef.current[id] = { peer, name: remoteName };
      setPeers(prev => ({ ...prev, [id]: { peer, name: remoteName } }));
    });

    socket.on('offer', ({ from, offer }) => {
      const peer = addPeer(offer, from, streamRef.current);
      peersRef.current[from] = { peer, name: 'Unknown' };
      setPeers(prev => ({ ...prev, [from]: { peer, name: 'Unknown' } }));
    });

    socket.on('answer', ({ from, answer }) => {
      peersRef.current[from]?.peer.signal(answer);
    });

    socket.on('candidate', ({ from, candidate }) => {
      peersRef.current[from]?.peer.signal(candidate);
    });

    socket.on('user-left', ({ id }) => {
      peersRef.current[id]?.peer.destroy();
      delete peersRef.current[id];
      setPeers(prev => {
        const newPeers = { ...prev };
        delete newPeers[id];
        return newPeers;
      });
    });

    return () => {
      socket.off('user-list');
      socket.off('user-joined');
      socket.off('offer');
      socket.off('answer');
      socket.off('candidate');
      socket.off('user-left');
    };
  }, [joined]);

  // Peer creation
  const createPeer = (userToSignal, callerId, stream) => {
    const peer = new Peer({ initiator: true, trickle: false, stream });
    peer.on('signal', signal => {
      socket.emit('offer', { to: userToSignal, offer: signal });
    });
    return peer;
  };

  const addPeer = (incomingSignal, from, stream) => {
    const peer = new Peer({ initiator: false, trickle: false, stream });
    peer.on('signal', signal => {
      socket.emit('answer', { to: from, answer: signal });
    });
    peer.signal(incomingSignal);
    return peer;
  };

  return (
    <div className="guest-room">
      {!joined ? (
        <div className="join-form">
          <h2>Join Ve Meet</h2>
          <input
            type="text"
            placeholder="Your Name"
            value={name}
            onChange={e => setName(e.target.value)}
          />
          <input
            type="text"
            placeholder="Room Code"
            value={roomId}
            onChange={e => setRoomId(e.target.value)}
          />
          <select
            value={selectedDeviceId}
            onChange={e => setSelectedDeviceId(e.target.value)}
          >
            {devices.map(device => (
              <option key={device.deviceId} value={device.deviceId}>
                {device.label || `Camera ${device.deviceId}`}
              </option>
            ))}
          </select>
          <button onClick={handleJoin}>Join</button>
        </div>
      ) : (
        <div className="video-grid">
          <div className="video-box">
            <video ref={localVideoRef} autoPlay muted playsInline />
            <div className="name-tag">{name} (You)</div>
          </div>

          {Object.entries(peers).map(([id, { peer, name }]) => (
            <Video key={id} peer={peer} name={name} />
          ))}
        </div>
      )}

      {joined && (
        <div className="participant-list">
          <h4>Participants:</h4>
          <ul>
            {participants.map((p, i) => (
              <li key={i}>{p}</li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

const Video = ({ peer, name }) => {
  const ref = useRef();

  useEffect(() => {
    peer.on('stream', stream => {
      if (ref.current) {
        ref.current.srcObject = stream;
      }
    });
  }, [peer]);

  return (
    <div className="video-box">
      <video ref={ref} autoPlay playsInline />
      <div className="name-tag">{name}</div>
    </div>
  );
};

export default GuestRoom;
