import React, { useEffect, useRef, useState } from "react";
import io from "socket.io-client";
import Peer from "simple-peer";
import "./Room.css";

const socket = io("https://192.168.29.23:5000", {
  secure: true,
  reconnection: true,
  rejectUnauthorized: false,
});

const GuestRoom = () => {
  const [peers, setPeers] = useState([]);
  const [stream, setStream] = useState();
  const [name, setName] = useState("");
  const [roomID, setRoomID] = useState("");
  const [joined, setJoined] = useState(false);
  const [myID, setMyID] = useState("");
  const [userNameMap, setUserNameMap] = useState({});
  const [micOn, setMicOn] = useState(true);
  const [videoOn, setVideoOn] = useState(true);
  const [screenSharerID, setScreenSharerID] = useState(null);

  const videoRefs = useRef({});
  const peersRef = useRef([]);

  useEffect(() => {
    socket.on("your-id", (id) => {
      setMyID(id);
    });

    socket.on("all-users", (users) => {
      const peers = [];
      users.forEach(({ userID, userName }) => {
        const peer = createPeer(userID, socket.id, stream);
        peersRef.current.push({
          peerID: userID,
          peer,
        });
        peers.push({ peerID: userID, peer });
        setUserNameMap((prev) => ({ ...prev, [userID]: userName }));
      });
      setPeers(peers);
    });

    socket.on("user-joined", ({ signal, callerID, userName }) => {
      const peer = addPeer(signal, callerID, stream);
      peersRef.current.push({
        peerID: callerID,
        peer,
      });
      setPeers((users) => [...users, { peerID: callerID, peer }]);
      setUserNameMap((prev) => ({ ...prev, [callerID]: userName }));
    });

    socket.on("receiving-returned-signal", ({ id, signal }) => {
      const item = peersRef.current.find((p) => p.peerID === id);
      item?.peer.signal(signal);
    });

    socket.on("user-left", (id) => {
      const peerObj = peersRef.current.find((p) => p.peerID === id);
      if (peerObj) {
        peerObj.peer.destroy();
      }
      peersRef.current = peersRef.current.filter((p) => p.peerID !== id);
      setPeers((users) => users.filter((p) => p.peerID !== id));
      setUserNameMap((prev) => {
        const updated = { ...prev };
        delete updated[id];
        return updated;
      });
    });

    socket.on("screen-share-started", (userID) => {
      setScreenSharerID(userID);
    });

    socket.on("screen-share-stopped", () => {
      setScreenSharerID(null);
    });

    return () => {
      socket.disconnect();
    };
  }, [stream]);

  useEffect(() => {
    if (screenSharerID && videoRefs.current[screenSharerID]) {
      const videoElem = videoRefs.current[screenSharerID];
      setTimeout(() => {
        if (
          videoElem &&
          videoElem.requestFullscreen &&
          document.fullscreenElement !== videoElem
        ) {
          videoElem.requestFullscreen().catch((err) => {
            console.warn("Fullscreen request failed:", err);
          });
        }
      }, 1000);
    }
  }, [screenSharerID]);

  function createPeer(userToSignal, callerID, stream) {
    const peer = new Peer({
      initiator: true,
      trickle: false,
      stream,
    });

    peer.on("signal", (signal) => {
      socket.emit("sending-signal", {
        userToSignal,
        callerID,
        signal,
        userName: name,
      });
    });

    return peer;
  }

  function addPeer(incomingSignal, callerID, stream) {
    const peer = new Peer({
      initiator: false,
      trickle: false,
      stream,
    });

    peer.on("signal", (signal) => {
      socket.emit("returning-signal", { signal, callerID });
    });

    peer.signal(incomingSignal);
    return peer;
  }

  const handleJoin = async () => {
    try {
      const localStream = await navigator.mediaDevices.getUserMedia({
        video: true,
        audio: true,
      });
      setStream(localStream);
      setJoined(true);
      socket.emit("join-room", { roomID, userName: name });
    } catch (err) {
      alert("Could not access camera or mic");
    }
  };

  const toggleMic = () => {
    stream.getAudioTracks()[0].enabled = !micOn;
    setMicOn(!micOn);
  };

  const toggleVideo = () => {
    stream.getVideoTracks()[0].enabled = !videoOn;
    setVideoOn(!videoOn);
  };

  const leaveMeeting = () => {
    window.location.reload();
  };

  return (
    <div className="room-container">
      <div className="header">
        <h1 className="logo">Ve <span style={{ color: "gold" }}>Meet</span></h1>
        <p className="room-info">Room Code: {roomID}</p>
        <p className="room-info">Your Name: {name}</p>
      </div>

      {!joined ? (
        <div className="join-container">
          <input
            value={name}
            onChange={(e) => setName(e.target.value)}
            placeholder="Enter your name"
            className="input-field"
          />
          <input
            value={roomID}
            onChange={(e) => setRoomID(e.target.value)}
            placeholder="Enter Room Code"
            className="input-field"
          />
          <button onClick={handleJoin} className="join-button">
            Join Meeting
          </button>
        </div>
      ) : (
        <>
          <div className="video-grid">
            {stream && (
              <div className="video-block">
                <video
                  muted
                  ref={(ref) => {
                    if (ref) {
                      ref.srcObject = stream;
                      ref.play();
                      videoRefs.current[myID] = ref;
                    }
                  }}
                  className="video-element"
                />
                <p className="name-label">{name} (You)</p>
              </div>
            )}
            {peers.map(({ peerID, peer }) => (
              <div className="video-block" key={peerID}>
                <video
                  ref={(ref) => {
                    if (ref && !videoRefs.current[peerID]) {
                      peer.on("stream", (stream) => {
                        ref.srcObject = stream;
                        ref.play();
                      });
                      videoRefs.current[peerID] = ref;
                    }
                  }}
                  className="video-element"
                />
                <p className="name-label">{userNameMap[peerID]}</p>
              </div>
            ))}
          </div>
          <div className="controls">
            <button onClick={toggleMic}>
              {micOn ? "Mute" : "Unmute"}
            </button>
            <button onClick={toggleVideo}>
              {videoOn ? "Video Off" : "Video On"}
            </button>
            <button onClick={leaveMeeting}>Leave</button>
          </div>
        </>
      )}
    </div>
  );
};

export default GuestRoom;
