import React, { useEffect, useRef, useState } from "react";
import io from "socket.io-client";
import Peer from "simple-peer";
import './Room.css';

const socket = io.connect("https://192.168.29.23:5000");

const GuestRoom = () => {
  const [stream, setStream] = useState();
  const [peers, setPeers] = useState([]);
  const [name, setName] = useState("");
  const [roomID, setRoomID] = useState("");
  const [joined, setJoined] = useState(false);
  const [micOn, setMicOn] = useState(true);
  const [camOn, setCamOn] = useState(true);
  const userVideo = useRef();
  const peersRef = useRef([]);

  useEffect(() => {
    if (userVideo.current && stream) {
      userVideo.current.srcObject = stream;
    }
  }, [stream]);

  function createPeer(userToSignal, callerID, stream, callerName) {
    const peer = new Peer({ initiator: true, trickle: false, stream });

    peer.on("signal", signal => {
      socket.emit("sending signal", { userToSignal, callerID, signal, callerName });
    });

    return peer;
  }

  function addPeer(incomingSignal, callerID, stream) {
    const peer = new Peer({ initiator: false, trickle: false, stream });

    peer.on("signal", signal => {
      socket.emit("returning signal", { signal, callerID });
    });

    peer.signal(incomingSignal);
    return peer;
  }

  const handleJoin = async () => {
    try {
      const userMedia = await navigator.mediaDevices.getUserMedia({ video: true, audio: true });
      setStream(userMedia);
      setJoined(true);
      socket.emit("join room", { roomID, name });

      socket.on("all users", users => {
        const peers = [];
        users.forEach(user => {
          const peer = createPeer(user.id, socket.id, userMedia, name);
          peersRef.current.push({ peerID: user.id, peer });
          peers.push({ peerID: user.id, peer, name: user.name });
        });
        setPeers(peers);
      });

      socket.on("user joined", payload => {
        const peer = addPeer(payload.signal, payload.callerID, userMedia);
        peersRef.current.push({ peerID: payload.callerID, peer });
        setPeers(users => [...users, { peerID: payload.callerID, peer, name: payload.callerName }]);
      });

      socket.on("receiving returned signal", payload => {
        const item = peersRef.current.find(p => p.peerID === payload.id);
        item.peer.signal(payload.signal);
      });
    } catch (err) {
      console.error("Error accessing media devices.", err);
    }
  };

  const toggleMic = () => {
    stream.getAudioTracks()[0].enabled = !micOn;
    setMicOn(!micOn);
  };

  const toggleCam = () => {
    stream.getVideoTracks()[0].enabled = !camOn;
    setCamOn(!camOn);
  };

  const leaveRoom = () => {
    window.close();
  };

  return (
    <div className="room-container">
      {!joined ? (
        <div className="join-container">
          <h2 className="logo">Ve Meet</h2>
          <input placeholder="Enter Your Name" onChange={(e) => setName(e.target.value)} />
          <input placeholder="Enter Room Code" onChange={(e) => setRoomID(e.target.value)} />
          <button onClick={handleJoin}>Join</button>
        </div>
      ) : (
        <div className="room-panel">
          <div className="video-grid">
            <div className="video-block">
              <video playsInline muted ref={userVideo} autoPlay className="video" />
              <p>{name} (You)</p>
            </div>
            {peers.map((peerObj, index) => (
              <Video key={peerObj.peerID} peer={peerObj.peer} name={peerObj.name} />
            ))}
          </div>
          <div className="controls">
            <button onClick={toggleMic}>{micOn ? "Mute" : "Unmute"}</button>
            <button onClick={toggleCam}>{camOn ? "Video Off" : "Video On"}</button>
            <button onClick={leaveRoom}>Leave</button>
          </div>
        </div>
      )}
    </div>
  );
};

const Video = ({ peer, name }) => {
  const ref = useRef();
  useEffect(() => {
    peer.on("stream", stream => {
      ref.current.srcObject = stream;
    });
  }, [peer]);

  return (
    <div className="video-block">
      <video playsInline autoPlay ref={ref} className="video" />
      <p>{name}</p>
    </div>
  );
};

export default GuestRoom;
