// /src/components/Home.js
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

export default function Home() {
  const [roomId, setRoomId] = useState('');
  const [name, setName] = useState('');
  const navigate = useNavigate();

  const createRoom = () => {
    const id = Math.random().toString(36).substring(2, 8); // 6-letter room ID
    if (name.trim()) {
      navigate(`/room/${id}`, { state: { name, isHost: true } });
    } else {
      alert('Please enter your name.');
    }
  };

  const joinRoom = () => {
    if (roomId.trim() && name.trim()) {
      navigate(`/room/${roomId}`, { state: { name, isHost: false } });
    } else {
      alert('Please enter both name and room ID.');
    }
  };

  return (
    <div style={{ textAlign: 'center', marginTop: '50px' }}>
      {/* LOGO from public folder */}
      <img src="/LOGO.png" alt="Ve Meet Logo" style={{ width: 200, marginBottom: 20 }} />

      <h1>Welcome to Ve Meet</h1>

      <input
        type="text"
        placeholder="Enter your name"
        value={name}
        onChange={(e) => setName(e.target.value)}
        style={{ padding: 10, margin: 10 }}
      />
      <br />

      <button onClick={createRoom} style={{ padding: 10, margin: 10 }}>
        Create Room
      </button>

      <br /><br />

      <input
        type="text"
        placeholder="Enter Room ID to join"
        value={roomId}
        onChange={(e) => setRoomId(e.target.value)}
        style={{ padding: 10, margin: 10 }}
      />
      <br />

      <button onClick={joinRoom} style={{ padding: 10, margin: 10 }}>
        Join Room
      </button>
    </div>
  );
}
