// src/components/GuestRoom.js
import React, { useState, useEffect, useRef } from 'react';
import io from 'socket.io-client';
import Peer from 'simple-peer';
import './GuestRoom.css'; // You’ll create a CSS file for styling

const SERVER_URL = 'https://localhost:5000'; // Your backend URL with https

const socket = io(SERVER_URL, {
  transports: ['websocket'],
});

export default function GuestRoom({ roomId, userName }) {
  const [peers, setPeers] = useState([]);
  const [myStream, setMyStream] = useState(null);
  const [audioEnabled, setAudioEnabled] = useState(true);
  const [videoEnabled, setVideoEnabled] = useState(true);
  const [cameras, setCameras] = useState([]);
  const [microphones, setMicrophones] = useState([]);
  const [selectedCamera, setSelectedCamera] = useState('');
  const [selectedMic, setSelectedMic] = useState('');
  const [participants, setParticipants] = useState([]); // [{id, name}]
  const myVideoRef = useRef();
  const peersRef = useRef([]);

  // Get devices and initial media stream
  useEffect(() => {
    async function getMedia(devices) {
      try {
        const constraints = {
          video: selectedCamera ? { deviceId: { exact: selectedCamera } } : true,
          audio: selectedMic ? { deviceId: { exact: selectedMic } } : true,
        };
        const stream = await navigator.mediaDevices.getUserMedia(constraints);
        setMyStream(stream);
        if (myVideoRef.current) {
          myVideoRef.current.srcObject = stream;
        }
        if (!devices) {
          await getDevices();
        }
      } catch (err) {
        alert('Error accessing camera/mic: ' + err.message);
      }
    }

    async function getDevices() {
      const devices = await navigator.mediaDevices.enumerateDevices();
      const videoDevices = devices.filter(d => d.kind === 'videoinput');
      const audioDevices = devices.filter(d => d.kind === 'audioinput');
      setCameras(videoDevices);
      setMicrophones(audioDevices);
      if (!selectedCamera && videoDevices[0]) setSelectedCamera(videoDevices[0].deviceId);
      if (!selectedMic && audioDevices[0]) setSelectedMic(audioDevices[0].deviceId);
    }

    getMedia();
  }, [selectedCamera, selectedMic]);

  useEffect(() => {
    socket.emit('join-room', { roomId, name: userName });

    socket.on('user-list', users => {
      setParticipants(users);
    });

    socket.on('user-joined', ({ id, name }) => {
      console.log('User joined:', name);
    });

    socket.on('user-left', ({ id }) => {
      // Remove peer from peersRef and peers state
      const peerObj = peersRef.current.find(p => p.peerID === id);
      if (peerObj) {
        peerObj.peer.destroy();
        peersRef.current = peersRef.current.filter(p => p.peerID !== id);
        setPeers(peersRef.current.map(p => p.peer));
      }
    });

    // Handle incoming signals for WebRTC
    socket.on('offer', handleReceiveOffer);
    socket.on('answer', handleReceiveAnswer);
    socket.on('candidate', handleReceiveCandidate);

    return () => {
      socket.disconnect();
      myStream && myStream.getTracks().forEach(track => track.stop());
    };
  }, [myStream]);

  // WebRTC functions
  function createPeer(userToSignal, callerId, stream) {
    const peer = new Peer({
      initiator: true,
      trickle: false,
      stream,
    });

    peer.on('signal', signal => {
      socket.emit('offer', { to: userToSignal, offer: signal });
    });

    return peer;
  }

  function addPeer(incomingSignal, callerId, stream) {
    const peer = new Peer({
      initiator: false,
      trickle: false,
      stream,
    });

    peer.on('signal', signal => {
      socket.emit('answer', { to: callerId, answer: signal });
    });

    peer.signal(incomingSignal);

    return peer;
  }

  function handleReceiveOffer({ from, offer }) {
    const peer = addPeer(offer, from, myStream);
    peersRef.current.push({ peerID: from, peer });
    setPeers([...peersRef.current.map(p => p.peer)]);
  }

  function handleReceiveAnswer({ from, answer }) {
    const peerObj = peersRef.current.find(p => p.peerID === from);
    if (peerObj) peerObj.peer.signal(answer);
  }

  function handleReceiveCandidate({ from, candidate }) {
    // simple-peer trickle=false disables candidate event
    // if you want, can implement trickle true for real candidate handling
  }

  // Toggle audio/video
  function toggleAudio() {
    if (!myStream) return;
    myStream.getAudioTracks().forEach(track => (track.enabled = !audioEnabled));
    setAudioEnabled(!audioEnabled);
  }
  function toggleVideo() {
    if (!myStream) return;
    myStream.getVideoTracks().forEach(track => (track.enabled = !videoEnabled));
    setVideoEnabled(!videoEnabled);
  }

  function leaveRoom() {
    peersRef.current.forEach(p => p.peer.destroy());
    peersRef.current = [];
    socket.emit('leave-room', { roomId });
    window.location.href = '/'; // or redirect to home page
  }

  return (
    <div className="guestroom-container">
      <header className="header">
        <h1 className="logo">Ve Meet</h1>
      </header>

      <div className="controls">
        <label>
          Camera:
          <select
            value={selectedCamera}
            onChange={e => setSelectedCamera(e.target.value)}
          >
            {cameras.map(cam => (
              <option key={cam.deviceId} value={cam.deviceId}>
                {cam.label || 'Camera'}
              </option>
            ))}
          </select>
        </label>

        <label>
          Microphone:
          <select
            value={selectedMic}
            onChange={e => setSelectedMic(e.target.value)}
          >
            {microphones.map(mic => (
              <option key={mic.deviceId} value={mic.deviceId}>
                {mic.label || 'Microphone'}
              </option>
            ))}
          </select>
        </label>

        <button onClick={toggleAudio}>
          {audioEnabled ? 'Mute Audio' : 'Unmute Audio'}
        </button>
        <button onClick={toggleVideo}>
          {videoEnabled ? 'Stop Video' : 'Start Video'}
        </button>
        <button className="leave-btn" onClick={leaveRoom}>
          Leave Meeting
        </button>
      </div>

      <div className="videos-grid">
        <div className="video-container">
          <video
            ref={myVideoRef}
            muted
            autoPlay
            playsInline
            className="video"
          />
          <div className="video-label">{userName} (You)</div>
        </div>

        {peersRef.current.map(({ peerID, peer }, index) => (
          <Video key={peerID} peer={peer} />
        ))}
      </div>

      <div className="participant-list">
        <h3>Participants ({participants.length}):</h3>
        <ul>
          {participants.map((p, i) => (
            <li key={i}>{p}</li>
          ))}
        </ul>
      </div>
    </div>
  );
}

function Video({ peer }) {
  const ref = useRef();

  useEffect(() => {
    peer.on('stream', stream => {
      if (ref.current) {
        ref.current.srcObject = stream;
      }
    });
  }, [peer]);

  return <video ref={ref} autoPlay playsInline className="video" />;
}
