import React, { useEffect, useRef, useState } from "react";
import io from "socket.io-client";
import Peer from "simple-peer";
import './Room.css';

const socket = io('https://192.168.29.23:5000', {
  secure: true,
  reconnection: true,
  rejectUnauthorized: false
});

const GuestRoom = () => {
  const [stream, setStream] = useState(null);
  const [peers, setPeers] = useState([]);
  const [name, setName] = useState("");
  const [roomID, setRoomID] = useState("");
  const [joined, setJoined] = useState(false);
  const [micOn, setMicOn] = useState(true);
  const [camOn, setCamOn] = useState(true);
  const userVideo = useRef(null);
  const peersRef = useRef([]);

  useEffect(() => {
    if (userVideo.current && stream) {
      userVideo.current.srcObject = stream;
    }
  }, [stream]);

  const handleJoin = async () => {
    if (!name || !roomID) {
      alert("Please enter your name and room code");
      return;
    }

    try {
      const userMedia = await navigator.mediaDevices.getUserMedia({ video: true, audio: true });
      setStream(userMedia);

      // Set video to local preview
      if (userVideo.current) {
        userVideo.current.srcObject = userMedia;
      }

      setJoined(true);
      socket.emit("join-room", { roomID, userName: name });

      socket.on("all-users", users => {
        const newPeers = [];
        users.forEach(user => {
          const peer = createPeer(user.socketID, socket.id, userMedia, name);
          peersRef.current.push({ peerID: user.socketID, peer });
          newPeers.push({ peerID: user.socketID, peer, userName: user.userName });
        });
        setPeers(newPeers);
      });

      socket.on("user-joined", payload => {
        const peer = addPeer(payload.signal, payload.callerID, userMedia, payload.userName);
        peersRef.current.push({ peerID: payload.callerID, peer });
        setPeers(prev => [...prev, { peerID: payload.callerID, peer, userName: payload.userName }]);
      });

      socket.on("receiving-returned-signal", payload => {
        const item = peersRef.current.find(p => p.peerID === payload.id);
        if (item) item.peer.signal(payload.signal);
      });

      socket.on("receiving-signal", ({ signal, from, name }) => {
        const trySignal = (attempts = 10) => {
         const foundPeer = peersRef.current.find(p => p.peerID === from);
         if (foundPeer?.peer) {
           foundPeer.peer.signal(signal);
         } else if (attempts > 0) {
           setTimeout(() => trySignal(attempts - 1), 200); // retry after 200ms
         } else {
           console.warn("⚠️ Peer not ready after retries for ID:", from);
         }
       };
       trySignal();
     });

      socket.on("user-disconnected", id => {
        setPeers(prev => prev.filter(p => p.peerID !== id));
        peersRef.current = peersRef.current.filter(p => p.peerID !== id);
      });
    } catch (err) {
      console.error("Error accessing media devices.", err);
      alert("Could not access camera and microphone.");
    }
  };

  const createPeer = (userToSignal, callerID, stream, userName) => {
    const peer = new Peer({
      initiator: true,
      trickle: false,
      stream,
    });

    peer.on("signal", signal => {
      socket.emit("sending-signal", { userToSignal, callerID, signal, userName });
    });

    return peer;
  };

  const addPeer = (incomingSignal, callerID, stream, userName) => {
    const peer = new Peer({
      initiator: false,
      trickle: false,
      stream,
    });

    peer.on("signal", signal => {
      socket.emit("returning-signal", { signal, callerID });
    });

    peer.signal(incomingSignal);

    return peer;
  };

  const toggleMic = () => {
    if (stream) {
      stream.getAudioTracks().forEach(track => {
        track.enabled = !micOn;
      });
      setMicOn(!micOn);
    }
  };

  const toggleCam = () => {
    if (stream) {
      stream.getVideoTracks().forEach(track => {
        track.enabled = !camOn;
      });
      setCamOn(!camOn);
    }
  };

  const leaveRoom = () => {
    peersRef.current.forEach(({ peer }) => peer.destroy());
    if (stream) {
      stream.getTracks().forEach(track => track.stop());
    }
    socket.disconnect();
    window.location.reload();
  };

  return (
    <div className="room-container">
      {!joined ? (
        <div className="join-container">
          <h2 className="logo">Ve Meet</h2>
          <input placeholder="Enter Your Name" onChange={(e) => setName(e.target.value)} />
          <input placeholder="Enter Room Code" onChange={(e) => setRoomID(e.target.value)} />
          <button onClick={handleJoin}>Join</button>
        </div>
      ) : (
        <div className="room-panel">
          <div className="video-grid">
            {stream && (
              <div className="video-block">
                <video
                  playsInline
                  muted
                  autoPlay
                  ref={userVideo}
                  className="video"
                />
                <p>{name} (You)</p>
              </div>
            )}
            {peers.map(peerObj => (
              <Video key={peerObj.peerID} peer={peerObj.peer} name={peerObj.userName} />
            ))}
          </div>
          <div className="controls">
            <button onClick={toggleMic}>{micOn ? "Mute" : "Unmute"}</button>
            <button onClick={toggleCam}>{camOn ? "Video Off" : "Video On"}</button>
            <button onClick={leaveRoom}>Leave</button>
          </div>
        </div>
      )}
    </div>
  );
};

const Video = ({ peer, name }) => {
  const ref = useRef();

  useEffect(() => {
    peer.on("stream", stream => {
      if (ref.current) {
        ref.current.srcObject = stream;
      }
    });
  }, [peer]);

  return (
    <div className="video-block">
      <video playsInline autoPlay ref={ref} className="video" />
      <p>{name}</p>
    </div>
  );
};

export default GuestRoom;
