// src/components/GuestRoom.js
import React, { useEffect, useRef, useState } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import socket from '../socket';
import Peer from 'simple-peer';

const GuestRoom = () => {
  const { roomId } = useParams();
  const [userName, setUserName] = useState('');
  const [joined, setJoined] = useState(false);
  const videoRef = useRef(null);
  const peerRef = useRef(null);
  const navigate = useNavigate();

  useEffect(() => {
    // Ask for camera/mic access
    navigator.mediaDevices.getUserMedia({ video: true, audio: true }).then((stream) => {
      if (videoRef.current) {
        videoRef.current.srcObject = stream;
        videoRef.current.play();
      }

      // Listen for signal from host
      socket.on('user-joined', (hostSignal) => {
        const peer = new Peer({
          initiator: false,
          trickle: false,
          stream,
        });

        peer.on('signal', (signal) => {
          socket.emit('return-signal', { signal, roomId });
        });

        peer.on('stream', (remoteStream) => {
          // optional: show host stream somewhere
        });

        peer.signal(hostSignal);
        peerRef.current = peer;
      });
    });

    // Clean up socket listener
    return () => {
      socket.off('user-joined');
    };
  }, [roomId]);

  const handleJoin = () => {
    if (!userName.trim()) return alert('Please enter your name');

    socket.emit('join-room', { roomId, userName });
    setJoined(true);
  };

  return (
    <div style={styles.container}>
      <div style={styles.card}>
        <h2 style={styles.title}>Ve Meet - Join as Guest</h2>

        {!joined && (
          <>
            <input
              type="text"
              placeholder="Enter your name"
              value={userName}
              onChange={(e) => setUserName(e.target.value)}
              style={styles.input}
            />
            <button onClick={handleJoin} style={styles.button}>
              Join Room
            </button>
          </>
        )}

        <video ref={videoRef} style={styles.video} muted playsInline></video>

        {joined && <p style={styles.success}>Connected! Please wait for the host...</p>}
      </div>
    </div>
  );
};

const styles = {
  container: {
    backgroundColor: '#000',
    height: '100vh',
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
  },
  card: {
    backgroundColor: '#111',
    padding: '30px',
    borderRadius: '15px',
    boxShadow: '0 0 20px gold',
    color: '#fff',
    width: '100%',
    maxWidth: '400px',
    textAlign: 'center',
  },
  title: {
    marginBottom: '20px',
    color: 'gold',
  },
  input: {
    padding: '10px',
    marginBottom: '15px',
    borderRadius: '8px',
    border: 'none',
    width: '100%',
    fontSize: '16px',
  },
  button: {
    padding: '10px 20px',
    backgroundColor: 'gold',
    color: '#000',
    border: 'none',
    borderRadius: '8px',
    fontWeight: 'bold',
    cursor: 'pointer',
    fontSize: '16px',
  },
  video: {
    marginTop: '20px',
    width: '100%',
    borderRadius: '10px',
  },
  success: {
    marginTop: '15px',
    color: '#0f0',
  },
};

export default GuestRoom;
