import React, { useEffect, useRef, useState } from "react";
import io from "socket.io-client";
import Peer from "simple-peer";
import "./Room.css"; // Make sure your black-gold CSS theme is loaded

const socket = io.connect("https://192.168.29.23:5000", {
  transports: ["websocket"],
});

const GuestRoom = () => {
  const [peers, setPeers] = useState([]);
  const [stream, setStream] = useState();
  const [userName, setUserName] = useState("");
  const [roomID, setRoomID] = useState("");
  const [joined, setJoined] = useState(false);
  const userVideo = useRef();
  const peersRef = useRef([]);

  useEffect(() => {
    socket.on("all-users", (users) => {
      const peers = [];
      users.forEach(({ socketID, name }) => {
        const peer = createPeer(socketID, socket.id, stream);
        peersRef.current.push({
          peerID: socketID,
          peer,
          name,
        });
        peers.push({ peerID: socketID, peer, name });
      });
      setPeers(peers);
    });

    socket.on("user-joined", ({ signal, callerID, name }) => {
      const peer = addPeer(signal, callerID, stream);
      peersRef.current.push({
        peerID: callerID,
        peer,
        name,
      });
      setPeers((users) => [...users, { peerID: callerID, peer, name }]);
    });

    socket.on("receiving-returned-signal", ({ id, signal }) => {
      const item = peersRef.current.find((p) => p.peerID === id);
      if (item) {
        item.peer.signal(signal);
      }
    });

    socket.on("user-left", (id) => {
      const peerObj = peersRef.current.find((p) => p.peerID === id);
      if (peerObj) {
        peerObj.peer.destroy();
      }
      const peers = peersRef.current.filter((p) => p.peerID !== id);
      peersRef.current = peers;
      setPeers(peers);
    });
  }, [stream]);

  function createPeer(userToSignal, callerID, stream) {
    const peer = new Peer({
      initiator: true,
      trickle: false,
      stream,
    });

    peer.on("signal", (signal) => {
      socket.emit("sending-signal", {
        userToSignal,
        callerID,
        signal,
        name: userName,
      });
    });

    return peer;
  }

  function addPeer(incomingSignal, callerID, stream) {
    const peer = new Peer({
      initiator: false,
      trickle: false,
      stream,
    });

    peer.on("signal", (signal) => {
      socket.emit("returning-signal", { signal, callerID });
    });

    peer.signal(incomingSignal);

    return peer;
  }

  const handleJoin = async () => {
    if (!userName || !roomID) {
      alert("Enter Name and Room ID");
      return;
    }
    try {
      const localStream = await navigator.mediaDevices.getUserMedia({
        video: true,
        audio: true,
      });
      setStream(localStream);
      if (userVideo.current) {
        userVideo.current.srcObject = localStream;
      }
      socket.emit("join-room", { roomID, name: userName });
      setJoined(true);
    } catch (err) {
      console.error("Media error:", err);
      alert("Could not access camera/microphone.");
    }
  };

  const toggleAudio = () => {
    if (stream) {
      stream.getAudioTracks()[0].enabled = !stream.getAudioTracks()[0].enabled;
    }
  };

  const toggleVideo = () => {
    if (stream) {
      stream.getVideoTracks()[0].enabled = !stream.getVideoTracks()[0].enabled;
    }
  };

  const leaveMeeting = () => {
    window.location.reload();
  };

  return (
    <div className="roomContainer">
      <div className="header">
        <h1 className="logo-text">Ve Meet</h1>
        <p>Room ID: {roomID}</p>
      </div>

      {!joined ? (
        <div className="joinContainer">
          <input
            type="text"
            placeholder="Your Name"
            value={userName}
            onChange={(e) => setUserName(e.target.value)}
          />
          <input
            type="text"
            placeholder="Room ID"
            value={roomID}
            onChange={(e) => setRoomID(e.target.value)}
          />
          <button onClick={handleJoin}>Join Room</button>
        </div>
      ) : (
        <div className="videoGrid">
          <div className="videoBlock">
            <video
              muted
              ref={userVideo}
              autoPlay
              playsInline
              className="videoElement"
            />
            <p>{userName} (You)</p>
          </div>

          {peers.map(({ peer, peerID, name }) => (
            <Video key={peerID} peer={peer} name={name} />
          ))}
        </div>
      )}

      {joined && (
        <div className="controls">
          <button onClick={toggleAudio}>Mute/Unmute</button>
          <button onClick={toggleVideo}>Video On/Off</button>
          <button onClick={leaveMeeting}>Leave</button>
        </div>
      )}
    </div>
  );
};

const Video = ({ peer, name }) => {
  const ref = useRef();

  useEffect(() => {
    peer.on("stream", (stream) => {
      if (ref.current) {
        ref.current.srcObject = stream;
      }
    });
  }, [peer]);

  return (
    <div className="videoBlock">
      <video playsInline autoPlay ref={ref} className="videoElement" />
      <p>{name}</p>
    </div>
  );
};

export default GuestRoom;
