// /src/components/Room.js
import React, { useEffect, useRef, useState } from 'react';
import { useParams, useLocation } from 'react-router-dom';
import socket from '../socket';

export default function Room() {
  const { roomId } = useParams();
  const location = useLocation();
  const localVideoRef = useRef();
  const [devices, setDevices] = useState([]);
  const [selectedDeviceId, setSelectedDeviceId] = useState('');
  const userName = location.state?.name || 'Guest';

  const getVideoDevices = async () => {
    const allDevices = await navigator.mediaDevices.enumerateDevices();
    const videoDevices = allDevices.filter((d) => d.kind === 'videoinput');
    setDevices(videoDevices);
    if (videoDevices.length > 0) {
      setSelectedDeviceId(videoDevices[0].deviceId); // Default to first camera
    }
  };

  const startCamera = async (deviceId) => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({
        video: { deviceId: deviceId ? { exact: deviceId } : undefined },
        audio: true,
      });
      if (localVideoRef.current) {
        localVideoRef.current.srcObject = stream;
      }
    } catch (err) {
      console.error('getUserMedia error:', err);
      alert('Could not access camera/mic. Please allow permissions.');
    }
  };

  useEffect(() => {
    getVideoDevices();
  }, []);

  useEffect(() => {
    if (selectedDeviceId) {
      startCamera(selectedDeviceId);
      socket.emit('join-room', { roomId, userName });
    }
  }, [selectedDeviceId]);

  return (
    <div style={{ background: '#111', color: 'white', height: '100vh', padding: 20 }}>
      <h2>Room ID: {roomId}</h2>
      <p>Welcome, {userName}!</p>

      <label>
        ðŸŽ¥ Select Camera:{' '}
        <select
          value={selectedDeviceId}
          onChange={(e) => setSelectedDeviceId(e.target.value)}
          style={{ padding: '5px', marginBottom: '10px' }}
        >
          {devices.map((device) => (
            <option key={device.deviceId} value={device.deviceId}>
              {device.label || `Camera ${device.deviceId.slice(-4)}`}
            </option>
          ))}
        </select>
      </label>

      <video
        ref={localVideoRef}
        autoPlay
        playsInline
        muted
        style={{ width: 400, borderRadius: 12, border: '2px solid #ccc', marginTop: 10 }}
      />
    </div>
  );
}
