import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import logo from '../assets/LOGO.png';

export default function Home() {
  const [roomId, setRoomId] = useState('');
  const navigate = useNavigate();

  const createRoom = () => {
    const id = Math.random().toString(36).substring(2, 8);
    navigate(`/room/${id}`);
  };

  const joinRoom = () => {
    if (roomId.trim()) {
      navigate(`/room/${roomId}`);
    }
  };

  return (
    <div style={{
      backgroundImage: `url('/background.jpg')`,
      backgroundSize: 'cover',
      backgroundPosition: 'center',
      height: '100vh',
      display: 'flex',
      flexDirection: 'column',
      alignItems: 'center',
      justifyContent: 'center',
      gap: '15px',
      padding: '20px'
    }}>
      <img src={logo} alt="Ve Meet Logo" style={{ width: 200, marginBottom: 40 }} />
      <button style={{ padding: '10px 20px', fontSize: 16, cursor: 'pointer' }} onClick={createRoom}>Create Room</button>
      <input
        placeholder="Enter Room ID"
        value={roomId}
        onChange={(e) => setRoomId(e.target.value)}
        style={{ padding: '10px', fontSize: 16, width: 200, borderRadius: 6, border: '1px solid #ccc' }}
      />
      <button style={{ padding: '10px 20px', fontSize: 16, cursor: 'pointer' }} onClick={joinRoom}>Join Room</button>
    </div>
  );
}
