import React, { useEffect, useRef, useState } from "react";
import { io } from "socket.io-client";

const socket = io("https://localhost:5000", {
  secure: true,
  transports: ["websocket"],
});

const Room = () => {
  const [roomId, setRoomId] = useState("");
  const [name, setName] = useState("");
  const [joined, setJoined] = useState(false);
  const [participants, setParticipants] = useState({});
  const [userList, setUserList] = useState([]);
  const localVideoRef = useRef(null);
  const localStreamRef = useRef(null);
  const peerConnections = useRef({});

  useEffect(() => {
    socket.on("user-list", (users) => {
      setUserList(users);
    });

    socket.on("user-joined", async ({ id, name }) => {
      const pc = createPeerConnection(id, name);
      peerConnections.current[id] = { pc, name };

      localStreamRef.current.getTracks().forEach((track) =>
        pc.addTrack(track, localStreamRef.current)
      );

      const offer = await pc.createOffer();
      await pc.setLocalDescription(offer);
      socket.emit("offer", { to: id, offer });
    });

    socket.on("offer", async ({ from, offer }) => {
      const pc = createPeerConnection(from);
      peerConnections.current[from] = { pc };

      localStreamRef.current.getTracks().forEach((track) =>
        pc.addTrack(track, localStreamRef.current)
      );

      await pc.setRemoteDescription(new RTCSessionDescription(offer));
      const answer = await pc.createAnswer();
      await pc.setLocalDescription(answer);
      socket.emit("answer", { to: from, answer });
    });

    socket.on("answer", async ({ from, answer }) => {
      const connection = peerConnections.current[from];
      if (connection) {
        await connection.pc.setRemoteDescription(
          new RTCSessionDescription(answer)
        );
      }
    });

    socket.on("candidate", ({ from, candidate }) => {
      const connection = peerConnections.current[from];
      if (connection && connection.pc) {
        connection.pc.addIceCandidate(new RTCIceCandidate(candidate));
      }
    });

    socket.on("user-left", ({ id }) => {
      const connection = peerConnections.current[id];
      if (connection) {
        connection.pc.close();
        delete peerConnections.current[id];
        setParticipants((prev) => {
          const updated = { ...prev };
          delete updated[id];
          return updated;
        });
      }
    });

    return () => {
      socket.disconnect();
    };
  }, []);

  const createPeerConnection = (id, remoteName) => {
    const pc = new RTCPeerConnection();

    pc.onicecandidate = (event) => {
      if (event.candidate) {
        socket.emit("candidate", { to: id, candidate: event.candidate });
      }
    };

    pc.ontrack = (event) => {
      setParticipants((prev) => ({
        ...prev,
        [id]: { stream: event.streams[0], name: remoteName },
      }));
    };

    return pc;
  };

  const handleJoin = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({
        video: true,
        audio: true,
      });

      localVideoRef.current.srcObject = stream;
      localStreamRef.current = stream;
      setJoined(true);

      socket.emit("join-room", { roomId, name });
    } catch (err) {
      console.error("Error accessing media devices:", err);
      alert("Failed to access camera/microphone.");
    }
  };

  return (
    <div className="bg-black text-white min-h-screen p-4">
      {!joined ? (
        <div className="max-w-md mx-auto mt-20 space-y-4">
          <h1 className="text-2xl font-bold text-gold-400">Join Ve Meet</h1>
          <input
            type="text"
            className="w-full p-2 rounded bg-gray-800 border border-gray-700"
            placeholder="Enter Room ID"
            value={roomId}
            onChange={(e) => setRoomId(e.target.value)}
          />
          <input
            type="text"
            className="w-full p-2 rounded bg-gray-800 border border-gray-700"
            placeholder="Enter Your Name"
            value={name}
            onChange={(e) => setName(e.target.value)}
          />
          <button
            onClick={handleJoin}
            className="bg-yellow-500 text-black px-4 py-2 rounded font-bold"
          >
            Join
          </button>
        </div>
      ) : (
        <div>
          <div className="text-xl font-semibold mb-4">
            Room: {roomId} | You: {name}
          </div>
          <div className="grid grid-cols-3 gap-4">
            <div>
              <video
                ref={localVideoRef}
                autoPlay
                muted
                playsInline
                className="rounded shadow border border-yellow-400"
              />
              <p className="mt-2 text-yellow-400 font-bold">{name} (You)</p>
            </div>
            {Object.entries(participants).map(([id, { stream, name }]) => (
              <div key={id}>
                <VideoPlayer stream={stream} />
                <p className="mt-2 text-yellow-400">{name || id}</p>
              </div>
            ))}
          </div>
        </div>
      )}
    </div>
  );
};

const VideoPlayer = ({ stream }) => {
  const ref = useRef();
  useEffect(() => {
    if (ref.current && stream) {
      ref.current.srcObject = stream;
    }
  }, [stream]);

  return (
    <video
      ref={ref}
      autoPlay
      playsInline
      className="rounded shadow border border-gray-600"
    />
  );
};

export default Room;
