import React, { useEffect, useRef, useState } from 'react';
import io from 'socket.io-client';

const socket = io('https://localhost:5000', { secure: true });

const Room = () => {
  const [roomId, setRoomId] = useState('');
  const [name, setName] = useState('');
  const [joined, setJoined] = useState(false);
  const [participants, setParticipants] = useState({});
  const localVideoRef = useRef();
  const peerConnections = useRef({});
  const remoteVideosRef = useRef({});
  const localStreamRef = useRef();

  useEffect(() => {
    socket.on('user-list', users => {
      setParticipants(users);
    });

    socket.on('user-joined', async ({ id, name }) => {
      const pc = createPeerConnection(id, name);
      peerConnections.current[id] = pc;

      localStreamRef.current.getTracks().forEach(track => {
        pc.addTrack(track, localStreamRef.current);
      });

      const offer = await pc.createOffer();
      await pc.setLocalDescription(offer);
      socket.emit('offer', { to: id, offer, fromName: name });
    });

    socket.on('offer', async ({ from, offer, name }) => {
      const pc = createPeerConnection(from, name);
      peerConnections.current[from] = pc;

      localStreamRef.current.getTracks().forEach(track => {
        pc.addTrack(track, localStreamRef.current);
      });

      await pc.setRemoteDescription(new RTCSessionDescription(offer));
      const answer = await pc.createAnswer();
      await pc.setLocalDescription(answer);
      socket.emit('answer', { to: from, answer });
    });

    socket.on('answer', async ({ from, answer }) => {
      const pc = peerConnections.current[from];
      if (!pc.currentRemoteDescription) {
        await pc.setRemoteDescription(new RTCSessionDescription(answer));
      }
    });

    socket.on('candidate', ({ from, candidate }) => {
      const pc = peerConnections.current[from];
      if (pc) {
        pc.addIceCandidate(new RTCIceCandidate(candidate));
      }
    });

    socket.on('user-left', ({ id }) => {
      if (peerConnections.current[id]) {
        peerConnections.current[id].close();
        delete peerConnections.current[id];
      }
      delete remoteVideosRef.current[id];
      setParticipants(prev => {
        const updated = { ...prev };
        delete updated[id];
        return updated;
      });
    });

    return () => {
      socket.disconnect();
    };
  }, []);

  const createPeerConnection = (id, name) => {
    const pc = new RTCPeerConnection();
    pc.onicecandidate = event => {
      if (event.candidate) {
        socket.emit('candidate', { to: id, candidate: event.candidate });
      }
    };
    pc.ontrack = event => {
      if (!remoteVideosRef.current[id]) {
        const video = document.createElement('video');
        video.autoplay = true;
        video.playsInline = true;
        video.srcObject = event.streams[0];
        video.style.width = '200px';
        video.style.margin = '5px';
        video.setAttribute('data-id', id);
        remoteVideosRef.current[id] = video;
        document.getElementById('remoteVideos').appendChild(video);
      }
    };
    return pc;
  };

  const handleJoin = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ video: true, audio: true });
      localStreamRef.current = stream;
      localVideoRef.current.srcObject = stream;
      socket.emit('join-room', { roomId, name });
      setJoined(true);
    } catch (err) {
      console.error('Error accessing media devices.', err);
    }
  };

  return (
    <div style={{ padding: '20px', background: '#111', color: 'white', fontFamily: 'sans-serif' }}>
      {!joined ? (
        <div>
          <h2>Join Room</h2>
          <input value={roomId} onChange={e => setRoomId(e.target.value)} placeholder="Room ID" />
          <input value={name} onChange={e => setName(e.target.value)} placeholder="Your Name" />
          <button onClick={handleJoin}>Join</button>
        </div>
      ) : (
        <div>
          <h2>Room: {roomId}</h2>
          <h3>Participants</h3>
          <ul>
            {Object.entries(participants).map(([id, userName]) => (
              <li key={id}>{userName}</li>
            ))}
          </ul>
          <video ref={localVideoRef} autoPlay muted playsInline style={{ width: '250px', margin: '10px' }} />
          <div id="remoteVideos" style={{ display: 'flex', flexWrap: 'wrap' }}></div>
        </div>
      )}
    </div>
  );
};

export default Room;
