// src/components/GuestRoom.js
import React, { useEffect, useRef, useState } from 'react';
import io from 'socket.io-client';
import Peer from 'simple-peer';
import { v4 as uuidv4 } from 'uuid';

const socket = io('http://localhost:5000'); // Update if hosted

const GuestRoom = () => {
  const [peers, setPeers] = useState([]);
  const [roomCode, setRoomCode] = useState('');
  const [userName, setUserName] = useState('');
  const [joined, setJoined] = useState(false);
  const [users, setUsers] = useState([]);
  const [stream, setStream] = useState();
  const [screenSharing, setScreenSharing] = useState(false);

  const userVideo = useRef();
  const peersRef = useRef([]);
  const screenTrackRef = useRef();

  useEffect(() => {
    socket.on('all-users', (users) => {
      const peers = [];
      users.forEach((user) => {
        const peer = createPeer(user.socketID, socket.id, stream);
        peersRef.current.push({ peerID: user.socketID, peer });
        peers.push({ peerID: user.socketID, peer });
      });
      setPeers(peers);
    });

    socket.on('user-joined', (payload) => {
      const peer = addPeer(payload.signal, payload.callerID, stream);
      peersRef.current.push({ peerID: payload.callerID, peer });
      setPeers((users) => [...users, { peerID: payload.callerID, peer }]);
    });

    socket.on('receiving-returned-signal', (payload) => {
      const item = peersRef.current.find(p => p.peerID === payload.id);
      item.peer.signal(payload.signal);
    });

    socket.on('update-user-list', (updatedUsers) => {
      setUsers(updatedUsers);
    });
  }, [stream]);

  const joinRoom = async () => {
    const stream = await navigator.mediaDevices.getUserMedia({ video: true, audio: true });
    setStream(stream);
    userVideo.current.srcObject = stream;
    const roomId = prompt('Enter Room Code:');
    const name = prompt('Enter Your Name:');
    setRoomCode(roomId);
    setUserName(name);
    socket.emit('join-room', { roomID: roomId, userName: name });
    setJoined(true);
  };

  function createPeer(userToSignal, callerID, stream) {
    const peer = new Peer({ initiator: true, trickle: false, stream });
    peer.on('signal', signal => {
      socket.emit('sending-signal', { userToSignal, callerID, signal });
    });
    return peer;
  }

  function addPeer(incomingSignal, callerID, stream) {
    const peer = new Peer({ initiator: false, trickle: false, stream });
    peer.on('signal', signal => {
      socket.emit('returning-signal', { signal, callerID });
    });
    peer.signal(incomingSignal);
    return peer;
  }

  const toggleMute = () => {
    stream.getAudioTracks()[0].enabled = !stream.getAudioTracks()[0].enabled;
  };

  const toggleVideo = () => {
    stream.getVideoTracks()[0].enabled = !stream.getVideoTracks()[0].enabled;
  };

  const shareScreen = async () => {
    const screenStream = await navigator.mediaDevices.getDisplayMedia({ cursor: true });
    const screenTrack = screenStream.getTracks()[0];
    peersRef.current.forEach(({ peer }) => {
      const sender = peer._pc.getSenders().find(s => s.track.kind === 'video');
      sender.replaceTrack(screenTrack);
    });
    screenTrack.onended = () => {
      peersRef.current.forEach(({ peer }) => {
        const sender = peer._pc.getSenders().find(s => s.track.kind === 'video');
        sender.replaceTrack(stream.getVideoTracks()[0]);
      });
    };
  };

  const leaveMeeting = () => {
    window.location.reload();
  };

  return (
    <div className="min-h-screen bg-black text-white p-4">
      {!joined ? (
        <div className="flex justify-center items-center h-screen">
          <button onClick={joinRoom} className="bg-yellow-500 px-6 py-3 rounded-xl text-black font-bold text-xl shadow-lg">Join Meeting</button>
        </div>
      ) : (
        <div>
          <div className="flex justify-between items-center mb-4">
            <div className="text-lg font-semibold text-yellow-400">Room: {roomCode}</div>
            <div className="text-lg font-semibold text-yellow-400">You: {userName}</div>
          </div>

          <div className="grid grid-cols-2 md:grid-cols-3 gap-4">
            <div>
              <video muted ref={userVideo} autoPlay playsInline className="w-full rounded-2xl border-2 border-yellow-500"></video>
              <div className="text-center mt-1 text-yellow-400 font-medium">{userName} (You)</div>
            </div>
            {peers.map((peerObj, index) => (
              <Video key={index} peer={peerObj.peer} />
            ))}
          </div>

          <div className="mt-4 flex justify-center gap-4">
            <button onClick={toggleMute} className="bg-yellow-500 text-black px-4 py-2 rounded-xl font-semibold">Mute/Unmute</button>
            <button onClick={toggleVideo} className="bg-yellow-500 text-black px-4 py-2 rounded-xl font-semibold">Video On/Off</button>
            <button onClick={shareScreen} className="bg-yellow-500 text-black px-4 py-2 rounded-xl font-semibold">Share Screen</button>
            <button onClick={leaveMeeting} className="bg-red-600 text-white px-4 py-2 rounded-xl font-semibold">Leave</button>
          </div>

          <div className="mt-6 bg-gray-900 rounded-lg p-4">
            <h3 className="text-yellow-400 text-lg mb-2">Participants</h3>
            <ul className="space-y-1">
              {users.map((user, i) => (
                <li key={i} className="text-white">• {user.name}</li>
              ))}
            </ul>
          </div>
        </div>
      )}
    </div>
  );
};

const Video = ({ peer }) => {
  const ref = useRef();

  useEffect(() => {
    peer.on('stream', stream => {
      ref.current.srcObject = stream;
    });
  }, [peer]);

  return <video playsInline autoPlay ref={ref} className="w-full rounded-2xl border-2 border-yellow-500" />;
};

export default GuestRoom;
