import React, { useEffect, useRef, useState } from "react";
import io from "socket.io-client";
import Peer from "simple-peer";
import './Room.css';

const socket = io('https://192.168.29.23:5000', {
  secure: true,
  reconnection: true,
  rejectUnauthorized: false
});

const GuestRoom = () => {
  const [name, setName] = useState("");
  const [roomID, setRoomID] = useState("");
  const [joined, setJoined] = useState(false);
  const [stream, setStream] = useState(null);
  const [peers, setPeers] = useState([]);
  const [micOn, setMicOn] = useState(true);
  const [camOn, setCamOn] = useState(true);

  const userVideo = useRef(null);
  const peersRef = useRef({}); // Map of peerID => peer instance

  // Set video stream to self preview
  useEffect(() => {
    if (userVideo.current && stream) {
      userVideo.current.srcObject = stream;
    }
  }, [stream]);

  const handleJoin = async () => {
    if (!name || !roomID) return alert("Name and Room Code required");

    try {
      const localStream = await navigator.mediaDevices.getUserMedia({ video: true, audio: true });
      setStream(localStream);
      setJoined(true);

      socket.emit("join-room", { roomId: roomID, userName: name });

      // This user receives list of other participants
      socket.on("user-list", (participants) => {
        const peersArr = [];
        participants.forEach(({ id, name: remoteName }) => {
          if (id === socket.id) return; // skip self
          const peer = createPeer(id, socket.id, localStream);
          peersRef.current[id] = { peer, name: remoteName };
          peersArr.push({ peerID: id, peer, userName: remoteName });
        });
        setPeers(peersArr);
      });

      // New user joins
      socket.on("user-joined", ({ id, name: remoteName }) => {
        const peer = addPeer(id, localStream);
        peersRef.current[id] = { peer, name: remoteName };
        setPeers(prev => [...prev, { peerID: id, peer, userName: remoteName }]);
      });

      socket.on("offer", ({ from, offer, name: remoteName }) => {
        const peer = new Peer({ initiator: false, trickle: false, stream: localStream });
        peer.on("signal", answer => {
          socket.emit("answer", { to: from, answer });
        });
        peer.signal(offer);
        peersRef.current[from] = { peer, name: remoteName };
        setPeers(prev => [...prev, { peerID: from, peer, userName: remoteName }]);
      });

      socket.on("answer", ({ from, answer }) => {
        const ref = peersRef.current[from];
        if (ref) ref.peer.signal(answer);
      });

      socket.on("ice-candidate", ({ from, candidate }) => {
        const ref = peersRef.current[from];
        if (ref) ref.peer.signal(candidate);
      });

      socket.on("user-left", (id) => {
        const ref = peersRef.current[id];
        if (ref) {
          ref.peer.destroy();
          delete peersRef.current[id];
        }
        setPeers(prev => prev.filter(p => p.peerID !== id));
      });

    } catch (err) {
      console.error("Error getting media:", err);
      alert("Camera or mic access failed.");
    }
  };

  const createPeer = (userToSignal, callerID, stream) => {
    const peer = new Peer({ initiator: true, trickle: false, stream });
    peer.on("signal", offer => {
      socket.emit("offer", { to: userToSignal, offer });
    });
    return peer;
  };

  const addPeer = (callerID, stream) => {
    const peer = new Peer({ initiator: false, trickle: false, stream });
    peer.on("signal", answer => {
      socket.emit("answer", { to: callerID, answer });
    });
    return peer;
  };

  const toggleMic = () => {
    stream?.getAudioTracks().forEach(track => {
      track.enabled = !micOn;
    });
    setMicOn(prev => !prev);
  };

  const toggleCam = () => {
    stream?.getVideoTracks().forEach(track => {
      track.enabled = !camOn;
    });
    setCamOn(prev => !prev);
  };

  const leaveRoom = () => {
    Object.values(peersRef.current).forEach(({ peer }) => peer.destroy());
    if (stream) {
      stream.getTracks().forEach(track => track.stop());
    }
    socket.disconnect();
    window.location.reload();
  };

  return (
    <div className="room-container">
      {!joined ? (
        <div className="join-container">
          <h2 className="logo">Ve Meet</h2>
          <input placeholder="Your Name" onChange={(e) => setName(e.target.value)} />
          <input placeholder="Room Code" onChange={(e) => setRoomID(e.target.value)} />
          <button onClick={handleJoin}>Join</button>
        </div>
      ) : (
        <div className="room-panel">
          <div className="video-grid">
            <div className="video-block">
              <video playsInline muted autoPlay ref={userVideo} className="video" />
              <p>{name} (You)</p>
            </div>
            {peers.map(({ peerID, peer, userName }) => (
              <Video key={peerID} peer={peer} name={userName} />
            ))}
          </div>
          <div className="controls">
            <button onClick={toggleMic}>{micOn ? "Mute" : "Unmute"}</button>
            <button onClick={toggleCam}>{camOn ? "Video Off" : "Video On"}</button>
            <button onClick={leaveRoom}>Leave</button>
          </div>
        </div>
      )}
    </div>
  );
};

const Video = ({ peer, name }) => {
  const ref = useRef();

  useEffect(() => {
    peer.on("stream", stream => {
      if (ref.current) ref.current.srcObject = stream;
    });
  }, [peer]);

  return (
    <div className="video-block">
      <video playsInline autoPlay ref={ref} className="video" />
      <p>{name}</p>
    </div>
  );
};

export default GuestRoom;
