import React, { useEffect, useRef, useState } from 'react';
import io from 'socket.io-client';
import Peer from 'simple-peer';

const socket = io.connect('https://localhost:5000', { secure: true });

const GuestRoom = () => {
  const [peers, setPeers] = useState([]);
  const [stream, setStream] = useState();
  const [name, setName] = useState('');
  const [roomID, setRoomID] = useState('');
  const [joined, setJoined] = useState(false);
  const videoRef = useRef();
  const peersRef = useRef([]);

  useEffect(() => {
    return () => {
      if (stream) stream.getTracks().forEach(track => track.stop());
    };
  }, [stream]);

  const handleJoin = async () => {
    if (!name || !roomID) return alert("Please enter name and room ID");

    try {
      const currentStream = await navigator.mediaDevices.getUserMedia({ video: true, audio: true });
      setStream(currentStream);
      videoRef.current.srcObject = currentStream;

      socket.emit("join-room", { roomID, name });

      socket.on("all-users", users => {
        const newPeers = users.map(user => {
          const peer = createPeer(user.socketID, socket.id, currentStream);
          peersRef.current.push({ peerID: user.socketID, peer, name: user.name });
          return { peerID: user.socketID, peer, name: user.name };
        });
        setPeers(newPeers);
      });

      socket.on("user-joined", payload => {
        const peer = addPeer(payload.signal, payload.callerID, currentStream);
        peersRef.current.push({ peerID: payload.callerID, peer, name: payload.name });
        setPeers(prev => [...prev, { peerID: payload.callerID, peer, name: payload.name }]);
      });

      socket.on("receiving-returned-signal", payload => {
        const item = peersRef.current.find(p => p.peerID === payload.id);
        item.peer.signal(payload.signal);
      });

      setJoined(true);
    } catch (err) {
      console.error("Error accessing media devices:", err);
    }
  };

  const createPeer = (userToSignal, callerID, stream) => {
    const peer = new Peer({ initiator: true, trickle: false, stream });

    peer.on("signal", signal => {
      socket.emit("sending-signal", { userToSignal, callerID, signal, name });
    });

    return peer;
  };

  const addPeer = (incomingSignal, callerID, stream) => {
    const peer = new Peer({ initiator: false, trickle: false, stream });

    peer.on("signal", signal => {
      socket.emit("returning-signal", { signal, callerID });
    });

    peer.signal(incomingSignal);

    return peer;
  };

  return (
    <div style={{ backgroundColor: '#000', color: '#fff', padding: 20 }}>
      <h1 style={{ fontSize: '2rem', color: 'gold', textShadow: '1px 1px 5px gold' }}>Ve Meet</h1>
      {!joined ? (
        <div>
          <input placeholder="Enter your name" value={name} onChange={e => setName(e.target.value)} style={{ padding: 10, marginRight: 10 }} />
          <input placeholder="Enter room ID" value={roomID} onChange={e => setRoomID(e.target.value)} style={{ padding: 10, marginRight: 10 }} />
          <button onClick={handleJoin} style={{ padding: 10, backgroundColor: 'gold', border: 'none', fontWeight: 'bold' }}>Join</button>
        </div>
      ) : (
        <div>
          <div style={{ display: 'flex', flexWrap: 'wrap', gap: '10px', marginTop: 20 }}>
            <div style={{ textAlign: 'center' }}>
              <video muted ref={videoRef} autoPlay playsInline style={{ width: 200, borderRadius: 10 }} />
              <div style={{ color: 'gold', marginTop: 5 }}>{name} (You)</div>
            </div>
            {peers.map((peerObj, index) => (
              <Video key={index} peer={peerObj.peer} name={peerObj.name} />
            ))}
          </div>
        </div>
      )}
    </div>
  );
};

const Video = ({ peer, name }) => {
  const ref = useRef();

  useEffect(() => {
    peer.on("stream", stream => {
      ref.current.srcObject = stream;
    });
  }, [peer]);

  return (
    <div style={{ textAlign: 'center' }}>
      <video playsInline autoPlay ref={ref} style={{ width: 200, borderRadius: 10 }} />
      <div style={{ color: 'gold', marginTop: 5 }}>{name}</div>
    </div>
  );
};

export default GuestRoom;
