import React, { useEffect, useRef, useState } from 'react';
import io from 'socket.io-client';
import Peer from 'simple-peer';

let namePrompted = false;

const socket = io('https://192.168.29.23:5000', {
  secure: true,
  reconnection: true,
  rejectUnauthorized: false
});

function Room() {
  const [peers, setPeers] = useState([]);
  const [participants, setParticipants] = useState([]);
  const [userName, setUserName] = useState('');
  const [roomID, setRoomID] = useState('');
  const [micOn, setMicOn] = useState(true);
  const [camOn, setCamOn] = useState(true);
  const [screenSharing, setScreenSharing] = useState(false);
  const userVideo = useRef();
  const peersRef = useRef([]);
  const streamRef = useRef();

  useEffect(() => {
    const name = prompt('Enter your name');
    const room = prompt('Enter room code');
    setUserName(name);
    setRoomID(room);

    navigator.mediaDevices.getUserMedia({ video: true, audio: true }).then(stream => {
      streamRef.current = stream;
      if (userVideo.current) {
        userVideo.current.srcObject = stream;
      }

      socket.emit('join-room', { roomID: room, userName: name });

      socket.on('all-users', users => {
        const newPeers = [];
        users.forEach(user => {
          const peer = createPeer(user.socketID, socket.id, stream, name);
          peersRef.current.push({
            peerID: user.socketID,
            peer,
            userName: user.userName,
          });
          newPeers.push({ peerID: user.socketID, peer, userName: user.userName });
        });
        setPeers(newPeers);
      });

      socket.on('user-joined', payload => {
        const peer = addPeer(payload.signal, payload.callerID, stream, payload.userName);
        if (!peer) return; // protect from undefined peer
        peersRef.current.push({
          peerID: payload.callerID,
          peer,
          userName: payload.userName,
        });
        setPeers(users => [...users, { peerID: payload.callerID, peer, userName: payload.userName }]);
      });

      socket.on('receiving-returned-signal', payload => {
        const item = peersRef.current.find(p => p.peerID === payload.id);
        if (item && item.peer) {
          item.peer.signal(payload.signal);
        }
      });

      socket.on('receiving-signal', ({ signal, from, name }) => {
        const trySignal = (attempts = 10) => {
          const foundPeer = peersRef.current.find(p => p.peerID === from);
          if (foundPeer && foundPeer.peer) {
            try {
              foundPeer.peer.signal(signal);
            } catch (e) {
              console.error("❌ Signal error:", e);
            }
          } else if (attempts > 0) {
            setTimeout(() => trySignal(attempts - 1), 200);
          } else {
            console.warn("⚠️ Could not find peer after retries for:", from);
          }
        };
        trySignal();
      });

      socket.on('update-user-list', users => {
        setParticipants(users);
      });

      socket.on('user-disconnected', id => {
        setPeers(peers => peers.filter(p => p.peerID !== id));
        peersRef.current = peersRef.current.filter(p => p.peerID !== id);
      });
    });
  }, []);

  function createPeer(userToSignal, callerID, stream, userName) {
    const peer = new Peer({
      initiator: true,
      trickle: false,
      stream,
    });

    peer.on('signal', signal => {
      socket.emit('sending-signal', { userToSignal, callerID, signal, userName });
    });

    return peer;
  }

  function addPeer(incomingSignal, callerID, stream, userName) {
    try {
      const peer = new Peer({
        initiator: false,
        trickle: false,
        stream,
      });

      peer.on('signal', signal => {
        socket.emit('returning-signal', { signal, callerID });
      });

      peer.signal(incomingSignal);

      return peer;
    } catch (e) {
      console.error("❌ Failed to add peer:", e);
      return null;
    }
  }

  const toggleMic = () => {
    streamRef.current.getAudioTracks()[0].enabled = !micOn;
    setMicOn(!micOn);
  };

  const toggleCam = () => {
    streamRef.current.getVideoTracks()[0].enabled = !camOn;
    setCamOn(!camOn);
  };

  const handleScreenShare = async () => {
    if (!screenSharing) {
      const screenStream = await navigator.mediaDevices.getDisplayMedia({ video: true });
      const screenTrack = screenStream.getVideoTracks()[0];

      peersRef.current.forEach(({ peer }) => {
        peer.replaceTrack(streamRef.current.getVideoTracks()[0], screenTrack, streamRef.current);
      });

      screenTrack.onended = () => {
        peersRef.current.forEach(({ peer }) => {
          peer.replaceTrack(screenTrack, streamRef.current.getVideoTracks()[0], streamRef.current);
        });
        setScreenSharing(false);
      };

      setScreenSharing(true);
    }
  };

  const endMeeting = () => {
    peersRef.current.forEach(({ peer }) => peer.destroy());
    peersRef.current = [];
    if (streamRef.current) {
      streamRef.current.getTracks().forEach(track => track.stop());
    }
    socket.disconnect();
    window.location.reload();
  };

  return (
    <div style={{ display: 'flex', height: '100vh', backgroundColor: '#000', color: 'gold', fontFamily: "'Segoe UI', sans-serif" }}>
      <div style={{ flex: 1, display: 'flex', flexDirection: 'column' }}>
        <header style={{ padding: '15px 20px', borderBottom: '2px solid gold', display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
          <div style={{ fontSize: '32px', fontWeight: 'bold', textShadow: '2px 2px 4px #000' }}>Ve Meet</div>
          <div>
            <span style={{ marginRight: '20px', fontWeight: 'bold' }}>Room: {roomID}</span>
            <span>You: {userName}</span>
          </div>
        </header>

        <main style={{ flex: 1, padding: '20px', display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(220px, 1fr))', gap: '15px', overflowY: 'auto' }}>
          <div style={{ backgroundColor: '#111', borderRadius: '10px', overflow: 'hidden', textAlign: 'center' }}>
            <video muted ref={userVideo} autoPlay playsInline style={{ width: '100%', borderRadius: '10px' }} />
            <div style={{ padding: '5px', background: '#222' }}>{userName} (You)</div>
          </div>

          {peers.map((peerObj, index) => (
            <Video key={index} peer={peerObj.peer} name={peerObj.userName} />
          ))}
        </main>
      </div>

      <aside style={{ width: '280px', background: '#111', padding: '20px', borderLeft: '2px solid gold', display: 'flex', flexDirection: 'column', justifyContent: 'space-between' }}>
        <div>
          <h3 style={{ marginBottom: '10px' }}>Participants ({participants.length + 1})</h3>
          <ul style={{ listStyle: 'none', padding: 0, maxHeight: '60vh', overflowY: 'auto' }}>
            <li style={{ padding: '5px 0', borderBottom: '1px solid #333' }}>{userName} (You)</li>
            {participants.map((p, i) => (
              <li key={i} style={{ padding: '5px 0', borderBottom: '1px solid #333' }}>{p}</li>
            ))}
          </ul>
        </div>

        <div style={{ marginTop: '20px', display: 'flex', flexDirection: 'column' }}>
          <button onClick={toggleMic} style={btnStyle}>{micOn ? 'Mute' : 'Unmute'}</button>
          <button onClick={toggleCam} style={btnStyle}>{camOn ? 'Video Off' : 'Video On'}</button>
          <button onClick={handleScreenShare} style={btnStyle}>{screenSharing ? 'Stop Sharing' : 'Share Screen'}</button>
          <button onClick={endMeeting} style={{ ...btnStyle, backgroundColor: 'red', color: 'white' }}>End Meeting</button>
        </div>
      </aside>
    </div>
  );
}

const Video = ({ peer, name }) => {
  const ref = useRef();

  useEffect(() => {
    peer.on('stream', stream => {
      if (ref.current) {
        ref.current.srcObject = stream;
      }
    });
  }, [peer]);

  return (
    <div style={{ backgroundColor: '#111', borderRadius: '10px', overflow: 'hidden', textAlign: 'center' }}>
      <video playsInline autoPlay ref={ref} style={{ width: '100%', borderRadius: '10px' }} />
      <div style={{ padding: '5px', background: '#222' }}>{name}</div>
    </div>
  );
};

const btnStyle = {
  margin: '5px 0',
  padding: '10px 15px',
  backgroundColor: 'gold',
  border: 'none',
  borderRadius: '8px',
  fontWeight: 'bold',
  cursor: 'pointer',
};

export default Room;
