import React, { useEffect, useRef, useState } from "react";
import io from "socket.io-client";
import Peer from "simple-peer";
import "./Room.css";

const socket = io("https://192.168.29.23:5000", {
  secure: true,
  reconnection: true,
  rejectUnauthorized: false,
});

const Room = () => {
  const [peers, setPeers] = useState([]);
  const [participants, setParticipants] = useState([]);
  const [userName, setUserName] = useState("");
  const [roomID, setRoomID] = useState("");
  const [micOn, setMicOn] = useState(true);
  const [camOn, setCamOn] = useState(true);
  const [screenSharing, setScreenSharing] = useState(false);

  const userVideo = useRef();
  const peersRef = useRef([]);
  const streamRef = useRef();
  const screenTrackRef = useRef();
  const videoRefs = useRef({});

  useEffect(() => {
    const name = prompt("Enter your name");
    const room = prompt("Enter room code");
    setUserName(name);
    setRoomID(room);

    navigator.mediaDevices
      .getUserMedia({ video: true, audio: true })
      .then((stream) => {
        streamRef.current = stream;
        if (userVideo.current) userVideo.current.srcObject = stream;

        socket.emit("join-room", { roomId: room, userName: name });

        socket.on("all-users", (users) => {
          const newPeers = [];
          users.forEach((user) => {
            if (user.id === socket.id) return;
            const peer = createPeer(user.id, socket.id, stream);
            peersRef.current.push({ peerID: user.id, peer, userName: user.name });
            newPeers.push({ peerID: user.id, peer, userName: user.name });
          });
          setPeers(newPeers);
          setParticipants(users.map((u) => u.name));
        });

        socket.on("user-joined", (payload) => {
          const peer = addPeer(payload.signal, payload.id, payload.name, stream);
          peersRef.current.push({ peerID: payload.id, peer, userName: payload.name });
          setPeers((users) => [...users, { peerID: payload.id, peer, userName: payload.name }]);
          setParticipants((list) => [...list, payload.name]);
        });

        socket.on("receiving-returned-signal", (payload) => {
          const item = peersRef.current.find((p) => p.peerID === payload.id);
          if (item) item.peer.signal(payload.signal);
        });

        socket.on("user-disconnected", (id) => {
          setPeers((users) => users.filter((p) => p.peerID !== id));
          peersRef.current = peersRef.current.filter((p) => p.peerID !== id);
          setParticipants((list) => 
            list.filter((name) => peersRef.current.some((p) => p.userName === name))
          );
        });
      });

    return () => {
      socket.disconnect();
      peersRef.current.forEach(({ peer }) => peer.destroy());
      if (streamRef.current) streamRef.current.getTracks().forEach((t) => t.stop());
    };
  }, []);

  const createPeer = (userToSignal, callerID, stream) => {
    const peer = new Peer({
      initiator: true,
      trickle: false,
      stream,
    });

    peer.on("signal", (signal) => {
      socket.emit("sending-signal", { userToSignal, callerID, signal });
    });

    peer.on("stream", (remoteStream) => {
      // Retry assigning stream if video element not ready yet
      const assignStream = () => {
        const videoElement = videoRefs.current[userToSignal];
        if (videoElement) {
          videoElement.srcObject = remoteStream;
        } else {
          setTimeout(assignStream, 100);
        }
      };
      assignStream();
    });

    return peer;
  };

  const addPeer = (incomingSignal, callerId, name, stream) => {
    const peer = new Peer({
      initiator: false,
      trickle: false,
      stream,
    });

    peer.on("signal", (signal) => {
      socket.emit("returning signal", { signal, to: callerId });
    });

    peer.on("stream", (remoteStream) => {
      // Retry assigning stream if video element not ready yet
      const assignStream = () => {
        const videoElement = videoRefs.current[callerId];
        if (videoElement) {
          videoElement.srcObject = remoteStream;
        } else {
          setTimeout(assignStream, 100);
        }
      };
      assignStream();
    });

    peer.signal(incomingSignal);
    return peer;
  };

  const toggleMic = () => {
    if (!streamRef.current) return;
    streamRef.current.getAudioTracks().forEach((track) => {
      track.enabled = !micOn;
    });
    setMicOn(!micOn);
  };

  const toggleCam = () => {
    if (!streamRef.current) return;
    streamRef.current.getVideoTracks().forEach((track) => {
      track.enabled = !camOn;
    });
    setCamOn(!camOn);
  };

  const handleScreenShare = async () => {
    if (!screenSharing) {
      try {
        const screenStream = await navigator.mediaDevices.getDisplayMedia({ video: true });
        const screenTrack = screenStream.getVideoTracks()[0];
        screenTrackRef.current = screenTrack;

        peersRef.current.forEach(({ peer }) => {
          peer.replaceTrack(streamRef.current.getVideoTracks()[0], screenTrack, streamRef.current);
        });

        screenTrack.onended = () => {
          peersRef.current.forEach(({ peer }) => {
            peer.replaceTrack(screenTrack, streamRef.current.getVideoTracks()[0], streamRef.current);
          });
          setScreenSharing(false);
        };

        setScreenSharing(true);
      } catch (err) {
        console.error("Screen share error:", err);
      }
    } else {
      if (screenTrackRef.current) screenTrackRef.current.stop();
      setScreenSharing(false);
    }
  };

  const endMeeting = () => {
    peersRef.current.forEach(({ peer }) => peer.destroy());
    peersRef.current = [];
    if (streamRef.current) streamRef.current.getTracks().forEach((track) => track.stop());
    socket.disconnect();
    window.location.reload();
  };

  return (
    <div className="room-container">
      <header className="room-header">
        <h2 className="logo">Ve Meet</h2>
        <div>
          <span>Room: {roomID}</span>
          <span className="username">You: {userName}</span>
        </div>
      </header>

      <main className="video-grid">
        <div className="video-block">
          <video muted ref={userVideo} autoPlay playsInline className="video" />
          <p>{userName} (You)</p>
        </div>
        {peers.map(({ peerID, userName }) => (
          <div className="video-block" key={peerID}>
            <video
              playsInline
              autoPlay
              className="video"
              ref={(el) => {
                videoRefs.current[peerID] = el;
              }}
            />
            <p>{userName}</p>
          </div>
        ))}
      </main>

      <aside className="participants-panel">
        <h3>Participants ({participants.length + 1})</h3>
        <ul>
          <li>{userName} (You)</li>
          {participants.map((pName, idx) => (
            <li key={idx}>{pName}</li>
          ))}
        </ul>

        <div className="controls">
          <button onClick={toggleMic}>{micOn ? "Mute" : "Unmute"}</button>
          <button onClick={toggleCam}>{camOn ? "Video Off" : "Video On"}</button>
          <button onClick={handleScreenShare}>{screenSharing ? "Stop Sharing" : "Share Screen"}</button>
          <button className="end-btn" onClick={endMeeting}>
            End Meeting
          </button>
        </div>
      </aside>
    </div>
  );
};

export default Room;
