import React, { useEffect, useRef, useState } from 'react';
import io from 'socket.io-client';
import Peer from 'simple-peer';
import '../styles/GuestRoom.css';

const socket = io.connect('https://localhost:5000');

function GuestRoom() {
  const [stream, setStream] = useState(null);
  const [peers, setPeers] = useState([]);
  const [peerNames, setPeerNames] = useState({});
  const [name, setName] = useState('');
  const [roomCode, setRoomCode] = useState('');
  const [joined, setJoined] = useState(false);
  const [devices, setDevices] = useState({ audio: [], video: [] });
  const [selectedVideo, setSelectedVideo] = useState('');
  const [selectedAudio, setSelectedAudio] = useState('');
  const [micOn, setMicOn] = useState(true);
  const [camOn, setCamOn] = useState(true);

  const userVideo = useRef();
  const peersRef = useRef([]);
  const videoGridRef = useRef();

  useEffect(() => {
    navigator.mediaDevices.enumerateDevices().then(devices => {
      const video = devices.filter(d => d.kind === 'videoinput');
      const audio = devices.filter(d => d.kind === 'audioinput');
      setDevices({ video, audio });
      setSelectedVideo(video[0]?.deviceId || '');
      setSelectedAudio(audio[0]?.deviceId || '');
    });
  }, []);

  const createPeer = (userToSignal, callerID, stream) => {
    const peer = new Peer({ initiator: true, trickle: false, stream });
    peer.on('signal', signal => {
      socket.emit('sending signal', { userToSignal, callerID, signal, name });
    });
    return peer;
  };

  const addPeer = (incomingSignal, callerID, stream, callerName) => {
    const peer = new Peer({ initiator: false, trickle: false, stream });
    peer.on('signal', signal => {
      socket.emit('returning signal', { signal, callerID });
    });
    peer.signal(incomingSignal);
    return peer;
  };

  const handleJoin = async () => {
    try {
      const mediaStream = await navigator.mediaDevices.getUserMedia({
        video: { deviceId: selectedVideo ? { exact: selectedVideo } : undefined },
        audio: { deviceId: selectedAudio ? { exact: selectedAudio } : undefined },
      });
      setStream(mediaStream);
      if (userVideo.current) userVideo.current.srcObject = mediaStream;
      setJoined(true);
      socket.emit('join room', { roomID: roomCode, name });

      socket.on('all users', users => {
        const peers = [];
        users.forEach(user => {
          const peer = createPeer(user.id, socket.id, mediaStream);
          peersRef.current.push({ peerID: user.id, peer });
          peers.push({ peerID: user.id, peer });
          setPeerNames(prev => ({ ...prev, [user.id]: user.name }));
        });
        setPeers(peers);
      });

      socket.on('user joined', payload => {
        const peer = addPeer(payload.signal, payload.callerID, mediaStream, payload.name);
        peersRef.current.push({ peerID: payload.callerID, peer });
        setPeers(users => [...users, { peerID: payload.callerID, peer }]);
        setPeerNames(prev => ({ ...prev, [payload.callerID]: payload.name }));
      });

      socket.on('receiving returned signal', payload => {
        const item = peersRef.current.find(p => p.peerID === payload.id);
        item?.peer.signal(payload.signal);
      });
    } catch (err) {
      console.error('Media error:', err);
      alert('Failed to access camera/mic');
    }
  };

  const toggleMic = () => {
    if (stream) {
      stream.getAudioTracks().forEach(track => (track.enabled = !micOn));
      setMicOn(!micOn);
    }
  };

  const toggleCamera = () => {
    if (stream) {
      stream.getVideoTracks().forEach(track => (track.enabled = !camOn));
      setCamOn(!camOn);
    }
  };

  const leaveCall = () => {
    peersRef.current.forEach(({ peer }) => peer.destroy());
    if (stream) stream.getTracks().forEach(track => track.stop());
    socket.disconnect();
    setJoined(false);
    setPeers([]);
    setPeerNames({});
    window.location.reload();
  };

  return (
    <div className="guest-room">
      {!joined && (
        <div className="join-form">
          <h2>Join Ve Meet Room</h2>
          <input
            type="text"
            placeholder="Your Name"
            value={name}
            onChange={e => setName(e.target.value)}
          />
          <input
            type="text"
            placeholder="Room Code"
            value={roomCode}
            onChange={e => setRoomCode(e.target.value)}
          />
          <select onChange={e => setSelectedVideo(e.target.value)}>
            {devices.video.map(d => (
              <option key={d.deviceId} value={d.deviceId}>{d.label || 'Camera'}</option>
            ))}
          </select>
          <select onChange={e => setSelectedAudio(e.target.value)}>
            {devices.audio.map(d => (
              <option key={d.deviceId} value={d.deviceId}>{d.label || 'Microphone'}</option>
            ))}
          </select>
          <button onClick={handleJoin}>Join</button>
        </div>
      )}

      {joined && (
        <div className="room-content">
          <div className="room-header">
            <h3>Room: {roomCode}</h3>
            <h4>You: {name}</h4>
            <button onClick={toggleMic}>{micOn ? 'Mute' : 'Unmute'}</button>
            <button onClick={toggleCamera}>{camOn ? 'Camera Off' : 'Camera On'}</button>
            <button onClick={leaveCall} className="leave-button">Leave</button>
          </div>

          <div className="video-grid" ref={videoGridRef}>
            <div className="video-box">
              <video playsInline muted ref={userVideo} autoPlay className="video-el" />
              <div className="name-tag">{name} (You)</div>
            </div>
            {peers.map(({ peerID, peer }) => (
              <Video key={peerID} peer={peer} name={peerNames[peerID]} />
            ))}
          </div>

          <div className="participants-list">
            <h4>Participants:</h4>
            <ul>
              <li>{name} (You)</li>
              {Object.entries(peerNames).map(([id, n]) => (
                <li key={id}>{n}</li>
              ))}
            </ul>
          </div>
        </div>
      )}
    </div>
  );
}

function Video({ peer, name }) {
  const ref = useRef();

  useEffect(() => {
    peer.on('stream', stream => {
      if (ref.current) ref.current.srcObject = stream;
    });
  }, [peer]);

  return (
    <div className="video-box">
      <video playsInline autoPlay ref={ref} className="video-el" />
      <div className="name-tag">{name}</div>
    </div>
  );
}

export default GuestRoom;
