import React, { useEffect, useRef, useState } from 'react';

export default function Room() {
  const localVideoRef = useRef();
  const [devices, setDevices] = useState([]);
  const [selectedDeviceId, setSelectedDeviceId] = useState('');

  // List all video input devices (cameras)
  const getVideoDevices = async () => {
    const allDevices = await navigator.mediaDevices.enumerateDevices();
    const videoDevices = allDevices.filter(device => device.kind === 'videoinput');
    setDevices(videoDevices);

    // If no camera selected yet, default to first device
    if (!selectedDeviceId && videoDevices.length > 0) {
      setSelectedDeviceId(videoDevices[0].deviceId);
    }
  };

  // Start video stream from selected device
  const startCamera = async (deviceId) => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({
        video: { deviceId: deviceId ? { exact: deviceId } : undefined },
        audio: true,
      });
      if (localVideoRef.current) {
        localVideoRef.current.srcObject = stream;
      }
    } catch (err) {
      console.error('Error accessing camera:', err);
      alert('Could not access camera: ' + err.message);
    }
  };

  // On component mount, get list of cameras
  useEffect(() => {
    getVideoDevices();
  }, []);

  // Restart camera whenever selectedDeviceId changes
  useEffect(() => {
    if (selectedDeviceId) {
      startCamera(selectedDeviceId);
    }
  }, [selectedDeviceId]);

  return (
    <div style={{ background: '#111', color: 'white', padding: 20, height: '100vh' }}>
      <h2>Select Camera</h2>
      <select
        value={selectedDeviceId}
        onChange={e => setSelectedDeviceId(e.target.value)}
        style={{ marginBottom: 20, padding: 8, fontSize: 16 }}
      >
        {devices.map(device => (
          <option key={device.deviceId} value={device.deviceId}>
            {device.label || `Camera ${device.deviceId.slice(-4)}`}
          </option>
        ))}
      </select>

      <video
        ref={localVideoRef}
        autoPlay
        playsInline
        muted
        style={{ width: 400, borderRadius: 12, border: '2px solid #ccc' }}
      />
    </div>
  );
}
