import React, { useEffect, useRef, useState } from "react";
import io from "socket.io-client";
import Peer from "simple-peer";
import "../Room.css";

const socket = io.connect("https://192.168.29.23:5000");

function Room() {
  const [peers, setPeers] = useState([]);
  const [stream, setStream] = useState();
  const [roomID, setRoomID] = useState("");
  const [userName, setUserName] = useState("");
  const [joined, setJoined] = useState(false);
  const [peerNames, setPeerNames] = useState({});
  const [isAudio, setIsAudio] = useState(true);
  const [isVideo, setIsVideo] = useState(true);
  const [screenSharing, setScreenSharing] = useState(false);
  const userVideo = useRef();
  const peersRef = useRef([]);
  const screenTrackRef = useRef();

  useEffect(() => {
    const id = prompt("Enter Room Code:");
    const name = prompt("Enter Your Name:");
    setRoomID(id);
    setUserName(name);

    navigator.mediaDevices.getUserMedia({ video: true, audio: true }).then((stream) => {
      setStream(stream);
      if (userVideo.current) {
        userVideo.current.srcObject = stream;
      }

      socket.emit("join room", { roomID: id, userName: name });

      socket.on("all users", (users) => {
        const peers = [];
        users.forEach((user) => {
          const peer = createPeer(user.socketID, socket.id, stream);
          peersRef.current.push({ peerID: user.socketID, peer });
          peers.push({ peerID: user.socketID, peer, userName: user.userName });
        });
        setPeers(peers);
      });

      socket.on("user joined", (payload) => {
        const peer = addPeer(payload.signal, payload.callerID, stream);
        peersRef.current.push({ peerID: payload.callerID, peer });
        setPeers((users) => [...users, { peerID: payload.callerID, peer, userName: payload.userName }]);
      });

      socket.on("receiving returned signal", (payload) => {
        const item = peersRef.current.find((p) => p.peerID === payload.id);
        if (item) {
          item.peer.signal(payload.signal);
        }
      });

      socket.on("user left", (id) => {
        const peerObj = peersRef.current.find((p) => p.peerID === id);
        if (peerObj) {
          peerObj.peer.destroy();
        }
        peersRef.current = peersRef.current.filter((p) => p.peerID !== id);
        setPeers((users) => users.filter((p) => p.peerID !== id));
      });
    });

    setJoined(true);
  }, []);

  function createPeer(userToSignal, callerID, stream) {
    const peer = new Peer({
      initiator: true,
      trickle: false,
      stream,
    });

    peer.on("signal", (signal) => {
      socket.emit("sending signal", { userToSignal, callerID, signal, userName });
    });

    return peer;
  }

  function addPeer(incomingSignal, callerID, stream) {
    const peer = new Peer({
      initiator: false,
      trickle: false,
      stream,
    });

    peer.on("signal", (signal) => {
      socket.emit("returning signal", { signal, callerID });
    });

    peer.signal(incomingSignal);

    return peer;
  }

  const toggleAudio = () => {
    stream.getAudioTracks()[0].enabled = !isAudio;
    setIsAudio(!isAudio);
  };

  const toggleVideo = () => {
    stream.getVideoTracks()[0].enabled = !isVideo;
    setIsVideo(!isVideo);
  };

  const leaveCall = () => {
    window.location.reload();
  };

  const endMeeting = () => {
    socket.emit("end meeting", roomID);
    window.location.reload();
  };

  const startScreenShare = async () => {
    try {
      const screenStream = await navigator.mediaDevices.getDisplayMedia({ cursor: true });
      const screenTrack = screenStream.getVideoTracks()[0];

      screenTrackRef.current = screenTrack;

      peersRef.current.forEach(({ peer }) => {
        const sender = peer.streams[0].getVideoTracks()[0];
        peer.replaceTrack(sender, screenTrack, stream);
      });

      screenTrack.onended = () => {
        stopScreenShare();
      };

      setScreenSharing(true);
    } catch (err) {
      console.error("Screen sharing error:", err);
    }
  };

  const stopScreenShare = () => {
    if (!screenTrackRef.current) return;
    peersRef.current.forEach(({ peer }) => {
      const sender = peer.streams[0].getVideoTracks()[0];
      peer.replaceTrack(screenTrackRef.current, sender, stream);
    });
    setScreenSharing(false);
  };

  return (
    <div className="room-container">
      <h1 className="logo">🎥 Ve <span>Meet</span></h1>
      <div className="room-info">
        <p>Room Code: <strong>{roomID}</strong></p>
        <p>Your Name: <strong>{userName}</strong></p>
      </div>

      <div className="video-grid">
        <div className="video-box">
          <video muted ref={userVideo} autoPlay playsInline />
          <p>{userName} (You)</p>
        </div>

        {peers.map((peerObj, index) => (
          <Video key={peerObj.peerID} peer={peerObj.peer} userName={peerObj.userName} />
        ))}
      </div>

      <div className="controls">
        <button onClick={toggleAudio}>{isAudio ? "Mute" : "Unmute"}</button>
        <button onClick={toggleVideo}>{isVideo ? "Stop Video" : "Start Video"}</button>
        <button onClick={screenSharing ? stopScreenShare : startScreenShare}>
          {screenSharing ? "Stop Sharing" : "Share Screen"}
        </button>
        <button onClick={leaveCall}>Leave</button>
        <button onClick={endMeeting} className="end">End Meeting</button>
      </div>
    </div>
  );
}

function Video({ peer, userName }) {
  const ref = useRef();

  useEffect(() => {
    peer.on("stream", (stream) => {
      if (ref.current) {
        ref.current.srcObject = stream;
      }
    });
  }, [peer]);

  return (
    <div className="video-box">
      <video playsInline autoPlay ref={ref} />
      <p>{userName}</p>
    </div>
  );
}

export default Room;
