import React, { useEffect, useRef, useState } from "react";
import io from "socket.io-client";
import Peer from "simple-peer";

const socket = io("https://192.168.29.23:5000", {
  secure: true,
  rejectUnauthorized: false,
});

const GuestRoom = () => {
  const [peers, setPeers] = useState([]);
  const [stream, setStream] = useState(null);
  const [roomId, setRoomId] = useState("");
  const [userName, setUserName] = useState("");
  const [joined, setJoined] = useState(false);
  const [videoEnabled, setVideoEnabled] = useState(true);
  const [audioEnabled, setAudioEnabled] = useState(true);

  const userVideoRef = useRef();
  const peersRef = useRef([]);

  useEffect(() => {
    const name = sessionStorage.getItem("guestName");
    const room = sessionStorage.getItem("roomId");
    if (name && room) {
      setUserName(name);
      setRoomId(room);
    }
  }, []);

  useEffect(() => {
    socket.on("user-disconnected", (socketID) => {
      setPeers((prevPeers) =>
        prevPeers.filter((p) => p.socketID !== socketID)
      );
      peersRef.current = peersRef.current.filter(
        (p) => p.socketID !== socketID
      );
    });

    socket.on("user-joined", ({ signal, callerID, name }) => {
      const peer = addPeer(signal, callerID, stream);
      peersRef.current.push({ peerID: callerID, peer, name });
      setPeers((users) => [
        ...users,
        { peerID: callerID, peer, name },
      ]);
    });

    socket.on("receiving-returned-signal", ({ id, signal }) => {
      const item = peersRef.current.find((p) => p.peerID === id);
      if (item) {
        item.peer.signal(signal);
      }
    });

    return () => socket.disconnect();
  }, [stream]);

  const handleJoin = async () => {
    try {
      const localStream = await navigator.mediaDevices.getUserMedia({
        video: true,
        audio: true,
      });

      setStream(localStream);
      setJoined(true);

      if (userVideoRef.current) {
        userVideoRef.current.srcObject = localStream;
      }

      socket.emit("join-room", { roomId, name: userName });

      socket.on("all-users", (users) => {
        const peers = users.map((user) => {
          const peer = createPeer(user.socketID, socket.id, localStream);
          peersRef.current.push({
            peerID: user.socketID,
            peer,
            name: user.name,
          });
          return { peerID: user.socketID, peer, name: user.name };
        });
        setPeers(peers);
      });
    } catch (err) {
      console.error("Media error:", err);
    }
  };

  const createPeer = (userToSignal, callerID, stream) => {
    const peer = new Peer({
      initiator: true,
      trickle: false,
      stream,
    });

    peer.on("signal", (signal) => {
      socket.emit("sending-signal", {
        userToSignal,
        callerID,
        signal,
        name: userName,
      });
    });

    return peer;
  };

  const addPeer = (incomingSignal, callerID, stream) => {
    const peer = new Peer({
      initiator: false,
      trickle: false,
      stream,
    });

    peer.on("signal", (signal) => {
      socket.emit("returning-signal", {
        signal,
        callerID,
        name: userName,
      });
    });

    peer.signal(incomingSignal);
    return peer;
  };

  const toggleVideo = () => {
    if (stream) {
      stream.getVideoTracks()[0].enabled = !videoEnabled;
      setVideoEnabled(!videoEnabled);
    }
  };

  const toggleAudio = () => {
    if (stream) {
      stream.getAudioTracks()[0].enabled = !audioEnabled;
      setAudioEnabled(!audioEnabled);
    }
  };

  const handleLeave = () => {
    socket.emit("leave-room");
    window.location.href = "/";
  };

  return (
    <div style={{ background: "#000", color: "gold", minHeight: "100vh", padding: "1rem" }}>
      {!joined ? (
        <div style={{ textAlign: "center", marginTop: "100px" }}>
          <h1 style={{ fontSize: "3rem", fontFamily: "serif", textShadow: "2px 2px #444" }}>Ve Meet</h1>
          <p style={{ fontSize: "1.2rem" }}>Click to Join Meeting</p>
          <button
            onClick={handleJoin}
            style={{
              padding: "0.75rem 2rem",
              fontSize: "1.2rem",
              backgroundColor: "gold",
              color: "#000",
              border: "none",
              borderRadius: "10px",
              cursor: "pointer",
              marginTop: "1rem",
            }}
          >
            Join Meeting
          </button>
        </div>
      ) : (
        <>
          <div style={{
            display: "grid",
            gridTemplateColumns: "repeat(auto-fit, minmax(250px, 1fr))",
            gap: "1rem",
          }}>
            <div style={{
              background: "#111",
              border: "2px solid gold",
              borderRadius: "10px",
              padding: "0.5rem",
            }}>
              <video
                ref={userVideoRef}
                autoPlay
                muted
                playsInline
                style={{ width: "100%", borderRadius: "10px" }}
              />
              <p style={{ textAlign: "center", marginTop: "0.5rem" }}>{userName} (You)</p>
            </div>
            {peers.map((peerObj, index) => (
              <Video key={index} peer={peerObj.peer} name={peerObj.name} />
            ))}
          </div>

          <div style={{ textAlign: "center", marginTop: "2rem" }}>
            <button onClick={toggleAudio} style={buttonStyle}>
              {audioEnabled ? "Mute" : "Unmute"}
            </button>
            <button onClick={toggleVideo} style={buttonStyle}>
              {videoEnabled ? "Video Off" : "Video On"}
            </button>
            <button onClick={handleLeave} style={{
              ...buttonStyle,
              backgroundColor: "red",
              color: "#fff"
            }}>
              Leave
            </button>
          </div>
        </>
      )}
    </div>
  );
};

const Video = ({ peer, name }) => {
  const ref = useRef();

  useEffect(() => {
    peer.on("stream", (stream) => {
      if (ref.current) {
        ref.current.srcObject = stream;
      }
    });
  }, [peer]);

  return (
    <div style={{
      background: "#111",
      border: "2px solid gold",
      borderRadius: "10px",
      padding: "0.5rem",
    }}>
      <video playsInline autoPlay ref={ref} style={{ width: "100%", borderRadius: "10px" }} />
      <p style={{ textAlign: "center", marginTop: "0.5rem" }}>{name}</p>
    </div>
  );
};

const buttonStyle = {
  margin: "0.5rem",
  padding: "0.6rem 1.5rem",
  backgroundColor: "gold",
  color: "#000",
  fontWeight: "bold",
  fontSize: "1rem",
  border: "none",
  borderRadius: "8px",
  cursor: "pointer",
};

export default GuestRoom;
