import React, { useEffect, useRef, useState } from 'react';
import io from 'socket.io-client';
import Peer from 'simple-peer';
import './Room.css';

const socket = io.connect('https://localhost:5000'); // or your ngrok/server URL

const Room = () => {
  const [peers, setPeers] = useState([]);
  const [participants, setParticipants] = useState([]);
  const [stream, setStream] = useState();
  const [roomID, setRoomID] = useState('VeMeet123');
  const [username, setUsername] = useState('Host');
  const userVideo = useRef();
  const peersRef = useRef([]);

  useEffect(() => {
    navigator.mediaDevices.getUserMedia({ video: true, audio: true }).then((currentStream) => {
      setStream(currentStream);
      if (userVideo.current) {
        userVideo.current.srcObject = currentStream;
      }

      socket.emit('join-room', { roomID, username });

      socket.on('all-users', (users) => {
        const peerList = [];
        users.forEach((user) => {
          const peer = createPeer(user.socketID, socket.id, currentStream);
          peersRef.current.push({
            peerID: user.socketID,
            peer,
            username: user.username,
          });
          peerList.push({ peerID: user.socketID, peer, username: user.username });
        });
        setPeers(peerList);
      });

      socket.on('user-joined', (payload) => {
        const peer = addPeer(payload.signal, payload.callerID, currentStream);
        peersRef.current.push({
          peerID: payload.callerID,
          peer,
          username: payload.username,
        });
        setPeers((users) => [...users, { peerID: payload.callerID, peer, username: payload.username }]);
      });

      socket.on('receiving-returned-signal', (payload) => {
        const item = peersRef.current.find(p => p.peerID === payload.id);
        if (item) {
          item.peer.signal(payload.signal);
        }
      });

      socket.on('user-disconnected', (id) => {
        const peerObj = peersRef.current.find(p => p.peerID === id);
        if (peerObj) {
          peerObj.peer.destroy();
        }
        peersRef.current = peersRef.current.filter(p => p.peerID !== id);
        setPeers(prevPeers => prevPeers.filter(p => p.peerID !== id));
      });
    });
  }, []);

  function createPeer(userToSignal, callerID, stream) {
    const peer = new Peer({
      initiator: true,
      trickle: false,
      stream,
    });

    peer.on('signal', signal => {
      socket.emit('sending-signal', { userToSignal, callerID, signal, username });
    });

    return peer;
  }

  function addPeer(incomingSignal, callerID, stream) {
    const peer = new Peer({
      initiator: false,
      trickle: false,
      stream,
    });

    peer.on('signal', signal => {
      socket.emit('returning-signal', { signal, callerID });
    });

    peer.signal(incomingSignal);

    return peer;
  }

  return (
    <div className="room-container">
      <h1 className="ve-meet-logo">Ve Meet</h1>
      <div className="room-info">
        <p><strong>Room Code:</strong> {roomID}</p>
        <p><strong>You:</strong> {username}</p>
      </div>
      <div className="video-grid">
        <div className="video-block">
          <video ref={userVideo} autoPlay muted playsInline className="video" />
          <div className="username-label">{username} (Host)</div>
        </div>
        {peers.map((peerObj) => (
          <Video key={peerObj.peerID} peer={peerObj.peer} name={peerObj.username} />
        ))}
      </div>
    </div>
  );
};

const Video = ({ peer, name }) => {
  const ref = useRef();

  useEffect(() => {
    peer.on('stream', stream => {
      if (ref.current) {
        ref.current.srcObject = stream;
      }
    });
  }, [peer]);

  return (
    <div className="video-block">
      <video ref={ref} autoPlay playsInline className="video" />
      <div className="username-label">{name}</div>
    </div>
  );
};

export default Room;
