// src/components/GuestRoom.js

import React, { useState, useEffect, useRef } from 'react';
import io from 'socket.io-client';
import Peer from 'simple-peer';
import './GuestRoom.css';

const socket = io('https://localhost:5000', {
  transports: ['websocket'],
  secure: true
});

const GuestRoom = () => {
  const [name, setName] = useState('');
  const [roomId, setRoomId] = useState('');
  const [joined, setJoined] = useState(false);
  const [peers, setPeers] = useState([]);
  const [userList, setUserList] = useState([]);
  const [stream, setStream] = useState();
  const [videoDevices, setVideoDevices] = useState([]);
  const [selectedDeviceId, setSelectedDeviceId] = useState('');

  const myVideo = useRef();
  const peersRef = useRef([]);

  useEffect(() => {
    navigator.mediaDevices.enumerateDevices().then(devices => {
      const videoInputDevices = devices.filter(device => device.kind === 'videoinput');
      setVideoDevices(videoInputDevices);
      if (videoInputDevices[0]) setSelectedDeviceId(videoInputDevices[0].deviceId);
    });
  }, []);

  const handleJoin = async () => {
    try {
      const userStream = await navigator.mediaDevices.getUserMedia({
        video: { deviceId: selectedDeviceId ? { exact: selectedDeviceId } : undefined },
        audio: true
      });

      setStream(userStream);
      myVideo.current.srcObject = userStream;

      socket.emit('join-room', { roomId, name });
      setJoined(true);

      socket.on('user-list', users => {
        setUserList(users);
      });

      socket.on('user-joined', ({ id, name: newUserName }) => {
        const peer = createPeer(id, socket.id, userStream);
        peersRef.current.push({ peerID: id, peer, name: newUserName });
        setPeers(prev => [...prev, { peerID: id, peer, name: newUserName }]);
      });

      socket.on('offer', ({ from, offer }) => {
        const peer = addPeer(offer, from, userStream);
        peersRef.current.push({ peerID: from, peer });
        setPeers(prev => [...prev, { peerID: from, peer }]);
      });

      socket.on('answer', ({ from, answer }) => {
        const item = peersRef.current.find(p => p.peerID === from);
        if (item) item.peer.signal(answer);
      });

      socket.on('candidate', ({ from, candidate }) => {
        const item = peersRef.current.find(p => p.peerID === from);
        if (item) item.peer.signal(candidate);
      });

      socket.on('user-left', ({ id }) => {
        const peerObj = peersRef.current.find(p => p.peerID === id);
        if (peerObj) {
          peerObj.peer.destroy();
        }
        peersRef.current = peersRef.current.filter(p => p.peerID !== id);
        setPeers(prev => prev.filter(p => p.peerID !== id));
      });

    } catch (err) {
      console.error('Error accessing media devices.', err);
    }
  };

  const createPeer = (userToSignal, callerID, stream) => {
    const peer = new Peer({ initiator: true, trickle: false, stream });
    peer.on('signal', signal => {
      socket.emit('offer', { to: userToSignal, offer: signal });
    });
    return peer;
  };

  const addPeer = (incomingSignal, callerID, stream) => {
    const peer = new Peer({ initiator: false, trickle: false, stream });
    peer.on('signal', signal => {
      socket.emit('answer', { to: callerID, answer: signal });
    });
    peer.signal(incomingSignal);
    return peer;
  };

  const handleDeviceChange = (e) => {
    setSelectedDeviceId(e.target.value);
  };

  return (
    <div className="room-container">
      {!joined ? (
        <div className="join-section">
          <h1 className="logo">ðŸŽ¥ <span style={{ color: 'gold' }}>Ve Meet</span></h1>
          <select onChange={handleDeviceChange} value={selectedDeviceId} className="dropdown">
            {videoDevices.map(device => (
              <option key={device.deviceId} value={device.deviceId}>
                {device.label || `Camera ${device.deviceId}`}
              </option>
            ))}
          </select>
          <input
            className="input"
            placeholder="Your Name"
            value={name}
            onChange={e => setName(e.target.value)}
          />
          <input
            className="input"
            placeholder="Room Code"
            value={roomId}
            onChange={e => setRoomId(e.target.value)}
          />
          <button className="join-btn" onClick={handleJoin}>Join Room</button>
        </div>
      ) : (
        <div className="video-grid">
          <div className="video-block">
            <video ref={myVideo} autoPlay playsInline muted className="video" />
            <p className="name-tag">{name} (You)</p>
          </div>
          {peers.map(({ peer, peerID, name }, idx) => (
            <Video key={peerID} peer={peer} name={name} />
          ))}
          <div className="participant-list">
            <h3>Participants:</h3>
            <ul>
              {userList.map((n, i) => (
                <li key={i}>{n}</li>
              ))}
            </ul>
          </div>
        </div>
      )}
    </div>
  );
};

const Video = ({ peer, name }) => {
  const ref = useRef();
  useEffect(() => {
    peer.on('stream', stream => {
      ref.current.srcObject = stream;
    });
  }, [peer]);

  return (
    <div className="video-block">
      <video playsInline autoPlay ref={ref} className="video" />
      <p className="name-tag">{name}</p>
    </div>
  );
};

export default GuestRoom;
