import React, { useEffect, useRef, useState } from "react";
import io from "socket.io-client";
import Peer from "simple-peer";
import "./Room.css";

const socket = io("https://192.168.29.23:5000", {
  secure: true,
  reconnection: true,
  rejectUnauthorized: false,
});

const GuestRoom = () => {
  const [stream, setStream] = useState(null);
  const [peers, setPeers] = useState([]);
  const [name, setName] = useState("");
  const [roomID, setRoomID] = useState("");
  const [joined, setJoined] = useState(false);
  const [micOn, setMicOn] = useState(true);
  const [camOn, setCamOn] = useState(true);
  const userVideo = useRef();
  const peersRef = useRef([]);

  useEffect(() => {
    if (userVideo.current && stream) {
      userVideo.current.srcObject = stream;
    }
  }, [stream]);

  const handleJoin = async () => {
    if (!name || !roomID) {
      alert("Please enter your name and room code");
      return;
    }

    try {
      const userMedia = await navigator.mediaDevices.getUserMedia({
        video: true,
        audio: true,
      });
      setStream(userMedia);
      if (userVideo.current) userVideo.current.srcObject = userMedia;

      socket.emit("join-room", { roomId: roomID, userName: name });

      socket.on("all-users", (users) => {
        const peers = [];
        users.forEach((user) => {
          if (user.id === socket.id) return;
          const peer = createPeer(user.id, socket.id, userMedia);
          peersRef.current.push({ peerID: user.id, peer, userName: user.name });
          peers.push({ peerID: user.id, peer, userName: user.name });
        });
        setPeers(peers);
      });

      socket.on("user-joined", (payload) => {
        const peer = addPeer(payload.signal, payload.id, userMedia);
        peersRef.current.push({ peerID: payload.id, peer, userName: payload.name });
        setPeers((users) => [...users, { peerID: payload.id, peer, userName: payload.name }]);
      });

      socket.on("receiving-signal", ({ signal, from, name }) => {
        const existingPeer = peersRef.current.find((p) => p.peerID === from);
        if (!existingPeer) {
          const peer = addPeer(signal, from, stream);
          peersRef.current.push({ peerID: from, peer, userName: name });
          setPeers((prev) => [...prev, { peerID: from, peer, userName: name }]);
        }
      });

      socket.on("receiving-returned-signal", ({ id, signal }) => {
        const item = peersRef.current.find((p) => p.peerID === id);
        if (item) {
          console.log("ðŸŸ¢ Received returned signal from", id);
          item.peer.signal(signal);
        }
      });

      socket.on("user-disconnected", (id) => {
        setPeers((users) => users.filter((p) => p.peerID !== id));
        peersRef.current = peersRef.current.filter((p) => p.peerID !== id);
      });

      setJoined(true);
    } catch (err) {
      console.error("Error accessing media devices.", err);
      alert("Could not access camera and microphone.");
    }
  };

  function createPeer(userToSignal, callerID, stream) {
    const peer = new Peer({
      initiator: true,
      trickle: false,
      stream,
    });

    peer.on("signal", (signal) => {
      socket.emit("sending-signal", { userToSignal, callerID, signal });
    });

    return peer;
  }

  function addPeer(incomingSignal, callerID, stream) {
    const peer = new Peer({
      initiator: false,
      trickle: false,
      stream,
    });

    peer.on("signal", (signal) => {
      socket.emit("returning-signal", { signal, callerID });
    });

    peer.signal(incomingSignal);

    return peer;
  }

  const toggleMic = () => {
    if (!stream) return;
    stream.getAudioTracks().forEach((track) => {
      track.enabled = !micOn;
    });
    setMicOn(!micOn);
  };

  const toggleCam = () => {
    if (!stream) return;
    stream.getVideoTracks().forEach((track) => {
      track.enabled = !camOn;
    });
    setCamOn(!camOn);
  };

  const leaveRoom = () => {
    peersRef.current.forEach(({ peer }) => peer.destroy());
    if (stream) stream.getTracks().forEach((track) => track.stop());
    socket.disconnect();
    window.location.reload();
  };

  return (
    <div className="room-container">
      {!joined ? (
        <div className="join-container">
          <h2 className="logo">Ve Meet</h2>
          <input placeholder="Enter Your Name" onChange={(e) => setName(e.target.value)} />
          <input placeholder="Enter Room Code" onChange={(e) => setRoomID(e.target.value)} />
          <button onClick={handleJoin}>Join</button>
        </div>
      ) : (
        <div className="room-panel">
          <div className="video-grid">
            {stream && (
              <div className="video-block">
                <video muted ref={userVideo} autoPlay playsInline className="video" />
                <p>{name} (You)</p>
              </div>
            )}
            {peers.map(({ peerID, peer, userName }) => (
              <Video key={peerID} peer={peer} name={userName} />
            ))}
          </div>
          <div className="controls">
            <button onClick={toggleMic}>{micOn ? "Mute" : "Unmute"}</button>
            <button onClick={toggleCam}>{camOn ? "Video Off" : "Video On"}</button>
            <button onClick={leaveRoom}>Leave</button>
          </div>
        </div>
      )}
    </div>
  );
};

const Video = ({ peer, name }) => {
  const ref = useRef();

  useEffect(() => {
    const remoteStream = new MediaStream();

    const handleTrack = (event) => {
      remoteStream.addTrack(event.track);
      if (ref.current) {
        ref.current.srcObject = remoteStream;
      }
    };

    peer.on("track", handleTrack);

    return () => {
      peer.off("track", handleTrack);
    };
  }, [peer]);

  return (
    <div className="video-block">
      <video playsInline autoPlay ref={ref} className="video" />
      <p>{name}</p>
    </div>
  );
};

export default GuestRoom;
