import React, { useEffect, useRef, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import io from "socket.io-client";
import "./Room.css";

const socket = io.connect("https://your-server-url.com"); // Replace with your actual backend URL

const GuestRoom = () => {
  const { roomId } = useParams();
  const navigate = useNavigate();
  const [name, setName] = useState("");
  const [joined, setJoined] = useState(false);
  const [remoteStreams, setRemoteStreams] = useState([]);
  const [users, setUsers] = useState([]);
  const [localStream, setLocalStream] = useState(null);
  const localVideoRef = useRef(null);
  const peersRef = useRef({});
  const remoteStreamsRef = useRef({});

  useEffect(() => {
    if (!joined || !name) return;

    navigator.mediaDevices.getUserMedia({ video: true, audio: true }).then((stream) => {
      setLocalStream(stream);
      if (localVideoRef.current) localVideoRef.current.srcObject = stream;
      socket.emit("join-room", { roomId, name });
    });

    socket.on("user-list", (userList) => setUsers(userList));

    socket.on("user-joined", ({ id, name }) => {
      const peerConnection = new RTCPeerConnection();

      stream.getTracks().forEach((track) => {
        peerConnection.addTrack(track, stream);
      });

      peerConnection.ontrack = (event) => {
        const remoteStream = event.streams[0];
        remoteStreamsRef.current[id] = remoteStream;
        setRemoteStreams(Object.values(remoteStreamsRef.current));
      };

      peerConnection.createOffer().then((offer) => {
        peerConnection.setLocalDescription(offer);
        socket.emit("offer", { targetId: id, offer });
      });

      peersRef.current[id] = peerConnection;
    });

    socket.on("offer", async ({ fromId, offer }) => {
      const peerConnection = new RTCPeerConnection();

      stream.getTracks().forEach((track) => {
        peerConnection.addTrack(track, stream);
      });

      peerConnection.setRemoteDescription(new RTCSessionDescription(offer));
      const answer = await peerConnection.createAnswer();
      peerConnection.setLocalDescription(answer);

      socket.emit("answer", { targetId: fromId, answer });

      peerConnection.ontrack = (event) => {
        const remoteStream = event.streams[0];
        remoteStreamsRef.current[fromId] = remoteStream;
        setRemoteStreams(Object.values(remoteStreamsRef.current));
      };

      peersRef.current[fromId] = peerConnection;
    });

    socket.on("answer", ({ fromId, answer }) => {
      const peerConnection = peersRef.current[fromId];
      peerConnection.setRemoteDescription(new RTCSessionDescription(answer));
    });

    socket.on("ice-candidate", ({ fromId, candidate }) => {
      const peer = peersRef.current[fromId];
      if (peer) peer.addIceCandidate(new RTCIceCandidate(candidate));
    });

    return () => {
      socket.disconnect();
    };
  }, [joined, name]);

  const handleJoin = () => {
    if (!name.trim()) return alert("Enter your name");
    setJoined(true);
  };

  return (
    <div className="room-container">
      <h1 className="logo">Ve <span>Meet</span></h1>

      {!joined ? (
        <div className="join-box">
          <input
            type="text"
            placeholder="Enter your name"
            value={name}
            onChange={(e) => setName(e.target.value)}
          />
          <button onClick={handleJoin}>Join Meeting</button>
        </div>
      ) : (
        <div className="video-grid">
          <div className="video-block">
            <video ref={localVideoRef} autoPlay muted playsInline className="video" />
            <p>{name} (You)</p>
          </div>

          {remoteStreams.map((stream, idx) => {
            const peerId = Object.keys(remoteStreamsRef.current).find(
              (id) => remoteStreamsRef.current[id] === stream
            );
            const user = users.find((u) => u.id === peerId);
            return (
              <div key={peerId || idx} className="video-block">
                <video
                  autoPlay
                  playsInline
                  ref={(video) => {
                    if (video) video.srcObject = stream;
                  }}
                  className="video"
                />
                <p>{user ? user.name : `Guest ${idx + 1}`}</p>
              </div>
            );
          })}
        </div>
      )}
    </div>
  );
};

export default GuestRoom;
