// src/components/GuestRoom.js
import React, { useEffect, useRef, useState } from "react";
import io from "socket.io-client";
import "./GuestRoom.css";

const socket = io("https://abcdef.ngrok.io", { secure: true });

const GuestRoom = () => {
  const [roomId, setRoomId] = useState("");
  const [name, setName] = useState("");
  const [joined, setJoined] = useState(false);
  const [users, setUsers] = useState([]);
  const [localStream, setLocalStream] = useState(null);
  const [remoteStreams, setRemoteStreams] = useState([]);
  const [audioEnabled, setAudioEnabled] = useState(true);
  const [videoEnabled, setVideoEnabled] = useState(true);
  const [screenSharing, setScreenSharing] = useState(false);
  const [videoDevices, setVideoDevices] = useState([]);
  const [selectedDeviceId, setSelectedDeviceId] = useState(null);

  const localVideoRef = useRef();
  const peersRef = useRef({});
  const remoteStreamsRef = useRef({});

  const handleJoin = () => {
    if (!roomId.trim() || !name.trim()) {
      alert("Enter both Room Code and Your Name");
      return;
    }

    const constraints = {
      video: selectedDeviceId ? { deviceId: { exact: selectedDeviceId } } : true,
      audio: true,
    };

    navigator.mediaDevices.getUserMedia(constraints).then((stream) => {
      setLocalStream(stream);
      if (localVideoRef.current) localVideoRef.current.srcObject = stream;

      socket.emit("join-room", { roomId, name });
      setJoined(true);

      setupSocketListeners(stream);
    }).catch((e) => {
      alert("Could not get camera/mic: " + e.message);
    });
  };

  useEffect(() => {
    navigator.mediaDevices.enumerateDevices().then((devices) => {
      const videoInputs = devices.filter((d) => d.kind === "videoinput");
      setVideoDevices(videoInputs);
      if (videoInputs.length > 0) setSelectedDeviceId(videoInputs[0].deviceId);
    });
  }, []);

  const setupSocketListeners = (stream) => {
    socket.off("all-users");
    socket.off("user-joined");
    socket.off("offer");
    socket.off("answer");
    socket.off("candidate");
    socket.off("user-left");

    socket.on("all-users", (usersList) => {
      setUsers(usersList);
      usersList.forEach(({ id, name: peerName }) => {
        if (id === socket.id) return;
        const peer = createPeer(id, socket.id, stream);
        peersRef.current[id] = { peer, name: peerName };
      });
    });

    socket.on("user-joined", ({ id, name: peerName }) => {
      setUsers((prev) => [...prev, { id, name: peerName }]);
      const peer = createPeer(id, socket.id, stream);
      peersRef.current[id] = { peer, name: peerName };
    });

    socket.on("offer", async ({ from, offer }) => {
      const peer = addPeer(offer, from, stream);
      peersRef.current[from] = { peer, name: getNameById(from) || "Guest" };
    });

    socket.on("answer", ({ from, answer }) => {
      const peerObj = peersRef.current[from];
      if (!peerObj) return;
      peerObj.peer.setRemoteDescription(new RTCSessionDescription(answer));
    });

    socket.on("candidate", ({ from, candidate }) => {
      const peerObj = peersRef.current[from];
      if (!peerObj) return;
      peerObj.peer.addIceCandidate(new RTCIceCandidate(candidate));
    });

    socket.on("user-left", ({ id }) => {
      if (peersRef.current[id]) {
        peersRef.current[id].peer.close();
        delete peersRef.current[id];
      }
      setUsers((prev) => prev.filter((u) => u.id !== id));
      delete remoteStreamsRef.current[id];
      updateRemoteStreams();
    });
  };

  const getNameById = (id) => {
    const user = users.find((u) => u.id === id);
    return user ? user.name : null;
  };

  const createPeer = (userToSignal, callerId, stream) => {
    const peer = new RTCPeerConnection({
      iceServers: [{ urls: "stun:stun.l.google.com:19302" }],
    });

    peer.onicecandidate = (e) => {
      if (e.candidate) {
        socket.emit("candidate", { to: userToSignal, candidate: e.candidate });
      }
    };

    peer.ontrack = (e) => {
      remoteStreamsRef.current[userToSignal] = {
        id: userToSignal,
        stream: e.streams[0],
        name: getNameById(userToSignal) || "Guest",
      };
      updateRemoteStreams();
    };

    stream.getTracks().forEach((track) => peer.addTrack(track, stream));

    peer.createOffer().then((offer) => peer.setLocalDescription(offer)).then(() => {
      socket.emit("offer", { to: userToSignal, offer: peer.localDescription });
    });

    return peer;
  };

  const addPeer = (incomingOffer, callerId, stream) => {
    const peer = new RTCPeerConnection({
      iceServers: [{ urls: "stun:stun.l.google.com:19302" }],
    });

    peer.onicecandidate = (e) => {
      if (e.candidate) {
        socket.emit("candidate", { to: callerId, candidate: e.candidate });
      }
    };

    peer.ontrack = (e) => {
      remoteStreamsRef.current[callerId] = {
        id: callerId,
        stream: e.streams[0],
        name: getNameById(callerId) || "Guest",
      };
      updateRemoteStreams();
    };

    stream.getTracks().forEach((track) => peer.addTrack(track, stream));

    peer.setRemoteDescription(new RTCSessionDescription(incomingOffer)).then(() =>
      peer.createAnswer()
    ).then((answer) => peer.setLocalDescription(answer)).then(() => {
      socket.emit("answer", { to: callerId, answer: peer.localDescription });
    });

    return peer;
  };

  const updateRemoteStreams = () => {
    setRemoteStreams(Object.values(remoteStreamsRef.current));
  };

  const toggleAudio = () => {
    if (!localStream) return;
    const audioTrack = localStream.getAudioTracks()[0];
    if (audioTrack) {
      audioTrack.enabled = !audioTrack.enabled;
      setAudioEnabled(audioTrack.enabled);
    }
  };

  const toggleVideo = () => {
    if (!localStream) return;
    const videoTrack = localStream.getVideoTracks()[0];
    if (videoTrack) {
      videoTrack.enabled = !videoTrack.enabled;
      setVideoEnabled(videoTrack.enabled);
    }
  };

  const handleScreenShare = async () => {
    if (screenSharing) {
      stopScreenShare();
    } else {
      startScreenShare();
    }
  };

  const startScreenShare = async () => {
    try {
      const screenStream = await navigator.mediaDevices.getDisplayMedia({ video: true });
      const screenTrack = screenStream.getVideoTracks()[0];
      const videoTrack = localStream.getVideoTracks()[0];

      Object.values(peersRef.current).forEach(({ peer }) => {
        const sender = peer.getSenders().find((s) => s.track.kind === "video");
        if (sender) sender.replaceTrack(screenTrack);
      });

      if (localVideoRef.current) localVideoRef.current.srcObject = screenStream;
      screenTrack.onended = () => stopScreenShare();
      setScreenSharing(true);
    } catch (err) {
      alert("Error sharing screen: " + err.message);
    }
  };

  const stopScreenShare = () => {
    const videoTrack = localStream.getVideoTracks()[0];
    Object.values(peersRef.current).forEach(({ peer }) => {
      const sender = peer.getSenders().find((s) => s.track.kind === "video");
      if (sender) sender.replaceTrack(videoTrack);
    });
    if (localVideoRef.current) localVideoRef.current.srcObject = localStream;
    setScreenSharing(false);
  };

  const handleLeave = () => {
    Object.values(peersRef.current).forEach(({ peer }) => peer.close());
    peersRef.current = {};
    remoteStreamsRef.current = {};
    setRemoteStreams([]);
    setUsers([]);
    localStream?.getTracks().forEach((track) => track.stop());
    setLocalStream(null);
    socket.emit("leave-room", { roomId });
    setJoined(false);
    setRoomId("");
    setName("");
  };

  if (!joined) {
    return (
      <div className="join-container">
        <h1 className="logo">Ve Meet</h1>
        <h2>Join a Meeting</h2>
        <input placeholder="Enter Room Code" value={roomId} onChange={(e) => setRoomId(e.target.value)} />
        <input placeholder="Enter Your Name" value={name} onChange={(e) => setName(e.target.value)} />
        <select value={selectedDeviceId || ""} onChange={(e) => setSelectedDeviceId(e.target.value)}>
          {videoDevices.map((device) => (
            <option key={device.deviceId} value={device.deviceId}>{device.label}</option>
          ))}
        </select>
        <button onClick={handleJoin} className="btn">Join</button>
      </div>
    );
  }

  return (
    <div className="guestroom-container">
      <header className="header">
        <h1 className="logo">Ve Meet</h1>
        <div>
          <span>Room: {roomId}</span> | <span>You: {name}</span>
          <button onClick={handleLeave} className="btn leave-btn">Leave Meeting</button>
        </div>
      </header>

      <div className="video-grid">
        <div className="video-block local">
          <video ref={localVideoRef} autoPlay muted playsInline className="video" />
          <p className="video-name">{name} (You)</p>
        </div>
        {remoteStreams.map(({ id, stream, name: userName }) => (
          <div className="video-block" key={id}>
            <video
              autoPlay
              playsInline
              ref={(video) => {
                if (video) video.srcObject = stream;
              }}
              className="video"
            />
            <p className="video-name">{userName}</p>
          </div>
        ))}
      </div>

      <div className="controls">
        <button onClick={toggleAudio} className="btn control-btn">{audioEnabled ? "Mute" : "Unmute"}</button>
        <button onClick={toggleVideo} className="btn control-btn">{videoEnabled ? "Stop Video" : "Start Video"}</button>
        <button onClick={handleScreenShare} className="btn control-btn">{screenSharing ? "Stop Sharing" : "Share Screen"}</button>
        <button onClick={handleLeave} className="btn control-btn leave-btn">Leave Meeting</button>
      </div>

      <aside className="sidebar">
        <h3>Participants ({users.length})</h3>
        <ul>
          {users.map(({ id, name: userName }) => (
            <li key={id}>{userName} {id === socket.id ? "(You)" : ""}</li>
          ))}
        </ul>
      </aside>
    </div>
  );
};

export default GuestRoom;
