import React, { useEffect, useRef, useState } from "react";
import io from "socket.io-client";
import "./Room.css";

const socket = io("https://abcdef.ngrok.io", { secure: true });

const Room = () => {
  const [roomId, setRoomId] = useState("");
  const [name, setName] = useState("");
  const [joined, setJoined] = useState(false);
  const [participants, setParticipants] = useState([]); // {id, name, stream}
  const [localStream, setLocalStream] = useState(null);
  const [audioEnabled, setAudioEnabled] = useState(true);
  const [videoEnabled, setVideoEnabled] = useState(true);
  const [screenSharing, setScreenSharing] = useState(false);

  const localVideoRef = useRef();
  const peersRef = useRef({}); // peer connections by socket id
  const remoteStreamsRef = useRef({}); // remote streams by socket id
  const screenTrackRef = useRef(null);

  useEffect(() => {
    if (!joined) return;

    // Get user media for host
    navigator.mediaDevices
      .getUserMedia({ video: true, audio: true })
      .then((stream) => {
        setLocalStream(stream);
        if (localVideoRef.current) {
          localVideoRef.current.srcObject = stream;
        }
        socket.emit("join-room", { roomId, name });

        // Listen to socket events
        setupSocketListeners(stream);
      })
      .catch((err) => {
        alert("Error accessing camera/mic: " + err.message);
      });

    return () => {
      // Cleanup on leave
      if (localStream) {
        localStream.getTracks().forEach((t) => t.stop());
      }
      Object.values(peersRef.current).forEach((peer) => peer.close());
      peersRef.current = {};
      remoteStreamsRef.current = {};
      setParticipants([]);
      socket.offAny();
      setScreenSharing(false);
      screenTrackRef.current = null;
    };
  }, [joined]);

  const setupSocketListeners = (stream) => {
    socket.off("all-users");
    socket.off("user-joined");
    socket.off("offer");
    socket.off("answer");
    socket.off("ice-candidate");
    socket.off("user-left");

    // Receive all existing users in room
    socket.on("all-users", (users) => {
      // users: [{ id, name }]
      // Create peer connections to all users (guests)
      users.forEach(({ id: userId, name: userName }) => {
        if (userId === socket.id) return; // skip self

        createPeerConnection(userId, userName, stream, true);
      });

      // Add self to participants list
      setParticipants((prev) => [
        ...prev,
        { id: socket.id, name, stream },
        ...users.map(({ id, name }) => ({ id, name, stream: null })),
      ]);
    });

    // New user joined
    socket.on("user-joined", ({ id: userId, name: userName }) => {
      // Add new user to participants list with null stream (will be updated when track arrives)
      setParticipants((prev) => {
        if (prev.find((p) => p.id === userId)) return prev;
        return [...prev, { id: userId, name: userName, stream: null }];
      });

      createPeerConnection(userId, userName, stream, false);
    });

    // Incoming offer from guest
    socket.on("offer", async ({ from, offer }) => {
      const peer = createPeerConnection(from, null, stream, false, true);

      await peer.setRemoteDescription(new RTCSessionDescription(offer));
      const answer = await peer.createAnswer();
      await peer.setLocalDescription(answer);

      socket.emit("answer", { to: from, answer: peer.localDescription });
    });

    // Incoming answer from guest
    socket.on("answer", async ({ from, answer }) => {
      const peer = peersRef.current[from];
      if (!peer) return;
      await peer.setRemoteDescription(new RTCSessionDescription(answer));
    });

    // ICE candidate from guest
    socket.on("ice-candidate", ({ from, candidate }) => {
      const peer = peersRef.current[from];
      if (!peer) return;
      peer
        .addIceCandidate(new RTCIceCandidate(candidate))
        .catch((e) => console.error("Error adding ICE candidate", e));
    });

    // User left
    socket.on("user-left", ({ id: userId }) => {
      if (peersRef.current[userId]) {
        peersRef.current[userId].close();
        delete peersRef.current[userId];
      }
      if (remoteStreamsRef.current[userId]) {
        delete remoteStreamsRef.current[userId];
      }
      setParticipants((prev) => prev.filter((p) => p.id !== userId));
    });
  };

  const createPeerConnection = (
    userId,
    userName,
    stream,
    isInitiator,
    isOfferAnswer = false
  ) => {
    if (peersRef.current[userId]) return peersRef.current[userId];

    const peer = new RTCPeerConnection({
      iceServers: [{ urls: "stun:stun.l.google.com:19302" }],
    });

    peersRef.current[userId] = peer;

    // Add local tracks
    if (stream) {
      stream.getTracks().forEach((track) => {
        peer.addTrack(track, stream);
      });
    }

    peer.onicecandidate = (event) => {
      if (event.candidate) {
        socket.emit("ice-candidate", { to: userId, candidate: event.candidate });
      }
    };

    peer.ontrack = (event) => {
      remoteStreamsRef.current[userId] = event.streams[0];
      setParticipants((prev) =>
        prev.map((p) =>
          p.id === userId ? { ...p, stream: event.streams[0] } : p
        )
      );
    };

    peer.oniceconnectionstatechange = () => {
      if (
        peer.iceConnectionState === "disconnected" ||
        peer.iceConnectionState === "failed" ||
        peer.iceConnectionState === "closed"
      ) {
        peer.close();
        delete peersRef.current[userId];
        delete remoteStreamsRef.current[userId];
        setParticipants((prev) => prev.filter((p) => p.id !== userId));
      }
    };

    if (isInitiator && !isOfferAnswer) {
      peer
        .createOffer()
        .then((offer) => peer.setLocalDescription(offer))
        .then(() => {
          socket.emit("offer", { to: userId, offer: peer.localDescription });
        });
    }

    return peer;
  };

  const toggleAudio = () => {
    if (!localStream) return;
    localStream.getAudioTracks().forEach((track) => {
      track.enabled = !track.enabled;
      setAudioEnabled(track.enabled);
    });
  };

  const toggleVideo = () => {
    if (!localStream) return;
    localStream.getVideoTracks().forEach((track) => {
      track.enabled = !track.enabled;
      setVideoEnabled(track.enabled);
    });
  };

  const handleScreenShare = async () => {
    if (!localStream) return;

    if (!screenSharing) {
      try {
        const screenStream = await navigator.mediaDevices.getDisplayMedia({
          video: true,
        });
        const screenTrack = screenStream.getVideoTracks()[0];
        screenTrackRef.current = screenTrack;

        // Replace video track for all peers
        Object.values(peersRef.current).forEach((peer) => {
          const sender = peer
            .getSenders()
            .find((s) => s.track.kind === "video");
          if (sender) sender.replaceTrack(screenTrack);
        });

        screenTrack.onended = () => {
          stopScreenShare();
        };

        setScreenSharing(true);
      } catch (err) {
        alert("Screen sharing failed: " + err.message);
      }
    } else {
      stopScreenShare();
    }
  };

  const stopScreenShare = () => {
    if (!screenTrackRef.current || !localStream) return;
    const videoTrack = localStream.getVideoTracks()[0];
    Object.values(peersRef.current).forEach((peer) => {
      const sender = peer.getSenders().find((s) => s.track.kind === "video");
      if (sender) sender.replaceTrack(videoTrack);
    });
    screenTrackRef.current.stop();
    setScreenSharing(false);
  };

  const handleEndMeeting = () => {
    // Close all peers and cleanup
    Object.values(peersRef.current).forEach((peer) => peer.close());
    peersRef.current = {};
    remoteStreamsRef.current = {};
    setParticipants([]);
    if (localStream) {
      localStream.getTracks().forEach((track) => track.stop());
    }
    socket.emit("leave-room", { roomId });
    setJoined(false);
  };

  if (!joined) {
    return (
      <div className="join-container" style={styles.joinContainer}>
        <h1 style={styles.logo}>Ve Meet</h1>
        <h2 style={{ color: "#FFD700" }}>Join a Meeting</h2>
        <input
          placeholder="Enter Room Code"
          value={roomId}
          onChange={(e) => setRoomId(e.target.value)}
          style={styles.input}
        />
        <input
          placeholder="Enter Your Name"
          value={name}
          onChange={(e) => setName(e.target.value)}
          style={styles.input}
        />
        <button onClick={() => setJoined(true)} style={styles.button}>
          Join
        </button>
      </div>
    );
  }

  return (
    <div style={styles.roomContainer}>
      <header style={styles.header}>
  <h1 style={styles.logo}>Ve Meet</h1>
  <div>
    <span style={styles.roomName}>Room: {roomId}</span>
    <span style={{ ...styles.userName, marginLeft: 15 }}>User: {name}</span>
    <button
      onClick={() => {
        const inviteLink = `${window.location.origin}/guest?roomId=${roomId}`;
        navigator.clipboard.writeText(inviteLink)
          .then(() => alert("Invite link copied to clipboard!"))
          .catch(() => alert("Failed to copy invite link"));
      }}
      style={{ 
        marginLeft: 20,
        padding: "6px 12px",
        fontSize: 14,
        cursor: "pointer",
        backgroundColor: "#FFD700",
        border: "none",
        borderRadius: 6,
        fontWeight: "bold",
        color: "black",
      }}
      title="Copy invite link"
    >
      Copy Invite Link
    </button>
  </div>
</header>

        <main style={styles.videoGrid}>
        {/* Local Video */}
        <div style={styles.videoBlock}>
          <video
            ref={localVideoRef}
            autoPlay
            muted
            playsInline
            style={styles.video}
          />
          <p style={styles.videoLabel}>
            You ({name}) {audioEnabled ? "ðŸ”Š" : "ðŸ”‡"} {videoEnabled ? "ðŸŽ¥" : "ðŸš«"}
          </p>
        </div>

        {/* Remote Videos */}
        {participants
          .filter((p) => p.id !== socket.id)
          .map(({ id, name: pName, stream }) => (
            <VideoBlock key={id} name={pName} stream={stream} />
          ))}
      </main>

      <footer style={styles.controls}>
        <button onClick={toggleAudio} style={styles.controlButton}>
          {audioEnabled ? "Mute" : "Unmute"}
        </button>
        <button onClick={toggleVideo} style={styles.controlButton}>
          {videoEnabled ? "Stop Video" : "Start Video"}
        </button>
        <button onClick={handleScreenShare} style={styles.controlButton}>
          {screenSharing ? "Stop Sharing" : "Share Screen"}
        </button>
        <button onClick={handleEndMeeting} style={styles.controlButtonEnd}>
          End Meeting
        </button>
      </footer>

      <aside style={styles.sidebar}>
        <h3 style={{ color: "#FFD700" }}>Participants ({participants.length})</h3>
        <ul style={styles.participantList}>
          {participants.map(({ id, name }) => (
            <li key={id} style={styles.participantItem}>
              {name} {id === socket.id && "(You)"}
            </li>
          ))}
        </ul>
      </aside>
    </div>
  );
};

const VideoBlock = ({ stream, name }) => {
  const videoRef = useRef();

  useEffect(() => {
    if (videoRef.current && stream) {
      videoRef.current.srcObject = stream;
    }
  }, [stream]);

  return (
    <div style={styles.videoBlock}>
      <video
        ref={videoRef}
        autoPlay
        playsInline
        muted={false}
        style={styles.video}
      />
      <p style={styles.videoLabel}>{name}</p>
    </div>
  );
};

