import React, { useEffect, useRef, useState } from "react";
import io from "socket.io-client";
import Peer from "simple-peer";
import "./Room.css";

const socket = io.connect("https://192.168.29.23:5000");

const GuestRoom = () => {
  const [peers, setPeers] = useState([]);
  const [stream, setStream] = useState();
  const [userName, setUserName] = useState("");
  const [roomId, setRoomId] = useState("");
  const [joined, setJoined] = useState(false);
  const [videoEnabled, setVideoEnabled] = useState(true);
  const [audioEnabled, setAudioEnabled] = useState(true);
  const [participants, setParticipants] = useState([]);
  const [screenStream, setScreenStream] = useState(null);

  const myVideo = useRef();
  const peersRef = useRef({});

  useEffect(() => {
    socket.on("user-list", (list) => {
      setParticipants(list);
    });

    socket.on("user-joined", ({ id, name }) => {
      const peer = createPeer(id, socket.id, stream);
      peersRef.current[id] = { peer, name };
      setPeers((prev) => [...prev, { peer, id, name }]);
    });

    socket.on("offer", async ({ from, offer, name }) => {
      const peer = addPeer(offer, from, stream);
      peersRef.current[from] = { peer, name };
      setPeers((prev) => [...prev, { peer, id: from, name }]);
    });

    socket.on("answer", ({ from, answer }) => {
      peersRef.current[from]?.peer.signal(answer);
    });

    socket.on("ice-candidate", ({ from, candidate }) => {
      peersRef.current[from]?.peer.signal(candidate);
    });

    socket.on("user-left", (id) => {
      if (peersRef.current[id]) {
        peersRef.current[id].peer.destroy();
        delete peersRef.current[id];
      }
      setPeers((prev) => prev.filter((p) => p.id !== id));
    });

    socket.on("screen-share", ({ streamId }) => {
      navigator.mediaDevices.getUserMedia({ video: false, audio: false }).then(() => {
        const screen = document.getElementById("screen-share");
        screen.srcObject = new MediaStream([new MediaStreamTrack(streamId)]);
        screen.style.display = "block";
      });
    });

  }, [stream]);

  const createPeer = (userToSignal, callerId, stream) => {
    const peer = new Peer({
      initiator: true,
      trickle: false,
      stream
    });

    peer.on("signal", (signal) => {
      socket.emit("offer", {
        to: userToSignal,
        offer: signal
      });
    });

    peer.on("stream", (remoteStream) => {
      addRemoteStream(userToSignal, remoteStream);
    });

    return peer;
  };

  const addPeer = (incomingSignal, from, stream) => {
    const peer = new Peer({
      initiator: false,
      trickle: false,
      stream
    });

    peer.on("signal", (signal) => {
      socket.emit("answer", {
        to: from,
        answer: signal
      });
    });

    peer.on("stream", (remoteStream) => {
      addRemoteStream(from, remoteStream);
    });

    peer.signal(incomingSignal);
    return peer;
  };

  const addRemoteStream = (id, remoteStream) => {
    const video = document.getElementById(`video-${id}`);
    if (video) {
      video.srcObject = remoteStream;
    }
  };

  const handleJoin = async () => {
    try {
      const userMedia = await navigator.mediaDevices.getUserMedia({ video: true, audio: true });
      setStream(userMedia);
      if (myVideo.current) {
        myVideo.current.srcObject = userMedia;
      }

      socket.emit("join-room", { roomId, userName });
      setJoined(true);
    } catch (err) {
      alert("Could not access camera/microphone.");
    }
  };

  const toggleAudio = () => {
    stream.getAudioTracks()[0].enabled = !audioEnabled;
    setAudioEnabled(!audioEnabled);
  };

  const toggleVideo = () => {
    stream.getVideoTracks()[0].enabled = !videoEnabled;
    setVideoEnabled(!videoEnabled);
  };

  const leaveRoom = () => {
    window.location.reload();
  };

  return (
    <div className="room-container">
      {!joined ? (
        <div className="join-container">
          <h1 className="logo">Ve Meet</h1>
          <input
            placeholder="Your Name"
            value={userName}
            onChange={(e) => setUserName(e.target.value)}
            className="input-field"
          />
          <input
            placeholder="Room Code"
            value={roomId}
            onChange={(e) => setRoomId(e.target.value)}
            className="input-field"
          />
          <button onClick={handleJoin} className="join-button">Join</button>
        </div>
      ) : (
        <div className="video-grid">
          <div className="video-box">
            <video ref={myVideo} autoPlay muted className="video" />
            <div className="name-label">{userName} (You)</div>
          </div>

          {peers.map(({ id, name }) => (
            <div key={id} className="video-box">
              <video id={`video-${id}`} autoPlay className="video" />
              <div className="name-label">{name}</div>
            </div>
          ))}

          <div className="controls">
            <button onClick={toggleAudio}>{audioEnabled ? "Mute" : "Unmute"}</button>
            <button onClick={toggleVideo}>{videoEnabled ? "Video Off" : "Video On"}</button>
            <button onClick={leaveRoom}>Leave</button>
          </div>

          <video id="screen-share" style={{ display: "none", position: "absolute", top: 0, left: 0, width: "100%", height: "100%", zIndex: 1000 }} autoPlay />
        </div>
      )}
    </div>
  );
};

export default GuestRoom;
