import React, { useEffect, useRef, useState } from "react";
import io from "socket.io-client";
import { v4 as uuidv4 } from "uuid";

const socket = io("http://localhost:5000");

const Room = () => {
  const [roomId, setRoomId] = useState("");
  const [name, setName] = useState("");
  const [joined, setJoined] = useState(false);
  const [peers, setPeers] = useState([]);
  const [localStream, setLocalStream] = useState(null);
  const [remoteStreams, setRemoteStreams] = useState({});
  const [devices, setDevices] = useState([]);
  const [selectedDeviceId, setSelectedDeviceId] = useState(null);
  const [isMuted, setIsMuted] = useState(false);
  const [isScreenSharing, setIsScreenSharing] = useState(false);

  const localVideoRef = useRef();
  const peerConnections = useRef({});

  useEffect(() => {
    navigator.mediaDevices.enumerateDevices().then(devices => {
      const videoDevices = devices.filter(device => device.kind === 'videoinput');
      setDevices(videoDevices);
      if (videoDevices.length > 0) {
        setSelectedDeviceId(videoDevices[0].deviceId);
      }
    });
  }, []);

  useEffect(() => {
    if (selectedDeviceId) {
      navigator.mediaDevices.getUserMedia({
        video: { deviceId: { exact: selectedDeviceId } },
        audio: true
      }).then(stream => {
        setLocalStream(stream);
        if (localVideoRef.current) localVideoRef.current.srcObject = stream;
      });
    }
  }, [selectedDeviceId]);

  const createRoom = () => {
    const newRoomId = uuidv4().slice(0, 6);
    setRoomId(newRoomId);
  };

  const joinRoom = () => {
    if (!roomId || !name) return alert("Room ID and Name required");
    setJoined(true);
    socket.emit("join-room", { roomId, name });
  };

  useEffect(() => {
    if (!joined || !localStream) return;

    socket.on("user-joined", ({ id, name }) => {
      const pc = new RTCPeerConnection();
      peerConnections.current[id] = pc;
      localStream.getTracks().forEach(track => pc.addTrack(track, localStream));

      pc.onicecandidate = e => {
        if (e.candidate) {
          socket.emit("ice-candidate", { to: id, candidate: e.candidate });
        }
      };

      pc.ontrack = e => {
        setRemoteStreams(prev => ({ ...prev, [id]: e.streams[0] }));
      };

      pc.createOffer().then(offer => {
        pc.setLocalDescription(offer);
        socket.emit("offer", { to: id, offer });
      });
    });

    socket.on("offer", async ({ from, offer }) => {
      const pc = new RTCPeerConnection();
      peerConnections.current[from] = pc;
      localStream.getTracks().forEach(track => pc.addTrack(track, localStream));

      pc.onicecandidate = e => {
        if (e.candidate) {
          socket.emit("ice-candidate", { to: from, candidate: e.candidate });
        }
      };

      pc.ontrack = e => {
        setRemoteStreams(prev => ({ ...prev, [from]: e.streams[0] }));
      };

      await pc.setRemoteDescription(new RTCSessionDescription(offer));
      const answer = await pc.createAnswer();
      await pc.setLocalDescription(answer);
      socket.emit("answer", { to: from, answer });
    });

    socket.on("answer", ({ from, answer }) => {
      const pc = peerConnections.current[from];
      pc.setRemoteDescription(new RTCSessionDescription(answer));
    });

    socket.on("ice-candidate", ({ from, candidate }) => {
      const pc = peerConnections.current[from];
      if (pc) pc.addIceCandidate(new RTCIceCandidate(candidate));
    });

    socket.on("user-left", ({ id }) => {
      if (peerConnections.current[id]) {
        peerConnections.current[id].close();
        delete peerConnections.current[id];
        setRemoteStreams(prev => {
          const updated = { ...prev };
          delete updated[id];
          return updated;
        });
      }
    });
  }, [joined, localStream]);

  const toggleMute = () => {
    localStream.getAudioTracks()[0].enabled = isMuted;
    setIsMuted(!isMuted);
  };

  const shareScreen = async () => {
    if (!isScreenSharing) {
      const screenStream = await navigator.mediaDevices.getDisplayMedia({ video: true });
      const track = screenStream.getVideoTracks()[0];
      const sender = Object.values(peerConnections.current)[0]?.getSenders().find(s => s.track.kind === "video");
      if (sender) sender.replaceTrack(track);
      track.onended = () => {
        sender.replaceTrack(localStream.getVideoTracks()[0]);
        setIsScreenSharing(false);
      };
      setIsScreenSharing(true);
    }
  };

  const leaveRoom = () => {
    window.location.reload();
  };

  return (
    <div style={{ padding: 20, fontFamily: "Arial", backgroundColor: "#f5f5f5", minHeight: "100vh" }}>
      {!joined ? (
        <div style={{ maxWidth: 400, margin: "auto", textAlign: "center" }}>
          <h2 style={{ marginBottom: 10 }}>Ve Meet</h2>
          <button onClick={createRoom} style={{ marginBottom: 10 }}>Generate Room Code</button><br />
          <input
            placeholder="Room Code"
            value={roomId}
            onChange={e => setRoomId(e.target.value)}
            style={{ width: "100%", marginBottom: 10 }}
          />
          <input
            placeholder="Your Name"
            value={name}
            onChange={e => setName(e.target.value)}
            style={{ width: "100%", marginBottom: 10 }}
          />
          <select
            onChange={e => setSelectedDeviceId(e.target.value)}
            value={selectedDeviceId}
            style={{ width: "100%", marginBottom: 10 }}
          >
            {devices.map(device => (
              <option key={device.deviceId} value={device.deviceId}>{device.label}</option>
            ))}
          </select>
          <button onClick={joinRoom}>Join</button>
        </div>
      ) : (
        <div>
          <div style={{ marginBottom: 10 }}>
            <strong>Room Code:</strong> {roomId} | <strong>You:</strong> {name}
          </div>
          <div style={{ display: "flex", gap: 10, flexWrap: "wrap" }}>
            <div>
              <video ref={localVideoRef} autoPlay muted playsInline style={{ width: 300, background: "black" }} />
              <div style={{ textAlign: "center" }}>{name} (You)</div>
            </div>
            {Object.entries(remoteStreams).map(([id, stream]) => (
              <div key={id}>
                <video
                  autoPlay
                  playsInline
                  ref={video => video && (video.srcObject = stream)}
                  style={{ width: 300, background: "black" }}
                />
                <div style={{ textAlign: "center" }}>{id}</div>
              </div>
            ))}
          </div>
          <div style={{ marginTop: 20 }}>
            <button onClick={toggleMute}>{isMuted ? "Unmute" : "Mute"}</button>
            <button onClick={shareScreen} style={{ marginLeft: 10 }}>Share Screen</button>
            <button onClick={leaveRoom} style={{ marginLeft: 10 }}>End Meeting</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default Room;
