import React, { useEffect, useState, useRef } from "react";
import io from "socket.io-client";

const SOCKET_SERVER_URL = "http://localhost:5000"; // your backend socket server URL

const styles = {
  container: {
    display: "flex",
    flexDirection: "column",
    height: "100vh",
    backgroundColor: "#1e1e1e",
    color: "#fff",
    fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
  },
  header: {
    padding: 12,
    borderBottom: "1px solid #333",
    display: "flex",
    justifyContent: "space-between",
    alignItems: "center",
  },
  roomInfo: {
    fontWeight: "bold",
    fontSize: 18,
  },
  mainArea: {
    display: "flex",
    flexGrow: 1,
    padding: 12,
    gap: 20,
  },
  videoGrid: {
    flexGrow: 1,
    display: "grid",
    gridTemplateColumns: "repeat(auto-fill, minmax(320px, 1fr))",
    gap: 16,
    overflowY: "auto",
    paddingRight: 12,
  },
  sideBar: {
    width: 240,
    borderLeft: "1px solid #333",
    paddingLeft: 12,
    display: "flex",
    flexDirection: "column",
  },
  videoWrapper: {
    position: "relative",
    backgroundColor: "black",
    borderRadius: 8,
    overflow: "hidden",
  },
  video: {
    width: "100%",
    height: "auto",
    borderRadius: 8,
    backgroundColor: "#000",
  },
  nameTag: {
    position: "absolute",
    bottom: 6,
    left: 6,
    padding: "2px 6px",
    backgroundColor: "rgba(0,0,0,0.6)",
    borderRadius: 4,
    fontSize: 14,
    color: "#fff",
  },
  controls: {
    display: "flex",
    justifyContent: "center",
    gap: 12,
    marginTop: 10,
  },
  button: {
    padding: "8px 14px",
    backgroundColor: "#333",
    border: "none",
    borderRadius: 6,
    color: "#fff",
    cursor: "pointer",
    fontWeight: "600",
    fontSize: 14,
  },
  inputGroup: {
    display: "flex",
    gap: 8,
    marginBottom: 12,
  },
  input: {
    flexGrow: 1,
    padding: 8,
    borderRadius: 6,
    border: "1px solid #555",
    backgroundColor: "#2e2e2e",
    color: "#fff",
  },
  select: {
    padding: 8,
    borderRadius: 6,
    border: "1px solid #555",
    backgroundColor: "#2e2e2e",
    color: "#fff",
  },
  participantsTitle: {
    fontWeight: "700",
    fontSize: 16,
    marginBottom: 10,
  },
  participantName: {
    padding: "6px 8px",
    borderBottom: "1px solid #444",
    fontSize: 14,
  },
};

export default function Room() {
  const [socket, setSocket] = useState(null);

  const [joined, setJoined] = useState(false);
  const [roomId, setRoomId] = useState("");
  const [userName, setUserName] = useState("");
  const [users, setUsers] = useState([]); // participants

  const [localStream, setLocalStream] = useState(null);
  const [remoteStreams, setRemoteStreams] = useState({}); // { socketId: MediaStream }

  const [audioDevices, setAudioDevices] = useState([]);
  const [videoDevices, setVideoDevices] = useState([]);
  const [selectedAudioDeviceId, setSelectedAudioDeviceId] = useState("");
  const [selectedVideoDeviceId, setSelectedVideoDeviceId] = useState("");

  const [audioEnabled, setAudioEnabled] = useState(true);
  const [videoEnabled, setVideoEnabled] = useState(true);
  const [screenSharing, setScreenSharing] = useState(false);

  const localVideoRef = useRef();
  const peersRef = useRef({}); // For storing peer connections keyed by socketId

  // Initialize socket and media devices on mount
  useEffect(() => {
    const sock = io(SOCKET_SERVER_URL);
    setSocket(sock);

    // Clean up on unmount
    return () => {
      sock.disconnect();
      stopLocalStream();
    };
  }, []);

  // List media devices
  useEffect(() => {
    async function getDevices() {
      try {
        const devices = await navigator.mediaDevices.enumerateDevices();
        const videoInputDevices = devices.filter(
          (d) => d.kind === "videoinput"
        );
        const audioInputDevices = devices.filter(
          (d) => d.kind === "audioinput"
        );
        setVideoDevices(videoInputDevices);
        setAudioDevices(audioInputDevices);

        if (videoInputDevices.length && !selectedVideoDeviceId)
          setSelectedVideoDeviceId(videoInputDevices[0].deviceId);
        if (audioInputDevices.length && !selectedAudioDeviceId)
          setSelectedAudioDeviceId(audioInputDevices[0].deviceId);
      } catch (e) {
        console.error("Error getting media devices", e);
      }
    }
    getDevices();
  }, []);

  // Get local media stream when selected devices change
  useEffect(() => {
    if (!joined) return; // only if joined

    async function getMedia() {
      try {
        const constraints = {
          audio: { deviceId: selectedAudioDeviceId ? { exact: selectedAudioDeviceId } : undefined },
          video: { deviceId: selectedVideoDeviceId ? { exact: selectedVideoDeviceId } : undefined },
        };
        const stream = await navigator.mediaDevices.getUserMedia(constraints);
        setLocalStream(stream);
        if (localVideoRef.current) {
          localVideoRef.current.srcObject = stream;
        }
        // After getting local stream, notify peers (you might want to renegotiate if implemented)
      } catch (err) {
        alert("Could not get media: " + err.message);
      }
    }
    getMedia();

    return () => stopLocalStream();

    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [selectedAudioDeviceId, selectedVideoDeviceId, joined]);

  // Helper to stop local stream tracks
  function stopLocalStream() {
    if (localStream) {
      localStream.getTracks().forEach((track) => track.stop());
      setLocalStream(null);
    }
  }

  // Join room handler
  function handleJoin() {
    if (!roomId.trim() || !userName.trim()) {
      alert("Please enter Room ID and Your Name");
      return;
    }
    setJoined(true);

    socket.emit("join-room", { roomId, userName });

    // Setup socket event listeners after join
    socket.on("all-users", (usersInRoom) => {
      setUsers(usersInRoom);
    });

    socket.on("user-joined", (newUser) => {
      setUsers((prev) => [...prev, newUser]);
    });

    socket.on("user-left", (leftUserId) => {
      setUsers((prev) => prev.filter((u) => u.socketId !== leftUserId));
      // Also clean peer connections
      if (peersRef.current[leftUserId]) {
        peersRef.current[leftUserId].close();
        delete peersRef.current[leftUserId];
        setRemoteStreams((prev) => {
          const copy = { ...prev };
          delete copy[leftUserId];
          return copy;
        });
      }
    });

    // You'd also want to add signaling for WebRTC peers here,
    // But this demo just manages UI & basic socket stuff.
  }

  // Mute/unmute audio
  function toggleAudio() {
    if (!localStream) return;
    localStream.getAudioTracks().forEach((track) => {
      track.enabled = !track.enabled;
    });
    setAudioEnabled((prev) => !prev);
  }

  // Mute/unmute video
  function toggleVideo() {
    if (!localStream) return;
    localStream.getVideoTracks().forEach((track) => {
      track.enabled = !track.enabled;
    });
    setVideoEnabled((prev) => !prev);
  }

  // Start/stop screen sharing
  async function toggleScreenShare() {
    if (screenSharing) {
      // Stop screen share: revert to camera video stream
      await switchToCameraStream();
      setScreenSharing(false);
    } else {
      try {
        const screenStream = await navigator.mediaDevices.getDisplayMedia({
          video: true,
        });
        // Replace local stream video track with screen share track
        // This example does not handle peer renegotiation for simplicity
        if (localStream) {
          localStream.getVideoTracks().forEach((track) => track.stop());
        }
        setLocalStream(screenStream);
        if (localVideoRef.current) {
          localVideoRef.current.srcObject = screenStream;
        }
        setScreenSharing(true);
      } catch (e) {
        alert("Could not start screen sharing: " + e.message);
      }
    }
  }

  async function switchToCameraStream() {
    try {
      const constraints = {
        audio: { deviceId: selectedAudioDeviceId ? { exact: selectedAudioDeviceId } : undefined },
        video: { deviceId: selectedVideoDeviceId ? { exact: selectedVideoDeviceId } : undefined },
      };
      const camStream = await navigator.mediaDevices.getUserMedia(constraints);
      setLocalStream(camStream);
      if (localVideoRef.current) {
        localVideoRef.current.srcObject = camStream;
      }
    } catch (e) {
      alert("Could not switch back to camera: " + e.message);
    }
  }

  // End meeting - leave room
  function endMeeting() {
    if (socket) {
      socket.emit("leave-room", { roomId, userName });
      socket.disconnect();
    }
    stopLocalStream();
    setJoined(false);
    setUsers([]);
    setRemoteStreams({});
    setRoomId("");
    setUserName("");
    peersRef.current = {};
  }

  // JSX Render
  return (
    <div style={styles.container}>
      {!joined ? (
        <div style={{ maxWidth: 400, margin: "auto", marginTop: 80 }}>
          <h2>Join a Meeting</h2>
          <div style={styles.inputGroup}>
            <input
              placeholder="Room Code"
              value={roomId}
              onChange={(e) => setRoomId(e.target.value)}
              style={styles.input}
            />
            <input
              placeholder="Your Name"
              value={userName}
              onChange={(e) => setUserName(e.target.value)}
              style={styles.input}
            />
          </div>
          <button style={styles.button} onClick={handleJoin}>
            Join
          </button>
        </div>
      ) : (
        <>
          <header style={styles.header}>
            <div style={styles.roomInfo}>
              Room: <span style={{color:"#00ffea"}}>{roomId}</span> | You:{" "}
              <span style={{color:"#00ffea"}}>{userName}</span>
            </div>
            <button
              style={{ ...styles.button, backgroundColor: "#d23f3f" }}
              onClick={endMeeting}
              title="End Meeting"
            >
              End Meeting
            </button>
          </header>
          <div style={styles.mainArea}>
            <section style={styles.videoGrid}>
              {/* Local Video */}
              <div style={styles.videoWrapper}>
                <video
                  ref={localVideoRef}
                  autoPlay
                  muted
                  playsInline
                  style={styles.video}
                />
                <div style={styles.nameTag}>{userName} (You)</div>
              </div>

              {/* Remote videos (placeholder) */}
              {Object.entries(remoteStreams).map(([id, stream]) => (
                <RemoteVideo key={id} stream={stream} name={users.find(u => u.socketId === id)?.userName || "Guest"} />
              ))}
            </section>

            {/* Sidebar: participants + controls */}
            <aside style={styles.sideBar}>
              <div style={styles.participantsTitle}>Participants</div>
              <div style={{ flexGrow: 1, overflowY: "auto" }}>
                {users.map((u) => (
                  <div key={u.socketId} style={styles.participantName}>
                    {u.userName} {u.socketId === socket.id ? "(You)" : ""}
                  </div>
                ))}
              </div>

              <div style={{ marginTop: 16 }}>
                {/* Audio Select */}
                <label>
                  Audio:
                  <select
                    style={styles.select}
                    value={selectedAudioDeviceId}
                    onChange={(e) => setSelectedAudioDeviceId(e.target.value)}
                  >
                    {audioDevices.map((d) => (
                      <option key={d.deviceId} value={d.deviceId}>
                        {d.label || `Mic ${d.deviceId}`}
                      </option>
                    ))}
                  </select>
                </label>

                {/* Video Select */}
                <label style={{ marginTop: 12, display: "block" }}>
                  Video:
                  <select
                    style={styles.select}
                    value={selectedVideoDeviceId}
                    onChange={(e) => setSelectedVideoDeviceId(e.target.value)}
                  >
                    {videoDevices.map((d) => (
                      <option key={d.deviceId} value={d.deviceId}>
                        {d.label || `Camera ${d.deviceId}`}
                      </option>
                    ))}
                  </select>
                </label>

                {/* Controls */}
                <div style={styles.controls}>
                  <button style={styles.button} onClick={toggleAudio}>
                    {audioEnabled ? "Mute Audio" : "Unmute Audio"}
                  </button>
                  <button style={styles.button} onClick={toggleVideo}>
                    {videoEnabled ? "Stop Video" : "Start Video"}
                  </button>
                  <button style={styles.button} onClick={toggleScreenShare}>
                    {screenSharing ? "Stop Sharing" : "Share Screen"}
                  </button>
                </div>
              </div>
            </aside>
          </div>
        </>
      )}
    </div>
  );
}

function RemoteVideo({ stream, name }) {
  const videoRef = useRef();

  useEffect(() => {
    if (videoRef.current && stream) {
      videoRef.current.srcObject = stream;
    }
  }, [stream]);

  return (
    <div style={{ position: "relative", backgroundColor: "black", borderRadius: 8 }}>
      <video
        ref={videoRef}
        autoPlay
        playsInline
        style={{ width: "100%", borderRadius: 8 }}
      />
      <div
        style={{
          position: "absolute",
          bottom: 6,
          left: 6,
          padding: "2px 6px",
          backgroundColor: "rgba(0,0,0,0.6)",
          borderRadius: 4,
          fontSize: 14,
          color: "#fff",
        }}
      >
        {name}
      </div>
    </div>
  );
}
