import React, { useState, useEffect, useRef } from 'react';
import io from 'socket.io-client';
import Peer from 'simple-peer';
import './GuestRoom.css';

const socket = io('https://localhost:5000', {
  transports: ['websocket'],
  secure: true
});

function GuestRoom() {
  const [name, setName] = useState('');
  const [roomId, setRoomId] = useState('');
  const [joined, setJoined] = useState(false);
  const [peers, setPeers] = useState({});
  const [remoteNames, setRemoteNames] = useState({});
  const [videoDevices, setVideoDevices] = useState([]);
  const [selectedDeviceId, setSelectedDeviceId] = useState(null);

  const localVideoRef = useRef();
  const peerRefs = useRef({});
  const streamRef = useRef();

  // Get camera list
  useEffect(() => {
    navigator.mediaDevices.enumerateDevices().then(devices => {
      const videoInputs = devices.filter(device => device.kind === 'videoinput');
      setVideoDevices(videoInputs);
      if (videoInputs.length > 0) {
        setSelectedDeviceId(videoInputs[0].deviceId);
      }
    });
  }, []);

  useEffect(() => {
    if (!joined) return;

    navigator.mediaDevices.getUserMedia({
      video: { deviceId: selectedDeviceId ? { exact: selectedDeviceId } : undefined },
      audio: true
    }).then(stream => {
      streamRef.current = stream;
      localVideoRef.current.srcObject = stream;

      socket.emit('join-room', { roomId, name });

      socket.on('user-joined', ({ id, name }) => {
        const peer = createPeer(id, stream);
        peerRefs.current[id] = peer;
        setPeers(prev => ({ ...prev, [id]: peer }));
        setRemoteNames(prev => ({ ...prev, [id]: name }));
      });

      socket.on('offer', ({ from, offer }) => {
        const peer = addPeer(from, offer, stream);
        peerRefs.current[from] = peer;
        setPeers(prev => ({ ...prev, [from]: peer }));
      });

      socket.on('answer', ({ from, answer }) => {
        peerRefs.current[from].signal(answer);
      });

      socket.on('candidate', ({ from, candidate }) => {
        peerRefs.current[from]?.signal(candidate);
      });

      socket.on('user-left', ({ id }) => {
        if (peerRefs.current[id]) {
          peerRefs.current[id].destroy();
          delete peerRefs.current[id];
        }
        setPeers(prev => {
          const updated = { ...prev };
          delete updated[id];
          return updated;
        });
        setRemoteNames(prev => {
          const updated = { ...prev };
          delete updated[id];
          return updated;
        });
      });

      socket.on('user-list', (names) => {
        console.log('Current Participants:', names);
      });

    }).catch(err => {
      alert("Error accessing media devices: " + err.message);
    });

    return () => {
      streamRef.current?.getTracks().forEach(track => track.stop());
      socket.emit('leave-room', { roomId });
    };
  }, [joined, selectedDeviceId]);

  function createPeer(userToSignal, stream) {
    const peer = new Peer({ initiator: true, trickle: false, stream });
    peer.on('signal', signal => {
      socket.emit('offer', { to: userToSignal, offer: signal });
    });
    peer.on('stream', remoteStream => {
      const video = document.getElementById(userToSignal);
      if (video) video.srcObject = remoteStream;
    });
    return peer;
  }

  function addPeer(callerId, offer, stream) {
    const peer = new Peer({ initiator: false, trickle: false, stream });
    peer.on('signal', signal => {
      socket.emit('answer', { to: callerId, answer: signal });
    });
    peer.on('stream', remoteStream => {
      const video = document.getElementById(callerId);
      if (video) video.srcObject = remoteStream;
    });
    peer.signal(offer);
    return peer;
  }

  return (
    <div className="room-container">
      {!joined ? (
        <div className="join-form">
          <h2 className="logo">Ve Meet</h2>
          <input
            type="text"
            placeholder="Your Name"
            value={name}
            onChange={(e) => setName(e.target.value)}
            className="input"
          />
          <input
            type="text"
            placeholder="Room Code"
            value={roomId}
            onChange={(e) => setRoomId(e.target.value)}
            className="input"
          />
          <select
            value={selectedDeviceId || ''}
            onChange={(e) => setSelectedDeviceId(e.target.value)}
            className="input"
          >
            {videoDevices.map(device => (
              <option key={device.deviceId} value={device.deviceId}>
                {device.label || `Camera ${device.deviceId}`}
              </option>
            ))}
          </select>
          <button className="button" onClick={() => setJoined(true)}>Join</button>
        </div>
      ) : (
        <div className="video-grid">
          <div className="video-box">
            <video ref={localVideoRef} autoPlay playsInline muted className="video" />
            <div className="name-label">{name} (You)</div>
          </div>
          {Object.keys(peers).map(peerId => (
            <div className="video-box" key={peerId}>
              <video id={peerId} autoPlay playsInline className="video" />
              <div className="name-label">{remoteNames[peerId]}</div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default GuestRoom;
