// /src/components/Room.js
import React, { useEffect, useRef, useState } from 'react';
import { useParams, useLocation } from 'react-router-dom';
import socket from '../socket';

const ICE_SERVERS = { iceServers: [{ urls: 'stun:stun.l.google.com:19302' }] };

export default function Room() {
  const { roomId } = useParams();
  const location = useLocation();
  const userName = location.state?.name || 'Guest';

  const localVideoRef = useRef(null);
  const localStreamRef = useRef(null);
  const peerConnections = useRef({});
  const [remoteStreams, setRemoteStreams] = useState([]); // {id, name, stream}
  const [participants, setParticipants] = useState([]); // [{id, name}]
  const [cameras, setCameras] = useState([]);
  const [selectedCamera, setSelectedCamera] = useState('');
  
  // Get media with selected camera
  async function getMedia(deviceId) {
    if (localStreamRef.current) {
      localStreamRef.current.getTracks().forEach(track => track.stop());
    }
    const constraints = deviceId ? 
      { video: { deviceId: { exact: deviceId } }, audio: true } : 
      { video: true, audio: true };
    const stream = await navigator.mediaDevices.getUserMedia(constraints);
    localStreamRef.current = stream;
    if (localVideoRef.current) localVideoRef.current.srcObject = stream;
    return stream;
  }

  // Load camera devices
  async function loadCameras() {
    const devices = await navigator.mediaDevices.enumerateDevices();
    const videoDevices = devices.filter(d => d.kind === 'videoinput');
    setCameras(videoDevices);
    if (!selectedCamera && videoDevices.length > 0) {
      setSelectedCamera(videoDevices[0].deviceId);
    }
  }

  useEffect(() => {
    loadCameras();
  }, []);

  // Whenever selected camera changes, update stream and renegotiate
  useEffect(() => {
    if (!selectedCamera) return;

    getMedia(selectedCamera).then(stream => {
      // For all peers, replace video tracks
      Object.values(peerConnections.current).forEach(pc => {
        const senders = pc.getSenders().filter(s => s.track.kind === 'video');
        senders.forEach(sender => sender.replaceTrack(stream.getVideoTracks()[0]));
      });
    });
  }, [selectedCamera]);

  useEffect(() => {
    if (!localStreamRef.current) return;

    socket.emit('join-room', { roomId, userName });

    socket.on('all-users', (users) => {
      setParticipants([{ id: socket.id, name: userName }, ...users]);
      users.forEach(({ socketId, name }) => {
        const pc = createPeerConnection(socketId, name);
        peerConnections.current[socketId] = pc;
        localStreamRef.current.getTracks().forEach(track => pc.addTrack(track, localStreamRef.current));
        pc.createOffer().then(offer => {
          pc.setLocalDescription(offer);
          socket.emit('send-offer', { to: socketId, offer });
        });
      });
    });

    socket.on('receive-offer', ({ from, offer, name }) => {
      const pc = createPeerConnection(from, name);
      peerConnections.current[from] = pc;
      localStreamRef.current.getTracks().forEach(track => pc.addTrack(track, localStreamRef.current));
      pc.setRemoteDescription(new RTCSessionDescription(offer));
      pc.createAnswer().then(answer => {
        pc.setLocalDescription(answer);
        socket.emit('send-answer', { to: from, answer });
      });
    });

    socket.on('receive-answer', ({ from, answer }) => {
      const pc = peerConnections.current[from];
      if (pc) pc.setRemoteDescription(new RTCSessionDescription(answer));
    });

    socket.on('ice-candidate', ({ from, candidate }) => {
      const pc = peerConnections.current[from];
      if (pc && candidate) pc.addIceCandidate(new RTCIceCandidate(candidate));
    });

    socket.on('user-left', ({ socketId }) => {
      const pc = peerConnections.current[socketId];
      if (pc) pc.close();
      delete peerConnections.current[socketId];
      setRemoteStreams(prev => prev.filter(peer => peer.id !== socketId));
      setParticipants(prev => prev.filter(p => p.id !== socketId));
    });

    return () => {
      socket.off('all-users');
      socket.off('receive-offer');
      socket.off('receive-answer');
      socket.off('ice-candidate');
      socket.off('user-left');
      socket.disconnect();
      if (localStreamRef.current) {
        localStreamRef.current.getTracks().forEach(track => track.stop());
      }
    };
  }, [localStreamRef.current]);

  // Create peer connection & handlers
  function createPeerConnection(socketId, name) {
    const pc = new RTCPeerConnection(ICE_SERVERS);

    pc.onicecandidate = (e) => {
      if (e.candidate) {
        socket.emit('send-ice', { to: socketId, candidate: e.candidate });
      }
    };

    pc.ontrack = (e) => {
      setRemoteStreams(prev => {
        if (prev.some(peer => peer.id === socketId)) return prev;
        return [...prev, { id: socketId, name, stream: e.streams[0] }];
      });
    };

    return pc;
  }

  return (
    <div style={{ background: '#1e1e1e', color: '#eee', height: '100vh', display: 'flex', flexDirection: 'column', fontFamily: 'Segoe UI, Tahoma, Geneva, Verdana, sans-serif' }}>
      {/* Header */}
      <header style={{ padding: 16, borderBottom: '1px solid #333', display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
        <div>
          <h1 style={{ margin: 0 }}>Ve Meet</h1>
          <div>Room Code: <strong>{roomId}</strong></div>
          <div>Welcome, <strong>{userName}</strong></div>
        </div>
        <div>
          <label htmlFor="cameraSelect" style={{ marginRight: 8 }}>Camera:</label>
          <select
            id="cameraSelect"
            value={selectedCamera}
            onChange={(e) => setSelectedCamera(e.target.value)}
            style={{ padding: '6px 10px', borderRadius: 4, border: 'none', fontSize: 14, backgroundColor: '#333', color: '#eee', cursor: 'pointer' }}
          >
            {cameras.map(cam => (
              <option key={cam.deviceId} value={cam.deviceId}>{cam.label || `Camera ${cam.deviceId}`}</option>
            ))}
          </select>
        </div>
      </header>

      {/* Main content */}
      <main style={{ flex: 1, display: 'flex', padding: 16, gap: 24 }}>
        {/* Video Area */}
        <section style={{ flex: 3, display: 'flex', flexWrap: 'wrap', gap: 20, justifyContent: 'center', alignItems: 'flex-start' }}>
          <div style={{ width: 320, display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
            <div style={{ fontWeight: 'bold', marginBottom: 8 }}>You ({userName})</div>
            <video
              ref={localVideoRef}
              autoPlay
              muted
              playsInline
              style={{ width: '100%', borderRadius: 10, border: '2px solid #555', backgroundColor: '#000' }}
            />
          </div>

          {remoteStreams.map(peer => (
            <div key={peer.id} style={{ width: 320, display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
              <div style={{ fontWeight: 'bold', marginBottom: 8 }}>{peer.name || 'Guest'}</div>
              <video
                autoPlay
                playsInline
                ref={videoEl => { if (videoEl) videoEl.srcObject = peer.stream; }}
                style={{ width: '100%', borderRadius: 10, border: '2px solid #555', backgroundColor: '#000' }}
              />
            </div>
          ))}
        </section>

        {/* Participants List */}
        <aside style={{ flexBasis: 220, backgroundColor: '#2a2a2a', borderRadius: 8, padding: 16, color: '#ddd', overflowY: 'auto' }}>
          <h3 style={{ marginTop: 0, marginBottom: 12, borderBottom: '1px solid #555', paddingBottom: 6 }}>Participants ({participants.length})</h3>
          <ul style={{ listStyle: 'none', padding: 0, margin: 0 }}>
            {participants.map(p => (
              <li key={p.id} style={{ padding: '8px 4px', borderBottom: '1px solid #444' }}>
                {p.name || 'Guest'}
              </li>
            ))}
          </ul>
        </aside>
      </main>
    </div>
  );
}
