import React, { useEffect, useRef, useState } from "react";
import io from "socket.io-client";
import Peer from "simple-peer";
import "./Room.css";

const socket = io.connect("https://192.168.29.23:5000");

const GuestRoom = () => {
  const [peers, setPeers] = useState([]);
  const [stream, setStream] = useState();
  const [myName, setMyName] = useState("");
  const [roomID, setRoomID] = useState("");
  const [isJoined, setIsJoined] = useState(false);
  const [videoEnabled, setVideoEnabled] = useState(true);
  const [audioEnabled, setAudioEnabled] = useState(true);

  const userVideo = useRef();
  const peersRef = useRef([]);
  const videoGridRef = useRef();

  useEffect(() => {
    // Listen for peers joining
    socket.on("all-users", (users) => {
      const peersList = [];
      users.forEach(({ socketID, userName }) => {
        const peer = createPeer(socketID, socket.id, stream);
        peersRef.current.push({ peerID: socketID, peer, userName });
        peersList.push({ peerID: socketID, peer, userName });
      });
      setPeers(peersList);
    });

    socket.on("user-joined", ({ signal, callerID, userName }) => {
      const peer = addPeer(signal, callerID, stream);
      peersRef.current.push({ peerID: callerID, peer, userName });
      setPeers((prevPeers) => [...prevPeers, { peerID: callerID, peer, userName }]);
    });

    socket.on("receiving-returned-signal", ({ id, signal }) => {
      const item = peersRef.current.find((p) => p.peerID === id);
      if (item) item.peer.signal(signal);
    });

    socket.on("user-disconnected", (id) => {
      const peerObj = peersRef.current.find((p) => p.peerID === id);
      if (peerObj) {
        peerObj.peer.destroy();
        peersRef.current = peersRef.current.filter((p) => p.peerID !== id);
        setPeers((prev) => prev.filter((p) => p.peerID !== id));
      }
    });
  }, [stream]);

  const createPeer = (userToSignal, callerID, stream) => {
    const peer = new Peer({
      initiator: true,
      trickle: false,
      stream: stream,
    });

    peer.on("signal", (signal) => {
      socket.emit("sending-signal", {
        userToSignal,
        callerID,
        signal,
        userName: myName,
        roomID,
      });
    });

    return peer;
  };

  const addPeer = (incomingSignal, callerID, stream) => {
    const peer = new Peer({
      initiator: false,
      trickle: false,
      stream: stream,
    });

    peer.on("signal", (signal) => {
      socket.emit("returning-signal", { signal, callerID });
    });

    peer.signal(incomingSignal);

    return peer;
  };

  const handleJoin = async () => {
    try {
      const currentStream = await navigator.mediaDevices.getUserMedia({
        video: true,
        audio: true,
      });

      setStream(currentStream);
      setIsJoined(true);
      if (userVideo.current) {
        userVideo.current.srcObject = currentStream;
      }

      socket.emit("join-room", { roomID, userName: myName });
    } catch (err) {
      console.error("Error accessing media devices.", err);
    }
  };

  const toggleVideo = () => {
    if (stream) {
      stream.getVideoTracks()[0].enabled = !videoEnabled;
      setVideoEnabled(!videoEnabled);
    }
  };

  const toggleAudio = () => {
    if (stream) {
      stream.getAudioTracks()[0].enabled = !audioEnabled;
      setAudioEnabled(!audioEnabled);
    }
  };

  const handleLeave = () => {
    peersRef.current.forEach((p) => p.peer.destroy());
    socket.disconnect();
    window.location.reload(); // Simple reset for now
  };

  return (
    <div className="roomContainer">
      <h1 className="logoText">Ve <span>Meet</span></h1>

      {!isJoined ? (
        <div className="joinContainer">
          <input
            type="text"
            placeholder="Enter Room Code"
            onChange={(e) => setRoomID(e.target.value)}
            className="inputBox"
          />
          <input
            type="text"
            placeholder="Enter Your Name"
            onChange={(e) => setMyName(e.target.value)}
            className="inputBox"
          />
          <button onClick={handleJoin} className="joinButton">
            Join Meeting
          </button>
        </div>
      ) : (
        <>
          <div className="videoGrid" ref={videoGridRef}>
            <div className="videoBlock">
              <video ref={userVideo} autoPlay muted playsInline className="video" />
              <p className="nameTag">{myName} (You)</p>
            </div>
            {peers.map(({ peerID, peer, userName }) => (
              <Video key={peerID} peer={peer} name={userName} />
            ))}
          </div>

          <div className="controls">
            <button onClick={toggleAudio}>{audioEnabled ? "Mute" : "Unmute"}</button>
            <button onClick={toggleVideo}>{videoEnabled ? "Video Off" : "Video On"}</button>
            <button onClick={handleLeave} className="leaveBtn">Leave</button>
          </div>
        </>
      )}
    </div>
  );
};

const Video = ({ peer, name }) => {
  const ref = useRef();

  useEffect(() => {
    peer.on("stream", (stream) => {
      if (ref.current) {
        ref.current.srcObject = stream;
      }
    });
  }, [peer]);

  return (
    <div className="videoBlock">
      <video playsInline autoPlay ref={ref} className="video" />
      <p className="nameTag">{name}</p>
    </div>
  );
};

export default GuestRoom;
