import React, { useEffect, useRef, useState } from "react";
import io from "socket.io-client";
import Peer from "simple-peer";
import "../Room.css";

const socket = io("https://192.168.29.23:5000", {
  transports: ["websocket"],
  secure: true,
});

const GuestRoom = () => {
  const [roomId, setRoomId] = useState("");
  const [userName, setUserName] = useState("");
  const [joined, setJoined] = useState(false);
  const [peers, setPeers] = useState({});
  const [userList, setUserList] = useState([]);
  const [stream, setStream] = useState(null);
  const [videoEnabled, setVideoEnabled] = useState(true);
  const [audioEnabled, setAudioEnabled] = useState(true);

  const videoRef = useRef();
  const peersRef = useRef({});
  const streamRef = useRef();

  useEffect(() => {
    streamRef.current = stream;
  }, [stream]);

  useEffect(() => {
    socket.on("user-joined", ({ id, name }) => {
      const peer = createPeer(id, socket.id, streamRef.current);
      peersRef.current[id] = peer;
      setPeers((prev) => ({
        ...prev,
        [id]: { peer, name },
      }));
    });

    socket.on("offer", handleReceiveOffer);
    socket.on("answer", handleAnswer);
    socket.on("ice-candidate", handleNewICECandidateMsg);
    socket.on("user-list", (users) => setUserList(users));
    socket.on("user-left", handleUserLeft);

    return () => {
      socket.disconnect();
    };
  }, []);

  const handleReceiveOffer = ({ from, offer, name }) => {
    const peer = addPeer(offer, from, streamRef.current);
    peersRef.current[from] = peer;
    setPeers((prev) => ({
      ...prev,
      [from]: { peer, name },
    }));
  };

  const handleAnswer = ({ from, answer }) => {
    const peer = peersRef.current[from];
    if (peer) peer.signal(answer);
  };

  const handleNewICECandidateMsg = ({ from, candidate }) => {
    const peer = peersRef.current[from];
    if (peer) peer.signal(candidate);
  };

  const handleUserLeft = (id) => {
    if (peersRef.current[id]) {
      peersRef.current[id].destroy();
      delete peersRef.current[id];
      setPeers((prev) => {
        const newPeers = { ...prev };
        delete newPeers[id];
        return newPeers;
      });
    }
  };

  const createPeer = (userToSignal, callerID, stream) => {
    const peer = new Peer({
      initiator: true,
      trickle: false,
      stream,
    });

    peer.on("signal", (signal) => {
      socket.emit("offer", {
        to: userToSignal,
        offer: signal,
      });
    });

    return peer;
  };

  const addPeer = (incomingSignal, callerID, stream) => {
    const peer = new Peer({
      initiator: false,
      trickle: false,
      stream,
    });

    peer.on("signal", (signal) => {
      socket.emit("answer", {
        to: callerID,
        answer: signal,
      });
    });

    peer.signal(incomingSignal);

    return peer;
  };

  const handleJoin = async () => {
    try {
      const localStream = await navigator.mediaDevices.getUserMedia({
        video: true,
        audio: true,
      });
      setStream(localStream);
      streamRef.current = localStream;
      if (videoRef.current) videoRef.current.srcObject = localStream;
      socket.emit("join-room", { roomId, userName });
      setJoined(true);
    } catch (err) {
      console.error("Media error:", err);
    }
  };

  const toggleAudio = () => {
    const enabled = !audioEnabled;
    setAudioEnabled(enabled);
    if (streamRef.current) {
      streamRef.current.getAudioTracks()[0].enabled = enabled;
    }
  };

  const toggleVideo = () => {
    const enabled = !videoEnabled;
    setVideoEnabled(enabled);
    if (streamRef.current) {
      streamRef.current.getVideoTracks()[0].enabled = enabled;
    }
  };

  const leaveRoom = () => {
    window.location.reload();
  };

  if (!joined) {
    return (
      <div className="join-container">
        <h1 className="logo-text">Ve <span className="highlight">Meet</span></h1>
        <input
          type="text"
          placeholder="Enter Room Code"
          value={roomId}
          onChange={(e) => setRoomId(e.target.value)}
        />
        <input
          type="text"
          placeholder="Enter Your Name"
          value={userName}
          onChange={(e) => setUserName(e.target.value)}
        />
        <button onClick={handleJoin}>Join</button>
      </div>
    );
  }

  return (
    <div className="room-container">
      <div className="top-bar">
        <h1 className="logo-text">Ve <span className="highlight">Meet</span></h1>
        <div className="controls">
          <button onClick={toggleAudio}>{audioEnabled ? "Mute" : "Unmute"}</button>
          <button onClick={toggleVideo}>{videoEnabled ? "Stop Video" : "Start Video"}</button>
          <button onClick={leaveRoom} className="leave-button">Leave</button>
        </div>
      </div>

      <div className="video-grid">
        <div className="video-block">
          <video ref={videoRef} autoPlay muted playsInline className="video" />
          <p>{userName} (You)</p>
        </div>
        {Object.entries(peers).map(([id, { peer, name }]) => (
          <Video key={id} peer={peer} name={name} />
        ))}
      </div>
    </div>
  );
};

const Video = ({ peer, name }) => {
  const ref = useRef();

  useEffect(() => {
    peer.on("stream", (stream) => {
      if (ref.current) {
        ref.current.srcObject = stream;
      }
    });
  }, [peer]);

  return (
    <div className="video-block">
      <video ref={ref} autoPlay playsInline className="video" />
      <p>{name}</p>
    </div>
  );
};

export default GuestRoom;
