import React, { useEffect, useRef, useState } from "react";
import io from "socket.io-client";
import Peer from "simple-peer";
import "./Room.css"; // Assuming this exists and contains your black-gold theme

const socket = io.connect("https://192.168.29.23:5000", {
  transports: ["websocket"],
  secure: true,
});

const GuestRoom = () => {
  const [stream, setStream] = useState(null);
  const [peers, setPeers] = useState([]);
  const [joined, setJoined] = useState(false);
  const [name, setName] = useState("");
  const [roomID, setRoomID] = useState("");
  const [myID, setMyID] = useState("");
  const [videoEnabled, setVideoEnabled] = useState(true);
  const [audioEnabled, setAudioEnabled] = useState(true);

  const myVideo = useRef();
  const peersRef = useRef([]);
  const videoGridRef = useRef();

  useEffect(() => {
    socket.on("your-id", (id) => {
      setMyID(id);
    });

    socket.on("user-joined", (payload) => {
      const peer = addPeer(payload.signal, payload.callerID, stream);
      peersRef.current.push({
        peerID: payload.callerID,
        peer,
        name: payload.name,
      });
      setPeers((users) => [...users, { peer, name: payload.name }]);
    });

    socket.on("receiving-returned-signal", (payload) => {
      const item = peersRef.current.find((p) => p.peerID === payload.id);
      if (item?.peer) {
        try {
          item.peer.signal(payload.signal);
        } catch (e) {
          console.warn("Peer signaling failed", e);
        }
      }
    });

    socket.on("all-users", (users) => {
      const peers = [];
      users.forEach((user) => {
        const peer = createPeer(user.id, socket.id, stream);
        peersRef.current.push({
          peerID: user.id,
          peer,
          name: user.name,
        });
        peers.push({ peer, name: user.name });
      });
      setPeers(peers);
    });

    socket.on("user-left", (id) => {
      const peerObj = peersRef.current.find((p) => p.peerID === id);
      if (peerObj) {
        peerObj.peer.destroy();
      }
      peersRef.current = peersRef.current.filter((p) => p.peerID !== id);
      setPeers((prevPeers) => prevPeers.filter((p) => p.peer !== peerObj?.peer));
    });

    return () => {
      socket.disconnect();
    };
  }, [stream]);

  const createPeer = (userToSignal, callerID, stream) => {
    const peer = new Peer({
      initiator: true,
      trickle: false,
      stream,
    });

    peer.on("signal", (signal) => {
      socket.emit("sending-signal", {
        userToSignal,
        callerID,
        signal,
        name,
      });
    });

    return peer;
  };

  const addPeer = (incomingSignal, callerID, stream) => {
    const peer = new Peer({
      initiator: false,
      trickle: false,
      stream,
    });

    peer.on("signal", (signal) => {
      socket.emit("returning-signal", { signal, callerID });
    });

    try {
      peer.signal(incomingSignal);
    } catch (e) {
      console.warn("Error applying signal in addPeer", e);
    }

    return peer;
  };

  const handleJoin = async () => {
    try {
      const localStream = await navigator.mediaDevices.getUserMedia({
        video: true,
        audio: true,
      });
      setStream(localStream);
      if (myVideo.current) {
        myVideo.current.srcObject = localStream;
      }
      socket.emit("join-room", { roomID, name });
      setJoined(true);
    } catch (err) {
      alert("Error accessing media devices.");
      console.error("Media error:", err);
    }
  };

  const toggleVideo = () => {
    stream.getVideoTracks()[0].enabled = !videoEnabled;
    setVideoEnabled(!videoEnabled);
  };

  const toggleAudio = () => {
    stream.getAudioTracks()[0].enabled = !audioEnabled;
    setAudioEnabled(!audioEnabled);
  };

  const leaveRoom = () => {
    window.location.reload();
  };

  return (
    <div className="room-container">
      <div className="header">
        <h1 className="logo-text">Ve Meet</h1>
        {joined && <h3>Room Code: {roomID}</h3>}
      </div>

      {!joined ? (
        <div className="join-section">
          <input
            placeholder="Enter Name"
            value={name}
            onChange={(e) => setName(e.target.value)}
          />
          <input
            placeholder="Enter Room Code"
            value={roomID}
            onChange={(e) => setRoomID(e.target.value)}
          />
          <button onClick={handleJoin}>Join</button>
        </div>
      ) : (
        <>
          <div className="video-grid" ref={videoGridRef}>
            <div className="video-container">
              <video
                muted
                ref={myVideo}
                autoPlay
                playsInline
                className="video"
              />
              <p>{name} (You)</p>
            </div>
            {peers.map((peerObj, index) => (
              <Video key={index} peer={peerObj.peer} name={peerObj.name} />
            ))}
          </div>
          <div className="controls">
            <button onClick={toggleAudio}>
              {audioEnabled ? "Mute" : "Unmute"}
            </button>
            <button onClick={toggleVideo}>
              {videoEnabled ? "Stop Video" : "Start Video"}
            </button>
            <button onClick={leaveRoom}>Leave</button>
          </div>
        </>
      )}
    </div>
  );
};

const Video = ({ peer, name }) => {
  const ref = useRef();

  useEffect(() => {
    peer.on("stream", (stream) => {
      if (ref.current) ref.current.srcObject = stream;
    });
  }, [peer]);

  return (
    <div className="video-container">
      <video playsInline autoPlay ref={ref} className="video" />
      <p>{name}</p>
    </div>
  );
};

export default GuestRoom;
