import React, { useEffect, useRef, useState } from 'react';
import io from 'socket.io-client';
import './Room.css';

const socket = io.connect('https://localhost:5000'); // Replace with your server URL

const Room = () => {
  const [localStream, setLocalStream] = useState(null);
  const [remoteStreams, setRemoteStreams] = useState([]);
  const [peers, setPeers] = useState({});
  const [users, setUsers] = useState([]);
  const [name, setName] = useState('Host');
  const [roomId, setRoomId] = useState('');
  const [joined, setJoined] = useState(false);

  const videoGridRef = useRef();
  const localVideoRef = useRef();

  useEffect(() => {
    navigator.mediaDevices.getUserMedia({ video: true, audio: true }).then(stream => {
      setLocalStream(stream);
      if (localVideoRef.current) localVideoRef.current.srcObject = stream;
    });
  }, []);

  useEffect(() => {
    socket.on('user-joined', ({ userId, userName }) => {
      setUsers(prev => [...prev, { id: userId, name: userName }]);
    });

    socket.on('user-left', (userId) => {
      setUsers(prev => prev.filter(u => u.id !== userId));
      setRemoteStreams(prev => prev.filter(s => s.id !== userId));
    });

    socket.on('remote-stream', ({ userId, stream }) => {
      setRemoteStreams(prev => [...prev, { id: userId, stream }]);
    });
  }, []);

  const handleJoin = () => {
    if (roomId.trim() === '') return;
    setJoined(true);
    socket.emit('join-room', { roomId, userName: name });
  };

  const renderRemoteVideos = () => {
    return remoteStreams.map(({ id, stream }, idx) => {
      const user = users.find(u => u.id === id);
      return (
        <div key={id} className="video-block">
          <video
            autoPlay
            playsInline
            ref={video => {
              if (video) video.srcObject = stream;
            }}
            className="video"
          />
          <p>{user ? user.name : `Guest ${idx + 1}`}</p>
        </div>
      );
    });
  };

  return (
    <div className="room-container">
      <div className="header">
        <h2 className="logo">Ve Meet</h2>
        {joined && <div className="room-id">Room Code: {roomId}</div>}
      </div>

      {!joined ? (
        <div className="join-container">
          <input value={name} onChange={e => setName(e.target.value)} placeholder="Your name" />
          <input value={roomId} onChange={e => setRoomId(e.target.value)} placeholder="Room ID" />
          <button onClick={handleJoin}>Join Room</button>
        </div>
      ) : (
        <div className="video-grid" ref={videoGridRef}>
          <div className="video-block">
            <video
              ref={localVideoRef}
              autoPlay
              muted
              playsInline
              className="video"
            />
            <p>{name} (Host)</p>
          </div>
          {renderRemoteVideos()}
        </div>
      )}
    </div>
  );
};

export default Room;
